@-moz-document domain("metjm.net") {
/*Background*/

body {
    width: 100%;
    margin: 0;
    padding: 0;
    font-family: helveticaneue-light, helveticaneue, Helvetica, Arial, sans-serif;
    background-color: #131317 !important;
    background-position: center top;
    background-repeat: no-repeat;
}
/*Skin Container*/

.skinContainer {
    width: 100%;
    height: 125px;
    background-color: #1d1d1f !important;
    position: relative;
}
/*Skin Container*/

.skinContainer:nth-child(even) {
    background-color: #1a1a23 !important;
}
/*Skin Container Font Color*/
.skinProperty {
    color: #808080;
}
/*Skin Container Background*/

.backgroundWhite {
    background-color: #1c1c21 !important;
    position: absolute;
    left: 0;
    top: 380px;
    right: 0;
    bottom: 0;
    
}
/*HQ Custom Rotation*/

element.style {
    background-color: rgb(71, 180, 220) !important;
    border: 8px solid rgb(71, 180, 220) !important;
}
/*Opskins*/

element.style {
    background-color: rgb(71, 180, 220) !important;
    border: 8px solid rgb(71, 180, 220) !important;
}

/*Queue Length*/
element.style {
    left: 600px;
}
#serverStatusMain > .statusElementDescSmall {
    color: #ba415f !important;
}


/*Donator Page*/

.donator > .name {
    color: #92a936 !important;
}

.newCenterContainer p {
    color: #369ec3 !important;
}

/*Borders Fix*/

#customOptionsContainer {
    border-bottom: 1px solid #1c1c21 !important;
}

#serverStatusMain {
    border-top: 1px solid #1c1c21 !important;
}

.recentScreensContainer {
    border-top: 1px solid #1c1c21 !important;
}

#bannerContainer {

    border-top: 1px solid #1c1c21 !important;
}

/*Stats Page Font Color*/

td, th {
    border: 1px solid #cd325a !important;
    padding: 5px;
    color: #cd325a !important;
}

/*Recent Screenshots*/
.recentScreensContainer p {
    color: #2086ba !important;
}

/*Settings Page*/

#secondRowContainer > div {
    color: white !important;
    background-color: #1c1c21 !important;
}

.rotationContainer > button {
    color: white;
    float: right;
    font-family: NexaLight !important;
}

.rotationContainer {
    padding-top: 3px !important;
    padding-right: 0 !important;
    padding-bottom: 3px !important;
    padding-left: 18px !important;
}

::-webkit-file-upload-button, button {
    box-sizing: border-box;
    padding: 2px 6px 3px;
    border-width: 0px;
    border-top-width: 0px;
    border-right-width: 0px;
    border-bottom-width: 0px;
    border-left-width: 0px;
}




/*_______________________________________________*/

element.style {
    background-color: rgb(71, 180, 220);
}
#resSelection {
    border-color: rgb(71, 180, 220) !important;
}



/*Scrollbar
--------------------------------------------------
 width */

::-webkit-scrollbar {
    width: 10px;
}
/* Track */

::-webkit-scrollbar-track {
    border-radius: 10px;
    background: #565656;
}
/* Handle */

::-webkit-scrollbar-thumb {
    background: #1a1a23;
    border-radius: 10px;
}
/* Handle on hover */

::-webkit-scrollbar-thumb:hover {
    background: #1a1a23;
}
}
