css:
@-moz-document url("https://kuvien.io/user/index.html") {
html {
    background: #2C2F33
}
.title {
    color: #ffffff;
    font-size: 2rem;
    font-weight: 600;
    line-height: 1.125;
}
.subtitle {
    color: #d6d6d6;
    font-size: 1.25rem;
    font-weight: 400;
    line-height: 1.25;
}
.table {
    background-color: #3c3c3c;
    color: #fdfdfd;
    margin-bottom: 1.5rem;
}
.table thead td,
.table thead th {
    border-width: 0 0 2px;
    color: #ffffff;
}
.navbar-item,
.navbar-link {
    color: #ffffff;
    display: block;
    line-height: 1.5;
    padding: .5rem 1rem;
    position: relative;
}
.navbar-menu {
    background-color: #2c2f33;
    box-shadow: 0 8px 16px hsla(0, 0%, 4%, .1);
    padding: .5rem 0;
}
.navbar.has-shadow {
    box-shadow: 0 2px 0px 3px hsla(0, 0%, 100%, 0.3);
}
.navbar {
    background-color: #2c2f33;
    min-height: 3.25rem;
    position: relative;
}
.navbar-item,
.navbar-link {
    color: #ffffff;
    display: block;
    line-height: 1.5;
    padding: .5rem 1rem;
    position: relative;
}
.dropdown-item,
.dropdown .dropdown-menu .has-link a {
    color: #ffffff;
    display: block;
    font-size: .875rem;
    line-height: 1.5;
    padding: .375rem 1rem;
    position: relative;
}
.dropdown-content {
    background-color: #3c3c3c;
    border-radius: 3px;
    box-shadow: 0 2px 3px hsla(0, 0%, 4%, .1), 0 0 0 1px hsla(0, 0%, 4%, .1);
    padding-bottom: .5rem;
    padding-top: .5rem;
}
.message.is-warning {
    background-color: #3c3c3c;
}
.message.is-warning .message-body {
    border-color: #ffbe00;
    color: #ffbe00;
}
a:hover {
    color: #f7a5a5;
}
.input[disabled],
.taginput [disabled].taginput-container.is-focusable,
.textarea[disabled] {
    background-color: #1f1f1f;
    border-color: #f5f5f5;
    box-shadow: none;
    color: #ffffff;
}
}
