css:
@-moz-document domain("mail.google.com") {
/* Compose button */
.z0>.L3 {
    background-color: rgba(255, 255, 255, 0.88) !important;
    border-radius: 4px !important;
    height: 40px !important;
}

/* Left label navigation */
.TK .TO,
.n6 .ah9,
.CL {
    -webkit-border-radius: 0 4px 4px 0 !important;
    border-radius: 0 4px 4px 0 !important;
}

/* Left label navigation (collapsed view) */
.bhZ:not(.bym) .TK .TO,
.bhZ:not(.bym) .n6 .ah9,
.bhZ:not(.bym) .CL {
    border-radius: 4px !important;
}

/* Left label navigation (collapsed view, hovered), list items */
.bhZ.bym,
.bhZ.bym .aj5,
.bhZ.bjB,
.yO {
    background-color: rgba(32, 33, 36, .9) !important;
	-webkit-backdrop-filter: saturate(120%) blur(5px);
}
.bhZ.bym {
    border-radius: 0 8px 0 0 !important;
}

/* Toast */
.bAp.b8.UC .vh {
	background-color: rgba(32, 33, 36, .8) !important;
	-webkit-backdrop-filter: blur(5px);
	border-radius: 8px !important;
	box-shadow: none !important;
	-webkit-box-shadow: none !important;
}

/* Northeast badge */
.gb_Va {
    border-radius: 4px !important;
    border: none !important;
}

/* Gmail link */
.gb_ke.gb_fc.gb_ze {
    font-size: 32px;
    text-decoration: none;
}
.gb_ke.gb_fc.gb_ze:hover {
    font-weight: bold;
}

/* Hide branding footer */
.apO {
	display: none;
}
}
