css:
@-moz-document domain(beej.us) {
body {
	background-color: black;
	color: #838383;
	font-family: "Arial", "Times", "Georgia", serif;
	font-size: 12pt;
	margin: 35pt;
	margin-top: 10pt;
	margin-bottom: 10pt;
}
pre.code {
	background-color: #1d1d1d;
	border-color: #000;
	border-style: solid;
	border-width: 1px;
	border-right-width: 2px;
	border-bottom-width: 2px;
	margin: 0.2em;
	padding: 0.2em;
}
.sect2title {
	font-family: "Arial","Arial",sans-serif;
	font-size: 1.16em;
	font-weight: bold;
}
pre.screen {
	font-weight: bold;
	background-color: #1d1d1d;
	border-color: #000;
	border-style: solid;
	border-right-style: dashed;
	border-bottom-style: dashed;
	border-width: 1px;
	margin: 0.2em;
	padding: 0.2em;
}
.code, .screen {
	font-family: "Arial","Courier",monospace;
	font-size: 0.765em;
}
}
