css:
@-moz-document domain(start.me)
{
/*header*/
	
	#header {
		background-image: none;
		background-color: rgba(0, 0, 0, 0.6);
	}
	section#searchbar .button {
		color: #f0f0f0;
		background: transparent !important;
	}
	#header .buttons #me {
		box-shadow: none;
	}
	#header .buttons #me:hover,
	#header .buttons #me.expanded,
	#header .buttons #me:hover img,
	#header .buttons #me.expanded img {
		-webkit-transition: none;
		transition: border-radius 0.25s;
		-webkit-filter: none;
		filter: none;
		background-color: transparent;
		border-radius: 0px;
	}
	#header #breadcrumbs li.trigger .fa-pencil {
		visibility: hidden;
	}
	#header .buttons .button.highlight {
		background: transparent;
	}
	section#searchbar .wrapper {
		background-color: rgba(0, 0, 0, 0.3);
	}
	section#searchbar .wrapper {
		transition: background-color 1s .5s;
	}
	section#searchbar #search::placeholder {
		color: rgba(255, 255, 255, 0.7);
	}
	section#searchbar .button:hover {
		background-color: transparent;
	}
	section#searchbar:hover .wrapper {
        background-color: rgba(0, 0, 0, 0.5) !important;
	}
	#header nav.right,
	#header nav.left {
		margin-right: 0px;
	}
	/*sidebar(s)*/
	
	aside.sidebar {
		background: rgba(0, 0, 0, .8);
	}
	aside.sidebar > header.title,
	aside.sidebar > section > header.title {
		color: rgba(255, 255, 255, .9);
	}
	aside.sidebar > section.body.header,
	aside.sidebar > section.body.footer {
		background-color: rgba(0, 0, 0, .8);
	}
	aside.sidebar .browser.header form aside.placeholder {
		color: rgba(255, 255, 255, 0.5);
	}
	aside.sidebar > section.body form .icon-selector {
		background: none;
	}
	aside.sidebar > section.body {
		background: transparent;
		color: #ddd;
	}
	aside.sidebar > nav.tabs {
		background-color: transparent;
	}
	aside.sidebar > nav.tabs ul {
		color: #ddd;
	}
	#header nav#sidemenu {
		font-size: 18px;
	}
	aside.sidebar > section.body form label {
		color: #ddd;
	}
	aside.sidebar section > .button {
		color: #ddd;
	}
	aside.sidebar.pages-menu section.list section.pages .page {
		background-color: rgba(0, 0, 0, .6);
	}
	body.show-sidemenu #header nav#sidemenu {
		background-color: rgba(0, 0, 0, .9);
		box-shadow: none;
	}
	aside.sidebar > section.body form input[type="color"],
	aside.sidebar > section.body form input[type="date"],
	aside.sidebar > section.body form input[type="datetime"],
	aside.sidebar > section.body form input[type="datetime-local"],
	aside.sidebar > section.body form input[type="email"],
	aside.sidebar > section.body form input[type="month"],
	aside.sidebar > section.body form input[type="number"],
	aside.sidebar > section.body form input[type="password"],
	aside.sidebar > section.body form input[type="search"],
	aside.sidebar > section.body form input[type="tel"],
	aside.sidebar > section.body form input[type="text"],
	aside.sidebar > section.body form input[type="time"],
	aside.sidebar > section.body form input[type="url"],
	aside.sidebar > section.body form input[type="week"],
	aside.sidebar > section.body form input:not([type]),
	aside.sidebar > section.body form textarea,
	aside.sidebar > section.body form input[type="file"],
	aside.sidebar > section.body form input[type="range"],
	aside.sidebar > section.body form select,
	aside.sidebar > section.body form textarea,
	aside.sidebar > section.body form .input-frame {
		color: #ddd;
	}
	aside.sidebar > section.body form input[type="color"]::placeholder,
	aside.sidebar > section.body form input[type="date"]::placeholder,
	aside.sidebar > section.body form input[type="datetime"]::placeholder,
	aside.sidebar > section.body form input[type="datetime-local"]::placeholder,
	aside.sidebar > section.body form input[type="email"]::placeholder,
	aside.sidebar > section.body form input[type="month"]::placeholder,
	aside.sidebar > section.body form input[type="number"]::placeholder,
	aside.sidebar > section.body form input[type="password"]::placeholder,
	aside.sidebar > section.body form input[type="search"]::placeholder,
	aside.sidebar > section.body form input[type="tel"]::placeholder,
	aside.sidebar > section.body form input[type="text"]::placeholder,
	aside.sidebar > section.body form input[type="time"]::placeholder,
	aside.sidebar > section.body form input[type="url"]::placeholder,
	aside.sidebar > section.body form input[type="week"]::placeholder,
	aside.sidebar > section.body form input:not([type])::placeholder,
	aside.sidebar > section.body form textarea::placeholder,
	aside.sidebar > section.body form input[type="file"]::placeholder,
	aside.sidebar > section.body form input[type="range"]::placeholder,
	aside.sidebar > section.body form select::placeholder,
	aside.sidebar > section.body form textarea::placeholder,
	aside.sidebar > section.body form .input-frame::placeholder {
		color: #ddd;
	}
	aside.sidebar > section.body form ul.radiogroup li.selected label {
		color: #0093d5;
	}
	aside.sidebar > section.body form .ui-buttonset label {
		color: #ddd;
	}
	aside.sidebar section > .button:hover,
	aside.sidebar section > .button.hover,
	aside.sidebar section > .button:focus,
	aside.sidebar.pages-menu section.list section.pages .page:hover {
		background-color: rgba(255, 255, 255, .5);
	}
	aside.sidebar .browser ul.items.show-tiles li {
		background-color: transparent;
	}
	#header,
	body.static.dark #header {
		color: #ddd;
	}
	body.dark #header section#searchbar {
		box-shadow: none;
	}
	/*round*/
	
	#header .buttons #me img,
	section#searchbar .wrapper,
	section#searchbar #search {
		border-radius: 100px;
	}
	.ui-widget-content,
	article.widget {
		border: none;
		border-radius: 15px;
	}
	/*transparent*/
	
	#header .buttons #me,
	#header .buttons .button.highlight.green,
	#header .buttons #add-content.expanded {
		background: transparent;
	}
	/*hide*/
	
	aside.advertisement {
		display: block;
		visibility: hidden;
	}
	#header .buttons #me.free::after {
		display: none;
	}
	#header #breadcrumbs li.shared i.fa {
		display: none;
	}
	#header #breadcrumbs li::after {
		display: none;
	}
	/*footer*/
	
	#footer #footer-tab {
		background-color: transparent;
		color: rgba(255, 255, 255, .4);
		width: 15px;
		border-radius: 100px;
	}
	#footer #footer-tab:hover {
		background-color: rgba(0, 0, 0, .8);
		color: #ddd;
	}
	#footer section.columns,
	#footer #copyright {
		color: #ddd;
		background-color: rgba(0, 0, 0, .8);
	}
	#footer section.columns section.column ul li a,
	#footer section.columns section.column h5,
	#footer section.columns section.column ul li i.fa {
		color: #fefefe;
	}
	#footer #footer-tab img {
		display: none;
	}
	#footer #footer-tab::after {
		font-size: 22px;
		content: "⌃";
		margin-top: 10px;
	}
	#footer.collapsed #footer-tab::after {
		font-size: 22px;
		content: "⌃";
		margin-top: 10px;
	}
	#footer:not(.collapsed) #footer-tab::after {
		font-size: 22px;
		content: "⌄";
		margin-top: 0;
		padding-bottom: 10px;
	}

	.dropdown.ui-menu {
		background-color: rgba(0, 0, 0, .8) !important;
        color:#ddd;
	}
	ul.dropdown.ui-menu li.ui-menu-item,
	ul.dropdown.ui-menu li.ui-menu-item a {
		color: #ddd;
	}
.focus_image {
    margin-left:12px;
}
}
