css:
@-moz-document domain("valleyofunicorns.com") {


body{
	background: url(https://orig00.deviantart.net/ea00/f/2015/235/0/d/sunset_by_snaku6763-d96tsat.jpg);
    background-attachment: fixed;
    background-size: cover;
    color: #ffccd2;
}

header>nav{
 	background: url(https://i.imgur.com/2o6Rfid.png);   
}

#logo a{
 	background: url(https://i.imgur.com/qhMHfx3.png) no-repeat;   
}

html{
 	background-color: transparent;   
}

#content-wrapper:before{
 	background: url(https://i.imgur.com/NfeFdNt.png);   
}

.welcome-content{
 	background: transparent;
    border: 5px ridge #e878aa;
    border-radius: 30px;
}

#content-wrapper.logged-in:before{
 	background: url(https://i.imgur.com/NfeFdNt.png);
}

a, .table{
	    color: #ffccd2;
}

.btn-info {
    color: #ffffff;
    background-color: #e580ff;
    border-color: #e580ff;
}

.btn-danger {
    background:url(https://i.pinimg.com/originals/d7/2e/dd/d72edd08c4a5b75c65d68c101ce8da18.png);
    background-size: 10%;
       background-size: cover;
    border-color: #d43f3a;
}

#content-wrapper{
 	background: linear-gradient(rgba(87, 73, 132, 0.93), rgba(87, 73, 132, 0.93), rgba(241, 111, 129, 0.32))
}

::selection, .qtip-default {
    background: #fc5467;
    color: white; 
}

.btn-success:hover, .btn-success:focus, .btn-success:active, .btn-success.active, .open .dropdown-toggle.btn-success, input:hover, .btn:hover, .button.is-primary.is-hovered, .button.is-primary:hover, .btn-info:hover, .btn-info:focus, .btn-info:active, .btn-info.active, .open .dropdown-toggle.btn-info, input[type=text]:focus, input[type=password]:focus, input[type=datetime]:focus, input[type=datetime-local]:focus, input[type=date]:focus, input[type=month]:focus, input[type=time]:focus, input[type=week]:focus, input[type=number]:focus, input[type=email]:focus, input[type=url]:focus, input[type=search]:focus, input[type=tel]:focus, input[type=color]:focus, .btn-info:hover, .btn-info:focus, .btn-info:active, .btn-info.active, .open .dropdown-toggle.btn-info{
    background:#fb6f2c; 
    color: white;
}


.image.is-48x48 {
    height: 60px;
    width: 60px;
    border-radius: 50%;
        margin-left: 6.5%;
    border: 2px ridge #e878aa;
}

.green{
 	color: #fc5467!important;   
}

#content-wrapper:after{
 	background: url(https://i.imgur.com/lgMiYOe.png);   
}

.title {
    font-family: 'gabriola', serif;
    color: #fc5467;
    text-align: center;
}

input:hover{
    
    
}

.button.is-primary,.btn-primary, input{
    background: #fc5467;
    border-color: #fc5467;
    color: white;
}

table tr:hover{
    	background: rgba(241, 111, 129, 0.32);
}

h3, h2,h1, h4, a strong, .alert-close, .text-danger, .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open .dropdown-toggle.btn-default, .btn-default{
	color: #fc5467!important;
}

.table td, .table th {
    border-color: #fc5467;
}

table tr{
    border-color: #e878aa;
}

.column is-one-fifth{
    
}

.box, .box2, .forums-post-body,.forums-post-cap, .well, .sidenav-block, .blockbutt {
        background: #8d1f4e29;
    border: 3px ridge #e878aa;
    border-radius: 30px;
    color: #ffccd2;
}

.table.is-striped tbody tr:not(.is-selected):nth-child(even){
 background-color:    #8d1f4e29;
}

.sf-reset h1{
           background: #8d1f4e29;
    border: 3px ridge #e878aa;
    border-radius: 30px;
    color: #ffccd2;
    text-align: center;
}

.forums-post-body{
    background: #8d1f4e29;
    border: 1px groove #e878aa38;
    border-radius: 30px;
    color: #ffccd2;
}

.forums-post-cb{
    border-top-left-radius: 30px;
    border-top-right-radius: 30px;
    background: linear-gradient(rgba(241, 111, 129, 0.32), transparent);
    border-bottom: transparent;
}

hr{
    border-top: 1px solid #e878aa;
}

tr, td, tbody, .table{
    background: none;
}

.alert-good, .notification, .sidenav-block .sidenav-link{
   background: rgba(241, 111, 129, 0.32);
    color: #ffccd2;
}

.tabs.is-boxed a{
    background: rgba(241, 111, 129, 0.32);
    color: #ffccd2;
    margin-right: 10%
}

a:hover{
 	color: #fb6f2c;   
}

.tabs.is-boxed a:hover, .sidenav-block .sidenav-link:hover{
    color: white;
    background-color: #fb6f2cb8;
}

.welcome-top .pet01{
        background: url(https://www.valleyofunicorns.com/Horse/images/2986625) no-repeat;
}

.welcome-top .pet02{
        background: url(https://www.valleyofunicorns.com/Horse/images/2373984) no-repeat;
}


.btn-success, .button.is-info{
    color: white;
    background-color: #fc5467;
    border: none;
}
}
