css:
@-moz-document url-prefix("https://learn.uwaterloo.ca/") {
/*Header*/
 .d2l-navigation-s {
    background-color :#000000;
}
/*Top Seperator Line*/
 .d2l-navigation-s-header {
    border-bottom: 1px solid #000;
}
/*Bottom Seperator Line*/
 .d2l-navigation-s-main, .d2l-navigation-main-tb {
    border-bottom:1px solid #E4B429;
}
/*Logo Image*/
 img[src="/d2l/lp/navbars/6606/theme/viewimage/6771898/view?v=10.7.11.10424-85"] {
    display: none;
}
.d2l-navigation-s-logo-link {
    width:300px;
    height:120px;
    background-image:url("https://uwaterloo.ca/brand/sites/ca.brand/files/resize/uploads/images/universityofwaterloo_logo_horiz_rev_rgb_1-300x120.png");
}
/*Font*/
 a.d2l-navigation-s-link, a.d2l-navigation-s-link:link, a.d2l-navigation-s-link:visited {
    color:#FFF;
	size: 12px;
}
.d2l-navigation-s-item {
    color:#FFF;
    font-size: 80%;
}

.d2l-navigation-s-has-branding .d2l-navigation-s-header-logo-area > .d2l-navigation-s-link {
	font-size : 0.95rem   
}
/*Top Icons*/
 .d2l-icon-0 {
    color:#FFF
}

.d2l-icon1:hover{
 	color:  #E4B429; 
}
.d2l-navigation-s-button-highlight {
    color:#FFFFFF
}
.d2l-navigation-s-button-highlight:hover {
    color:#FFFFFF
}
/*Padding*/
 .d2l-page-header, .d2l-homepage-pageheader, .d2l-homepage-pageheader {
    padding: 0px;
    background: #E4B429
}
.d2l-navigation-s-main-wrapper{
    height: calc(0.2rem + 30px)
}

.d2l-page-main-padding{
	padding-top: 0px;   
}

.d2l-homepage-pageheader{
	margin-bottom: 0px;   
}
/*Dumb Gradient*/
 .d2l-navigation-s-shadow-gradient {
    background: transparent
}
.d2l-homepage {
    background-color: #fff;
    padding: 20px;
}
.d2l-body {
    background-color: #313131
}
}
