css:
@-moz-document url-prefix("http://oppgaver.kidsakoder.no") {
body{
    background-color:#333 !important;
    color: #b7b7b7 !important;
}
code{
    background-color:#212121 !important;
    color: #c79625 !important;
}
section.protip, section.tip{
    border: 3px solid #610000 !important;
    background: #422929 !important;
}
pre{
   background-color: #292929 !important;
   border: 1px solid  #151515 !important;
}
.hljs-attribute, .hljs-link, .hljs-number, .hljs-regexp{
   color: #0ab9ff !important;
}
header h1.title{
   color: #000 !important;
}
header .lesson-icons .course span, header .lesson-icons .course span.text, header .lesson-icons .lesson-link span, header .lesson-icons .lesson-link span.text, header .lesson-icons .level span, header .lesson-icons .level span.text, header .lesson-icons .pdf span, header .lesson-icons .pdf span.text, header .lesson-icons .readme span, header .lesson-icons .readme span.text{
      color: #000000 !important;
}
.top-menu .search .form-control{
   background-color: #525252 !important;
}
.top-menu a.intro{
   color: #000 !important;
}
.improve{
   background-color: #004e2a !important;
}
.improve a.code{
   background-color: #b9853b !important;
   border-color: #7b5319 !important;
}
.improve a.issue{
   background-color: #9e9e9e !important;
   border-color: #7b7b7b !important;
}
#footer{
   background-color: #3c4e3b !important;
}
li.lesson{
   background-color: #2b2b2b !important;
   border: 1px solid #6b6b6b !important;
}
}
