css:
@-moz-document url(http://www.google.es),
url-prefix(http://www.google.es/search?),
url-prefix(http://www.google.com/search?),
                url(http://www.google.com/),
               url-prefix(http://www.google.com/webhp),
               url-prefix(http://www.google.com/search?),
               url-prefix(http://www.google.com/advanced_search),
               url-prefix(http://www.google.com/preferences),
               url-prefix(http://www.google.com/language_tools),
               url-prefix(http://images.google.com/webhp),
               url(http://www.google.com/intl/en/),
               url(http://www.google.com/intl/en/options/),
               url-prefix(http://www.google.com/movies),
               url-prefix(http://blogsearch.google.com),
               url-prefix(http://www.google.com/custom?)  {        /* Add your local google domain  if it doesn't work for you*/

 
body { 
  background-color:   #aaa !important;
}

/*Visited links */
a.l:visited  {
  text-decoration: line-through !important;
  opacity: 0.4 !important;
}
/********
a.l:visited:after {
  content:"\00A0\2713" !important;
  color: #33FF33 !important;
} ********/

td,tr,div,.r,a.l{
  font-family: "Georgia" !important;
  font-size:10pt !important;
}

/* user bar at the top */
#gb {
  background-color: #ddd !important;
  margin: 0px !important;
  padding: 0px !important;
  z-index:  99 !important;
}
#gbar a {
  color: darkblue !important;
  font-weight: bold !important;
}
#guser  a:hover,#gbar a:hover {
  color: #000 !important;
  text-decoration: none !important;
}

.gb1 a,.gb3 a  {
  color: blue !important;
  font-size: 10pt !important;
  font-weight: bold !important;
}

/* Quit logo, save space */
TD[valign="top"][class="tc"],#logo,
#sft > TBODY > TR > TD:first-child,
.gbh  {
   display: none !important;
}

/* input search text */
INPUT[type="text"][title="Buscar"][name="q"],
INPUT[type="text"][title="Search"][name="q"]{
  text-indent: 20px !important;
  font-family: segoe ui,verdana,trebuchet ms !important;
  font-weight:bold !important;
  font-size:11pt !important;
  color:  white !important;
  width: 600px !important;
  margin-left: 270px !important;
  padding: 0px !important;
}

/* Form */
INPUT[type="text"][title="Buscar"][name="q"]:not([type="submit"]),
INPUT[type="text"][title="Search"][name="q"]:not([type="submit"])  {
  -moz-appearance: none !important;
  background-color: royalblue !important;
 -moz-border-radius: 10px 200px 200px 10px !important;
  border: 1px outset black !important;
}

INPUT[type="text"][title="Buscar"][name="q"]:not([type="submit"]):focus,
INPUT[type="text"][title="Search"][name="q"]:not([type="submit"]):focus  {
  -moz-appearance: none !important;
  border: 1px solid cyan !important;
}

INPUT[type="text"][title="Buscar"][name="q"]:not([type="submit"]):hover,
INPUT[type="text"][title="Search"][name="q"]:not([type="submit"]):hover  {
  background-color: blue !important;
}
 
table#mbEnd,#tpa1,#tpa2,#offered_in {
  display:none !important;
}

/* Positioning the form input at top*/
#sft,#sft > TBODY {
  max-height: 80px !important;
  margin-top:  45px !important;
}

#sft > TBODY > TR > TD:last-child,
#sft > TBODY > TR > TD:last-child > TABLE {
  max-height: 20px !important;
  margin-top:  20px !important;
}

#sft > TBODY > TR {
  display: inline !important;
  max-height: 80px !important;
  margin-top:  20px !important;
}

#sft > TBODY > TR > TD:last-child {
  text-align: center !important;
}

/*Numero de resultados*/
#ssb ,#ssb > P{
  background-color: #999 !important;
  max-height: 10px !important;
}

/*Other results */
#gsr > DIV:first-child + SCRIPT + NOSCRIPT + DIV + DIV {
  margin-top: -8px !important;
  text-align: center !important;
}

/************ Main results *************/
li.g {
  margin-top: 10px !important;
}

div {
  font-family: "Georgia" !important;
  font-size:10pt !important;
  line-height:14px !important;
  color:#222 !important;
}

/* Titles of the results */
.r   a {
  font-family: "helvetica" !important;
  font-size:  11pt !important;
  color:  #000    !important;           
  font-weight: bold !important;
  text-indent: 5px !important;
  text-decoration: none !important;
  line-height:22px !important;
 
}

H3:hover  .r a,
H3:hover  .r  >  em  {
  color:  darkred    !important;     
  text-decoration: underline !important;
  font-weight: bold !important;
}

 .g > table   {
  margin-top:  5px    !important;       
}

/* Keywords on Titles of the results */
 .r  a >  em {
  color:  blue !important; 
  font-weight: bold !important;
}


/* Result box */
.g {
  position:relative !important;
   left: 20px !important;
  width: 900px !important;
  text-indent: 5px !important;
  line-height: 15px !important;
  background-color:  #ccc  !important;  
   margin-left:100px !important;
   margin-top:  8px !important;
   padding:  2px !important;
  display:list-item !important;   
  list-style-type: disc !important;
  border: 1px solid black !important;
  font-family: "helvetica" !important;
}

.g > table {
  width: 900px !important; 
}

/* Type of document=> eg. PDF,GZIP,etc */
 .g >span.w>font {
  color: #aa22aa !important;
  font-size: 11pt !important;
}

/* HTML version of pdf,etc */
.std > a {
    color: #aaa !important;
}

/* Content of the result box */
  .s {
  text-align: justify !important;
  font-size: 11pt !important;
  font-family: "Georgia" !important;
  color:   #000 !important;
  line-height: 14px !important;
  max-width: 900px !important;
  background-color:   #ddd !important;
  border-top: 1px solid black !important;
  margin-top: -4px !important;
}

  .g:hover,
  .g:hover  .s {
  background-color:   #eee !important;
}

 .g:hover {
  background-color:   #ddd !important;
}

/* Keywords searching */
 .s > em {
   color:  #000 !important;
}

/* In cache, Similar pages, etc*/
 .gl {
   text-align: center !important;
   color: #777 !important;
   text-decoration: none !important;
   font-size: 9px !important;
}

.s  > nobr {
   display: block !important;
   text-align: right !important;
   color: #aaa !important;
   text-decoration: none !important;
   font-size: 9px !important;
}

/* More results from... */
.s>  .gl a,
.s.nobr >  .gl a  {
   /*display: block !important;*/
   text-align: right !important;
  color: #aaa !important;
  font-weight: bold !important;
  font-family: trebuchet ms !important;
}

 .gl a:hover  {
  color: black !important;
} 

/* Link 
cite {
   font-family: "arial" !important;
   color:  green !important;
   font-size: 11pt !important;
   display: block !important;
   text-align: left !important;
   font-weight: bold !important;
   margin-top: 5px !important;
}*/
cite {
   font-weight: bold !important;
}
span[class="a"]:hover {
   color: darkblue !important;
}

/* Results from youtube */
.j > table >tbody> tr >td{
   color: #111 !important;
}

/* footer */
#bsf {
  background-color: #999 !important;
}

}
