css:
@-moz-document domain(javascript.info) {
body {
    color: #fff;
    background: #333;
}
.main-en-banner:before {
    border-bottom: 19px solid #333;
}
.main-en-contents {
    background: #444;
}
.lessons-list {
    background: #3a3a3a;
    border-color: #444;
}
.lessons-list__lesson {
    border-color: #444;
}
.list-sub__link, :link, :visited {
    color: #44BCDE;
}
a:hover {
    color: #44BCDE;
    text-decoration: none;
}
.main ul > li::before {
    color: #fff;
}
img {
    filter: invert(85%);
}
.main-en-contents .list__link {
    color: #ccc;
}
.sitetoolbar {
    background: #444;
}
.important {
    border-color: #777;
}
#TEXT_EXPANDED {
    fill: #ddd;
}
.tutorial-progress[value] {
    background: #666 !important;
}
.buy-book-button {
    color: #44BCDE;
    box-shadow: inset 0 0 0 2px #aaa;
}
.link__type {
    background: #44BCDE;
    color: #44BCDE;
}
.comments__read-before-popup-i {
    background: #333;
}
.main code {
    background: #444;
}
.page__nav {
    transition: 300ms all;
}
.page__nav:hover {
    background: #ddd;
    border-color: #ddd;
}
.page__nav:hover > .page__nav-text-alternate {
    color: #333 !important;
}
.page__nav-text-alternate {
    color: #fff;
}
.tablet-menu__line {
    border-color: #666;
}
.tablet-menu {
    background: #444;
}
.tablet-menu__content .map, .tablet-menu__content .tablet-ebook {
    color: #fff;
}
.sitetoolbar_menu_open .sitetoolbar__nav-toggle, .sitetoolbar_user_open .sitetoolbar__user {
    background: #444;
}
.sidebar {
    background: #3a3a3a;
    border-color: #555;
}
.tutorial-map-overlay {
    background: #333;
}
.tutorial-map__filter {
    background: #444;
}
.text-input__control:focus, .text-input__control {
    box-shadow: none;
}
.tutorial-map-overlay__close::before {
    background: #bbb;
}
.tutorial-map-overlay__close, .tutorial-map-overlay__close:active {
    color: #666;
}
.tutorial-map-overlay .tutorial-map::after {
    background: none;
}
.page__nav {
    border-color: #666;
}
.page__nav-wrap::after {
    background: #666;
}
.page-footer {
    background: #444;
    border-color: #666;
}
.tutorial-progress[value]::-moz-progress-bar {
    background: #44BCDE;
}
pre[class*="language-"] {
    background: #444;
}
.line-numbers .line-numbers-rows, .line-numbers-rows > span::before, .line-numbers .line-numbers-rows::after {
    background: #555;
}
.token.atrule, .token.attr-value, .token.keyword {
    color: #44BCDE;
}
code[class*="language-"], pre[class*="language-"] {
    color: #ddd;
}
.token.boolean, .token.number, .token.property, .token.tag {
    color: #E94F64;
}
.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url {
    color: #E57254;
}
.main .block-highlight .mask, .main .inline-highlight .mask {
    background: #666;
    outline: 2px solid #666;
}
.token.cdata, .token.comment, .token.doctype, .token.prolog {
    color: #D6D6DE;
}

.main table tr:nth-child(2n) {
    background: #444;
}

.main table tr {
    border-color: #666;
}

.toolbar .toolbar__button {
    background: #555;
}
}
