css:
@-moz-document domain("zbigz.com") {
.side_bar_container, .login_page {
    background-color: #222 !important;
}

.main_file_block {
    background: #495057;
}

.z_input {
    background: #151b1e !important;
    color: #bbb !important;
}

.z_progress span {
    position: relative;
    z-index: 2;
    text-shadow: #000 0px 0px 2px;
    color: #fff;
}

header.main_header .header_content {
    background-color: #495057 !important;
    border-bottom: 1px solid #20a8d8 !important;
}

.side_bar_wrapper {
    background-color: #20a8d8 !important;
}

.header_wrap {
    background-color: #495057 !important;
}

.file_page__file, .file_page__file.pos_0, .z_progress, .file_name_block a.file_name {
    background-color: #495057;
    border: #6c757d;
    border-color: #6c757d;
    color: #bbb;
}

.file_page__file:hover {
    background: #495057;
}

.m_loader {
    Background-color:#495057
}

.layout_toast {
    padding-top: 50px;
}

.main_files_page .no_files_block, .main_page section, .main_page section.gray, footer.footer.main_page_footer {
    background: #495057;
    color: #bbb;
}

.main_files_page .no_files_block p, .main_files_page .no_files_block h2 {
    color: #bbb;
}

body, h1, h2, h3, h4, h5, h6 {
    color: #bbb;
}

.torrent_search_form .select_list, .payment_page section.gray, .payment_page__plans_list .plan .list-group-item.price, .payment_page__plans_list .plan .list-group-item, .payment_page__plans_list .plan .list-group-item.price p.small, .card-body, textarea, .login_page__login_form .login_form {
    background: #495057;
    border-color: #6c757d;
    color: #bbb !important;
}

.pay_table td {
    text-shadow: 0 0px;
    background-color: #495057;
}

.pay_table th, .card-header:first-child, .card-footer:last-child, .form_item .label, .form_item .label .fa, .form_item .label .icon {
    text-shadow: 0 0px;
    background-color: #393e44 !important;
    color: #bbb
}
}
