css:
@-moz-document url-prefix("https://www.hulu.com/") {
#content {
    background-color: #0a0a0a !important;
}

.GlobalNav,
.BrowseMenu__menu,
.Nav__dropdown,
.GlobalNav__logout,
.Footer,
.SearchBarContainer,
.SearchBar > input[type=text],
.FullTextSearch__Entry,
.SimpleCollection__title,
.BrowseMenu__menu-container {
 	background-color: #0a0a0af7 !important;   
}

.EditorialCollection__slider-overflow-hider {
 	background-image: linear-gradient(to right,#0a0a0af7 97%,#0a0a0af7 100%);   
}

.Nav__link .fill {
 	fill: #8d95a9 !important;   
}

.SimpleCollection__title,
.Tile__title--clickable,
.GlobalNav__profile-first-item,
.GlobalNav__profile-first-item--profile-name,
.GlobalNav__add-account,
.Nav__link,
.Subnav__item.active,
.Select-value,
.Select-option.is-focused,
.DetailsAbout__name,
.Tile__title,
.ViewAllLink__btn-text,
.BrowseMenu__item,
.PortraitCollection__title,
.Hub__title,
.GenericTileContent__title {
 	color: #8d95a9 !important;
}

.DetailsMasthead__container .Tile__title {
    color: #fffc !important;
}

.SearchBar > input[type=text],
.FullTextSearch__Entry,
.ListItem__HighlightText--Highlighted {
 	color: #00b6ff;   
}

.BrowseMenu__item--active {
    color: #8d95a9 !important;
	-webkit-text-stroke-color: #8d95a9 !important;;
    border-bottom-color: #8d95a9 !important;;   
}

.Nav__dropdown > .Nav__container:hover,
.GlobalNav__add-account:hover,
.GlobalNav__logout:hover,
.ListItem--Selected {
 	background-color: #3b3e4680 !important;   
}

.GlobalNav__profile-first-item:hover .GlobalNav__letter {
	background-color: #5e6573 !important;
}

.LevelTwo__container,
.DetailsBody__about,
.DetailsDropdown__single,
.Select-value,
.Select-menu,
.Select-option.is-focused {
	background-color: #08090c !important;
}

.SingleNetworkMasthead__arrow--right circle,
.SingleNetworkMasthead__arrow--left circle,
.Slider__button--next circle,
.Slider__button--previous circle {
 	fill: #08090c !important;
}

.Slider__button--next,
.Slider__button--previous {
	background: #08090c !important;
}

.Hub__collections .SimpleCollection,
.ListItem,
.Select-value {
 	border-bottom: 1px solid #8d95a9 !important;   
}

.Nav.GlobalNav {
 	box-shadow: 0 1px 0 0 #8d95a999;
}

.Nav__dropdown {
 	border: 1px solid #8d95a999 !important;   
}

.BrowseMenu__genres {
 	border-left: 1px solid #8d95a9 !important; 
}

.GlobalNav__profile-first-item,
.GlobalNav__add-account,
.SingleNetwork__subnav {
    border-bottom: 1px solid #8d95a999 !important;
}
}
