css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org"){

*{-moz-appearance:none!important}

/*lol, silly pix, comment out what you don't wanna change */
[value="Submit"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}
[value="Previous"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}
[value="Next"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}
[value="Delete"],[value="Go"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}
[value="Report"]{width:23px!important;height:23px!important;margin:0!important;padding:0!important;border:0!important;background:transparent url(data:image/jpg;base64,) no-repeat center top!important;text-indent:-1000em!important;cursor:pointer!important;}

/* comment out these lines if ye don't like the rounded corners */
.postarea imput,td textarea{-moz-border-radius-bottomright:10px!important;-moz-border-radius-topright:10px!important;}
.postarea .postblock{-moz-border-radius-bottomleft:10px!important;-moz-border-radius-topleft:10px!important;}
.postarea [name="name"],[name="email"],[name="sub"],[name="pwd"],[name="s"]{-moz-border-radius-bottomright:10px!important;-moz-border-radius-topright:10px!important;}
td.deletebuttons [name="pwd"]{-moz-border-radius:10px!important;}

}
