css:
@-moz-document domain("chat.google.com") {

/* VARIABLES HERE */
/* YOU CAN CHANGE VALUE HERE */

:root {
  --sidebar_background: #303E4D;
  --sidebar_background_hover: #4A5664;
  --sidebar_background_active: #6698C8;

  --sidebar_color_primary: #FFF;
  --sidebar_color_secondary: #E0E0E0;

  --sidebar_button_background_hover: #46576D;
  
  --sidebar_width: 280px;
  
  --chat_message_background_hover: #F9F9F9;
}


/* CSS CODE HERE */
/* DON'T MODIFY HERE */
.OVbIKc {
  box-shadow: none;
}
.QTQg5e {
  border-bottom: 1px solid #E5E5E5;
}

.Riuhhf, .X9KLPc {
  background: var(--sidebar_background);
  color: var(--sidebar_color_primary);
  width: var(--sidebar_width);
}

/* Chat Logo */
#XMLID_8_-Clipped {
 fill: var(--sidebar_color_primary);
 opacity: 1;
}

.nk7hB {
  position: relative;
	bottom: 4px;
  font-size: 1.3em;
}

.xzTN8b {
  position: absolute;
  bottom: -3px;
  left: 15px;
  background: none;
}

.Jrbdv, .HLTcjb {
 display: none; 
}

.yoV6yd {
 min-width: 300px; 
}

.X9KLPc .d6pS5 {
	position: absolute;
  z-index: 10000;
  top: -47px;
  right: 16px;
}

.X9KLPc .d6pS5, .X9KLPc .W2x1cc, .X9KLPc .O0WRkf {
	width: 36px;
  height: 36px;
  min-height: 0;
  min-width: 0;
  border-radius: 50px;
}

.D3DXDc {
 position: relative;
 right: 22px;
 fill: var(--sidebar_color_primary);
}

.GbZFNe {
 border-bottom: none;
}
.GbZFNe:hover, .GbZFNe:focus {
 background: var(--sidebar_button_background_hover); 
}

.aOHsTc, .PL5Wwe.H7du2 .t5F5nf, .PL5Wwe.KKjvXb .t5F5nf, .PL5Wwe.KKjvXb .sFuxxd, .PL5Wwe.qs41qe .t5F5nf, .PL5Wwe.qs41qe .sFuxxd, .GSEpfc .t5F5nf, .IliFSc {
 color: var(--sidebar_color_primary); 
}

.t5F5nf, .sFuxxd, .wR3Nid {
 color: var(--sidebar_color_secondary); 
}

.QeRfYe {
 fill: var(--sidebar_color_secondary); 
}
.QeRfYe:hover {
 fill: var(--sidebar_color_primary);
}


.teTAFe .PL5Wwe:focus, .teTAFe .PL5Wwe:hover:not(.tuKyod), .teTAFe .tuKyod:hover .LoYJxb {
 background: var(--sidebar_background_hover);
}

.teTAFe .PL5Wwe.KKjvXb:not(.tuKyod), .teTAFe .PL5Wwe.qs41qe:not(.tuKyod), .nz9RLc {
  background: var(--sidebar_background_active);
}

.mTRrYc {
	padding: 20px 32px 0 10px;
  min-height: 0;
}

.cmEq8b:hover, .cmEq8b:hover .jGyvbd, .cmEq8b:hover .oGsu4, .cmEq8b:hover .AflJR {
 background: var(--chat_message_background_hover);
}

.nF6pT.FwR7Pc .Hj5Fxb, .hu21Y:hover {
 background-color: var(--chat_message_background_hover); 
}

}
