css:
@-moz-document domain("paper.dropbox.com") {
#main-header,
#main-header-container {
  height: 35px;
}
.header-pad-title-and-folder {
  display: flex;
  align-items: center;
}
.hp-header-title-wrapper {
  margin-top: -4px;
  margin-right: 15px;
}
.hp-header-pad-subtitle {
  margin-top: -1px;
}
.main-header-right {
  height: 35px;
  padding-top: 0;
}
.hp-user-avatar-large.hp-user-avatar-type-image:after,
.hp-user-avatar-large .hp-user-avatar-display,
.hp-user-avatar-large.hp-user-avatar-with-border {
  height: 26px;
  width: 26px;
}
.hp-user-avatar-large.hp-user-avatar-with-border {
  margin-top: 4px;
}
.hp-sharing-button {
  height: 26px;
  margin-top: 4px;
}
.hp-sharing-button span {
  line-height: 22px;
}
#padpage-container {
  margin-top: 35px;
  height: calc(100% - 35px);
}
.ace-feature-viewport>.ace-editor {
  padding-top: 55px;
}
.main-header-right {
  margin-top: -11px;
}
#hp-editor-exp-assist-buttons {
  display: none;
}
.hp-versions-button.more-discoverable-sidebars {
  right: 70px;
}

.invite-team-members-panel {
    display: none;
}

.hp-mini-app-sidebar {
    width: 40px;
}

.hp-mini-app-sidebar-icon {
    margin: 16px 0 0 4px;
}

html:not(.hp-mobile):not(.hp-native-desktop) .hp-experiment-mini-app-sidebar #padpage-container {
    width: calc(100% - 40px);
}

html:not(.hp-mobile):not(.hp-native-desktop) .hp-experiment-mini-app-sidebar .pad-page-sidebar-open-button, html:not(.hp-mobile):not(.hp-native-desktop) .hp-experiment-mini-app-sidebar .hp-page:not(.hp-non-pad-page)~.hp-sidebar-enabled .hp-sidebar-container, html:not(.hp-mobile):not(.hp-native-desktop) .hp-experiment-mini-app-sidebar .hp-toc, html:not(.hp-mobile):not(.hp-native-desktop) .hp-experiment-mini-app-sidebar #padpage-container {
    left: 40px;
}
}
