css:
@-moz-document domain('particuliers.societegenerale.fr'), domain('particuliers.secure.societegenerale.fr'){
/*Global black*/
 html, body {
    background:none!important;
    background-color:#000000!important;
    color:white!important;
}
h1, h2, h3, h4, h5, h6, p, span, div, ul, li, nav {
    background-color:#000000!important;
    color:white!important;
}
a {
    background-color:#000000!important;
    color:#d10000!important;
}
/*Specific Stuff*/

/*Homepage*/
 #n2g_conteneur {
    background-color:#000000!important;
    color:white!important;
}
#n2g_zone_logo {
    background-color:#ffffff!important;
}
#n2g_zone_haut_1 .n2g_zone_logo a img {
    background-color:white!important;
}
#n2g_zone_centre {
    background-color:#000000!important;
    color:white!important;
}
#n2g_authentification.n2g_mire_connexion_form .n2g_picto_cadenas {
    background-color:white!important
}
#mots_cles {
    background-color:#000000!important;
    color:white!important;
}
/*Other pages*/
 #n2g_btn_bloc1 {
    background:transparent!important;
    background-color:#000000!important;
    color:white!important;
}
#n2g_bloc_bloc1, #n2g_bloc_bloc2, #n2g_bloc_bloc3, #n2g_bloc_bloc4, .n2g_home_orient_emprunter_bloc_del_projetimmo, .n2g_home_orient_emprunter_bloc_del_projetperso, .n2g_bloc_diagnostic, .n2g_bloc_diagnostic_button {
    background:transparent!important;
    background-color:#000000!important;
    color:white!important
}
#n2g_bloc_bloc4 div {
    background:transparent!important;
}
#n2g_zone_centre_milieu .n2g_tab_title_blue {
    border:0px solid transparent!important;
    background:transparent;
}
.n2g_toolbox {
    background:none!important;
}
.n2g_cdf_page_courante item_3 {
    color:blue!important
}
.n2g_bloc_prevenir, .n2g_bloc_prevenir_texte, .n2g_bloc_prevenir_fleche, .n2g_bloc_declarer, .n2g_bloc_declarer_texte, .n2g_bloc_declarer_fleche, .n2g_bloc_guides, .n2g_bloc_guides_texte, .n2g_bloc_guides_fleche, .n2g_bloc_moments, .n2g_bloc_moments_texte, .n2g_bloc_moments_fleche, .n2g_bloc_declarer_texte a.n2g_liens {
    background:transparent!important;
    background-color:white!important;
    color:black!important;
}
#n2g_zone_droite .n2g_menuBouton .n2g_menuBouton_liste .item_1 .usage_assistance span, #n2g_zone_droite .n2g_menuBouton .n2g_menuBouton_liste .item_2 .usage_contactsecu span {
    background-color:transparent!important
}
.box-1.case-ext, .box-2.case, .box-3.case, .box-4.case, .box-1.case-ext a, .box-2.case a, .box-3.case a, .box-4.case a {
    background:transparent;
    background-color:#000000!important;
    color:white!important;
}
.box-1 div {
    color:black!important;
}
.liste_rubrique_fiche_produit {
    background:transparent!important;
}
.n2g_cd_contact_sgetvous_items_list, .n2g_cd_contact_sgetvous_items_list li, .n2g_cd_contact_sgetvous_items_list a, .n2g_cd_contact_sgetvous_items_list a span {
    background-color:white!important;
}
/*Password windows fix*/
 .password_div, .password_div a img {
    background-color:white!important;
}
.valid_img,

.buttons {background:transparent!important;
    background-color:transparent!important;
}
/*Secure space (https://particuliers.secure.societegenerale.fr/)*/
 table, tbody, td, tr, th {
    background-color:black!important;
    color:white!important;
    border:1px solid darkgrey;
}
.ongletA, .ombreTableau, .finCadre {
    background-color:black!important;
    background:transparent!important;
}
.n2g_lgn_compteur_container .n2g_lgn_compteur {
    background:transparent!important;
    background-color:black!important;
    color:white!important;
}
.n2g_lgn_compteur_container:hover .n2g_lgn_compteur, .n2g_menu_section .n2g_menu_section_item .n2g_lgn_compteur_container.actif .n2g_lgn_compteur {
    color:#d10000!important;
}
.n2g_menu_section ul .n2g_menu_section_item a:hover, .n2g_menu_section ul .n2g_menu_section_item a:active, .n2g_menu_section ul .n2g_menu_section_item a:focus, .n2g_menu_section ul .n2g_menu_section_item a.actif, .n2g_menu_monprofil_links-rub li a:hover, .n2g_menu_monprofil_links-rub li a.actif, .n2g_menu_monprofil_links-rub li a.focus {
    border-left: #d10000 2px solid;
}
}
