css:
@-moz-document url-prefix("https://www.worldstarhiphop.com/videos/"), url-prefix("http://www.worldstarhiphop.com/videos/") {
body {
    margin: 0;
    padding: 0;
    color: #fff;
    font: 1.3em/1.616em Verdana, Geneva, sans-serif;
    background: #1f1f1f;
    min-width: 1070px;
    height: 100%;
}
#main {
    width: 990px;
    margin: 0 auto;
    background: #131313;
    position: relative;
    z-index: 1;
}
.container {
    width: 990px;
    margin: 0 auto;
    border: 1px solid #131313;
    background: #131313;
    z-index: 1;
}
.heading {
    text-align: center;
    padding: 3px 15px 15px;
    margin: 0 0 4px;
    border-bottom: 1px solid #d6d6d6;
    background: #fff;
    background-image: url(data:image/svg+xml;base64,PD94bWwgdmVyc2lvbj0iMS4wIiA/Pgo8c3ZnIHhtbG5zPSJod…EiIGhlaWdodD0iMSIgZmlsbD0idXJsKCNncmFkLXVjZ2ctZ2VuZXJhdGVkKSIgLz4KPC9zdmc+);
    background: -moz-linear-gradient(top, #fff 0%, #fff 36%, #f1f1f1 90%, #f1f1f1 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fff), color-stop(36%,#fff), color-stop(90%,#f1f1f1), color-stop(100%,#f1f1f1));
    background: -webkit-linear-gradient(top, #fff 0%,#fff 36%,#f1f1f1 90%,#f1f1f1 100%);
    background: -o-linear-gradient(top, #fff 0%,#fff 36%,#f1f1f1 90%,#f1f1f1 100%);
    background: -ms-linear-gradient(top, #fff 0%,#fff 36%,#f1f1f1 90%,#f1f1f1 100%);
    background: linear-gradient(to bottom, #1f1f1f 0%,#1f1f1f 36%,#1f1f1f 90%,#1f1f1f 100%);
    box-shadow: 0 2px 2px -2px rgba(0,0,0,.3);
    -webkit-box-shadow: 0 2px 2px -2px rgba(0,0,0,.3);
}
.views, .comments, .views a, .comments a {
    color: #fff;
}
.box .title a {
    color: #ffffff;
    font-weight: bold;
}
.heading h1 {
    font-size: 1.538em;
    line-height: 1.2em;
    text-transform: uppercase;
    margin: 0 200px;
    font-weight: normal;
    color: #ffffff;
}
.carousel .title {
    font-weight: normal;
    color: #ffffff;
    display: block;
    margin: 0 0 5px;
}
mark {
    color: #ffffff;
    background: none;
}
.carousel .btn-prev, .carousel .btn-next {
    position: absolute;
    top: 0;
    left: -46px;
    text-indent: -9999px;
    overflow: hidden;
    background: url(https://i.imgur.com/fstBaFj.png) no-repeat;
    width: 47px;
    height: 219px;
}
.carousel .btn-next {
    left: auto;
    right: -48px;
    background-position: 100% 0;
}
.widget-title h1 {
    text-transform: uppercase;
    color: #ffffff;
    font-size: 1.077em;
    line-height: 1.285em;
    margin: 0;
    font-weight: normal;
}
.widget .widget-title {
    margin: 0 -13px 14px -16px;
    text-align: center;
    padding: 14px;
    border-bottom: 1px solid #131313;
    background: #f1f1f1;
    background-image: url(data:image/svg+xml;base64,PD94bWwgdmVyc2lvbj0iMS4wIiA/Pgo8c3ZnIHhtbG5zPSJod…EiIGhlaWdodD0iMSIgZmlsbD0idXJsKCNncmFkLXVjZ2ctZ2VuZXJhdGVkKSIgLz4KPC9zdmc+);
    background: -moz-linear-gradient(top, #fff 0%, #fff 36%, #f1f1f1 90%, #f1f1f1 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fff), color-stop(36%,#fff), color-stop(90%,#f1f1f1), color-stop(100%,#f1f1f1));
    background: -webkit-linear-gradient(top, #fff 0%,#fff 36%,#f1f1f1 90%,#f1f1f1 100%);
    background: -o-linear-gradient(top, #fff 0%,#fff 36%,#f1f1f1 90%,#f1f1f1 100%);
    background: -ms-linear-gradient(top, #fff 0%,#fff 36%,#f1f1f1 90%,#f1f1f1 100%);
    background: #131313;
    box-shadow: 0 2px 2px -2px rgba(0,0,0,.3);
    -webkit-box-shadow: 0 2px 2px -2px rgba(0,0,0,.3);
}
.information .date {
    display: block;
    font-weight: bold;
    font-size: 0.923em;
    margin: 0 0 4px;
    color: #b7b7b7;
}
.video-list .title a {
    color: #0080ff;
}
.two-columns:after {
    content: '';
    position: absolute;
    z-index: -1;
    top: 15px;
    bottom: 0;
    right: 314px;
    border-left: 1px solid #131313;
}
.watch-view-count {
    float: right;
    color: #ababab;
    font-size: 1.538em;
    padding: 0 3px;
    font-weight: normal;
}
.comments-holder {
    background: #131313;
    padding: 0px 20px 0px 20px;
}
body {
    margin: 0;
    padding: 0;
    color: #fff;
    font: 1.3em/1.616em Verdana, Geneva, sans-serif;
    background: #131313;
    min-width: 1070px;
    height: 100%;
}
}
