css:
@-moz-document domain(alternatehistory.com)
{

#content .pageContent
  {
    background-color: rgb(0,0,0);
  }

  *
  {
  }


   /*  
  --------------------
  Color Pallete 
  --------------------
  */
  :root
  {
    --background-color-a: #1E1E1E;
    --border-color-a: #3672b1;
    --background-color-b: #3672b1;
    --background-color-c: rgb(21,45,60);
    --border-color-b: black;
    --color-a: #207cc9;
    --color-b: white;
    --ugc-link-hover-color-a: white;
    --ugc-link-focus-color-a: white;
    --background-color-d: #1e1f21;
    --border-color-c: #393939;
    --border-color-d: #357ecd;
    --background-color-e: #6cb2e4;
    --color-c: #ff8c10;
    --border-color-e: #415c87;
    --color-d: #6cb2e4;
    --border-color-f: #6cb2e4;
    --color-e: black;
    --background-color-f: #393939;
    --background-color-g: #D2B48C;
    --background-color-h: black;
    --background-color-i: #415c87;
    --color-f: rgb(204,204,204);
    --color-g: rgb(150,150,150);
    --color-h: rgb(200,200,200);
    --background-color-j: rgb(25,31,45);
    --background-color-k: #5b617e;
    --background-color-l: rgb(15, 62, 119);
  }
  /*

  /*
  --------------------
  Search
  --------------------
  */
  element.style
  {
    font-size: 20px;
  }
  
  #QuickSearch.active
  {
    background-color: var(--background-color-a);
  }

  #QuickSearch.active .formPopup
  {
    background-color: var(--background-color-a);
  }

  #QuickSearch .formPopup .controlsWrapper
  {
    background: var(--background-color-h);
  }

  .searchResult .snippet a
  {
    color: var(--main-color);
  }

  .searchResult .meta
  {
    color: var(--color-d);
  }

  .searchResult .meta a
  {
    color: var(--color-a);
  }
  /*
  --------------------
  Threadmark Making
  --------------------
  */

  .Touch .xenOverlay .formOverlay
  {
    background-color: var(--background-color-j);
  }

  /* Gradient attempt #1 */
  /*
  .xenOverlay .formOverlay
  {
    border-image: linear-gradient( var(--background-color-j),
      var(--background-color-a) );
  }
  */
  /*
  --------------------
  Body
  --------------------
  */
  html
  {
  }

  body
  {
    color: var(--color-f);
  }

  .pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt
  {
    color: var(--color-g);
  }

  body .faint, body a.faint, body .faint a
  {
    color: var(--color-g);
  }

  body .muted, body a.muted, body .muted a
  {
    color: var(--color-g);
  }

  body .muted a:hover
  {
    color: var(--color-b);
  }

  body .faint, body a.faint, body .faint a
  {
    color: var(--color-h);
  }

  #pageDescription
  {
    font-size: 11px;
    margin-top: 2px;
  }

  /*
  --------------------
  Forum Navigation 
  --------------------
  */

  body .itemPageNav a
  {
    background-color: var(--background-color-c);
    border: 1px solid var(--border-color-a);
  }

  .navTabs
  {
    background-color: var(--background-color-a);
    border-color: var(--border-color-a);
  }

  .navTabs .navTab.selected .tabLinks, .navTabs .navTab.selected .navLink
  {
    background-color: var(--background-color-b);
  }

  .breadcrumb
  {
    background-color: var(--background-color-c);
    border: 1px solid var(--border-color-b);
    font-size: 12px;
  }

  .breadcrumb .crust .arrow
  {
    display: none;
  }

  .breadcrumb .crust a.crumb
  {
    background-color: var(--background-color-a);
    padding-left: 10px;
  }

  .breadcrumb .crust a.crumb:hover
  {
    background-color: var(--background-color-c);
    padding-left: 10px;
  }

  .breadcrumb .crust:hover a.crumb
  {
    text-decoration: underline;
    color: var(--ugc-link-hover-color-a);
  }

  /*
  --------------------
  Links 
  --------------------
  */

  a:link, a:visited
  {
    color: var(--color-a);
  }

  a:hover
  {
    color: var(--ugc-link-hover-color-a);
    background: none;
    text-decoration: underline;
    box-shadow: none;
  }
  /*
  --------------------
  Nodes/Subforum 
  --------------------
  */

  .section
  {
    background-color: var(--background-color-c);
    border-color: var(--border-color-d);
  }

  .sectionMain
  {
    background-color: var(--background-color-c);
    border-color: var(--border-color-b);
    border-radius: 5px;
  }

  .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo
  {
    background-color: var(--background-color-c);
    border-bottom: 1px solid var(--border-color-b);
  }

  .nodeList .category, .forum_view .sectionMain
  {
    background-color: var(--background-color-c);
    border-color: var(--border-color-a);
  }

  .node .nodeLastPost
  {
    background-color: var(--background-color-d);
    border: 1px solid var(--border-color-c);
  }

  .nodeList .categoryStrip
  {
    background-color: var(--background-color-b);
    border-color: var(--border-color-d);
  }

  
  /*
  --------------------
  Miscellaneous
  --------------------
  */

  .secondaryContent
  {
    background-color: var(--background-color-c);
    border-color: var(--border-color-b);
    border-radius: 5px;
  }

  .sidebar .section .secondaryContent h3
  {
    border-bottom-color: var(--border-color-b);
  }

  .messageList .newMessagesNotice
  {
    color: var(--color-e);
  }

  .message .newIndicator
  {
    margin-right: -15px;
  }

.navPopup .listPlaceholder ol.secondaryContent.Unread
		{
			background-color: rgb(109, 199, 255);
		}

  /*
  --------------------
  Posts 
  --------------------
  */

  .messageText
  {
    font-size: 10pt;
    font-family: tahoma,arial,helvetica,sans-serif;
    line-height: 1.4;
  }

  .messageUserBlock
  {
    background-color: var(--background-color-a);
    border: 1px solid var(--border-color-b);
  }

  .messageUserBlock .arrow
  {
    border-left-color: var(--border-color-b);
    -moz-border-left-colors: var(--border-color-b);
  }

  .avatar img, .avatar .img, .avatarCropper
  {
    border: 1px solid var(--border-color-b);
    border-radius: 4px;
  }

  .message .messageInfo
  {
    background-color: var(--background-color-c);
  }

  .message .likesSummary
  {
    background-color: var(--background-color-d);
    border-color: var(--border-color-c);
  }

/*
  a.username
  {
    font-weight: bold;
    font-size: 100%;
    margin-top: -4px;
    margin-bottom: 0px;
    display: block;
    overflow: hidden;
    line-height: 24px;
  }
*/
  /*
  --------------------
  Threadmarks 
  --------------------
  */
  .messageList .message.hasThreadmark
  {
    border-top: 1px solid var(--border-color-b);
  }

  .threadmarker_nav_top .previous a, .threadmarker_nav_top .index a, .threadmarker_nav_top .next a, .threadmarker_nav_bottom .previous a, .threadmarker_nav_bottom .index a, .threadmarker_nav_bottom .next a
  {
    padding: 3px 6px;
    color: var(--color-b);
    background-color: var(--background-color-b);
    border: 1px solid var(--border-color-b);
    border-radius: 4px;
  }

  .threadmarker_nav_top .previous a:hover, .threadmarker_nav_top .index a:hover, .threadmarker_nav_top .next a:hover, .threadmarker_nav_bottom .previous a:hover, .threadmarker_nav_bottom .index a:hover, .threadmarker_nav_bottom .next a:hover
  {
    color: var(--color-e);
    background-color: var(--background-color-e);
    border: 1px solid var(--border-color-b);
    border-radius: 4px;
  }

  .threadmarker .label strong
  {
    color: var(--color-c);
  }

  .threadmarker .label
  {
    color: var(--color-b);
    background-color: var(--background-color-b);
    border: 1px solid var(--border-color-b);
    border-radius: 4px;
  }

  .threadmarker
  {
    border: none;
  }

  .threadmark
  {
    background-color: var(--background-color-c);
  }

  .memberListItem .extra .DateTime
  {
    padding-right: 10px;
  }
  /*
  --------------------
  Conversations 
  --------------------
  */
  .conversation_view .messageList
  {
    border-top: 1px solid var(--border-color-e);
    border-right: none;
    padding-right: 0;
    border-radius: 10px;
  }
  /*
  --------------------
  Quotes/Spoilers 
  --------------------
  */
  .bbCodeBlock .type
  {
    color: var(--color-b);
    background-color: var(--background-color-l);
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 100%), linear-gradient(to right, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0.1) 100%);
  }

  .bbCodeBlock, .hasJs .bbCodeSpoilerText
  {
    background-color: var(--background-color-a);
    border: 1px solid var(--border-color-c);
  }

  .bbCodeBlock, .hasJs .bbCodeSpoilerText
  {
    margin-right: 0;
  }

  .bbCodeQuote .quoteContainer
  {
    background-color: var(--background-color-h);
  }

  /*
  --------------------
  Buttons
  --------------------
  */
  .button, .button.primary
  {
    color: var(--color-b);
    background-color: var(--background-color-b);
    border: 1px solid var(--border-color-b);
    border-radius: 4px;
  }

  .button:hover, .button.primary:hover
  {
    color: var(--color-e);
    background-color: var(--background-color-e);
    border: 1px solid var(--border-color-b);
    border-radius: 4px;
  }

  a.callToAction
  {
    background-color: var(--background-color-i);
    border-color: var(border-color-e);
  }

  a.callToAction:hover
  {
    background-color: var(--background-color-i);
    border-color: var(border-color-e);
  }

  a.callToAction span
  {
    color: var(--color-b);
    background-color: var(--background-color-b);
    border-color: var(border-color-e);
  }

  /*
  --------------------
  Thread Lists
  --------------------
  */
  .discussionListItem, .primaryContent
  {
    background-color: var(--background-color-c);
    border-bottom: 1px solid var(--border-color-b);
  }

  .discussionListItem
  {
    /*
    background-color:var(--main-bg-color);
    border-bottom: var(--main-border);
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: var(--border-color-only); 
    */
    ;
  }

  .discussionListItem .posterAvatar, .discussionListItem .stats
  {
    background-color: var(--background-color-c);
  }

  .discussionList .sectionHeaders
  {
    background-color: var(--background-color-b);
    border: 1px solid var(--border-color-b);
  }

  .sectionFooter
  {
    background-color: var(--background-color-b);
    border: 1px solid rgb(0, 0, 0);
    border-top: none;
  }

  .sectionFooter a
  {
    color: var(--color-b);
  }
  /*
  --------------------
  Thread Banners 
  --------------------
  */

  .titleBar h1
  {
    /*
    font-size: 18pt;
    overflow: hidden;
    overflow-x: hidden;
    overflow-y: hidden;
    zoom: 1;
    */
    ;
  }

  #uaThreadViewContainer, #uaThreadReadContainer
  {
    border: 1px solid var(--border-color-b);
  }

  #uaThreadViewContainer h3, #uaThreadReadContainer h3
  {
    border-bottom: 1px solid var(--border-color-b);
  }
  /*
  --------------------
  Footer 
  --------------------
  */
  .footer .pageContent
  {
    background-color: var(--background-color-a);
    border: 0px solid var(--border-color-b);
  }
  /*
  --------------------
  Text Editor 
  --------------------
  */
  .messageList .message, .quickReply
  {
    padding-bottom: 10px;
  }

  .messageList .message, .quickReply
  {
    background-color: var(--background-color-c);
    border: 1px solid var(--border-color-b);
    border-radius: 5px;
  }

  #QuickReply
  {
    background-color: var(--background-color-c);
  }

  .quickReply .quickReplyTitle
  {
    display: none;
  }

  #QuickReply .submitUnit
  {
    background-color: var(--background-color-c);
    margin-top: 0px;
  }

  .bbCodeEditorContainer
  {
    background-color: var(--background-color-c);
  }

  textarea.textCtrl.Elastic
  {
    background-color: var(--background-color-a);
  }

  .textCtrl
  {
    background-image: none;
    background-color: var(--background-color-j);
    color: var(--color-b);
    border: 1px solid var(--border-color-e);
  }

  .textCtrl:focus
  {
    background-image: none;
    background-color: var(--background-color-k);
    color: var(--color-b);
  }

  textarea.textCtrl:focus
  {
    background-image: none;
  }

  .redactor_toolbar
  {
    background-color: var(--background-color-b);
  }

  .redactor_box
  {
    background-color: var(--background-color-a);
    border: 1px solid var(--border-color-b);
  }

  div html
  {
    background-color: var(--background-color-d) !important;
  }
  /*
  --------------------
  User Polls 
  --------------------
  */
  .pollBlock .pollOption label:hover
  {
    color: var(--color-e);
    background-color: var(--background-color-e);
  }
  /*
  --------------------
  Page Navigation 
  --------------------
  */
  .PageNav a, .PageNav a
  {
    color: var(color-d);
    background-color: var(--background-color-c);
    border-color: var(--border-color-b);
  }

  .PageNav a:hover, .PageNav a:focus
  {
    color: var(color-d);
    background-color: var(--background-color-c);
    border-color: var(--border-color-f);
  }

  .PageNav a.currentPage
  {
    color: var(--color-e);
    background-color: var(--background-color-b);
    border-color: var(--border-color-b);
  }

  .pageNavLinkGroup .linkGroup
  {
    background-color: var(--background-color-h);
  }

  /*
  --------------------
  Dropdown Menu
  --------------------
  */
  .Menu .primaryContent, .Menu .secondaryContent
  {
    background-color: var(--background-color-c);
  }

  .navPopup .PopupItemLinkActive:hover
  {
    background-color: var(--background-color-a);
  }

  .Menu .sectionFooter
  {
    background-color: var(--background-color-b);
  }
  /*
  --------------------
  Alerts
  --------------------
  */

  #StackAlerts
  {
    background-color: var(--background-color-e);
  }

  #StackAlerts .stackAlertContent
  {
    background-color: rgb(40,40,50);
  }
}
