css:
@-moz-document domain("s.to") {
   .ssto-share-buttons {
    display: none !important;
}
.main-header {
    background-color: #0f1620;
    color: #eee;
    background-image: none;
}
.logo-wrapper {
    background: #0f1620 none repeat scroll 0 0;
}
.header-content {
    background: #0f1620 none repeat scroll 0 0;
}
.primary-navigation .signup-toggle {
    color: #fff;
    background: transparent none repeat scroll 0 0;
    border: 2px solid #0f1620;
    background-color: #0f1620;
    padding: 12px 25px;
    border-radius: 4px;
    margin-left: 25px;
}
.primary-navigation .signup-toggle:hover {
    color:#eee;
    border:2px solid #888;
    background:transparent
}
.hosterSiteVideo {
    background-color: #0f1620;
}
.socialshareboxseries {
    display: none !important;
}
.socialMediaBox {
    display: none !important;
}
.hosterSiteDirectNav {
    background-color: #0f1620;
    color: #fff;
}
#footer {
    background-color: #0f1620;
    ;
}
.footer-container {
    background-color: #222;
    display: none !important;
}
.globalNewsMessage {
    background-color: #222;
    display: none !important;
}
.series-add {
    background-color: #222;
    display: none !important;
}
.col-md-6-5 {
    width: 75%;
}
.button.blue {
    background: #0f1620;
}
.button.blue:hover {
    background: #888;
}
.hosterSectionTitle {
    background: #333;
}
#wrapper {
    background: #0f1620;
}
h3 {
    color: #ddd;
}
a {
    color: #ddd;
}
h1 {
    color: #ddd;
}
p {
    color: #ddd;
}
.paragraph-end {
    background: #0f1620;
}
.homeContentPromotionBox > .homeSliderView .col-md-3 {
    background: #0f1620;
}
html {
    background: #0f1620;
}
#footer {
    display: none !important;
}
.catalogNav li {
    background: #0f1620;
}
strong {
    color: #ddd;
}
seriesListNavigation a {
    color: #fff;
    border-bottom: 2px solid #fff;
}
.genre ul li a {
    color: #fff;
}
seriesListNavigation {
    color: #fff;
    background: #999;
}
.container {
    width: 90vw;
}
.hosterSectionTitle {
    display: none !important;
}
.changeLanguageBox {
    background: #1c2b40;
    width:250px;
    box-shadow:none !important; 
}
.selectedLanguage {
    border:none !important;
}
.changeLanguageBox img {
    cursor:pointer;
    border:none;
    height:30px
}

.allSeriesBox {
	display: none;
}

.descriptionSpoilerPlaceholder {
   display: none;
}

.submitNewDescription {
   display: none;
}
}

@-moz-document url-prefix("https://s.to/redirect/") {
    .header-logo {
        background: #0f1620 none repeat scroll 0 0;
    }
    #wrapper {
        background: #0f1620 none repeat scroll 0 0;
        width: 75%;
        height: 75%;
        font-size: 100%;
    }
    .sk-double-bounce {
        width: 60px;
        height: 60px;
    }
    .container {
        background: #0f1620 none repeat scroll 0 0;
        width: 90vw;
    }
    h1 {
        font-size: 11px;
    }
    h4 {
        display: none !important;
    }
    .container h1 {
        font-size: 8px;
    }
    p {
        display: none !important;
    }
    .logo-wrapper {
        display: none !important;
    }
    small {
        display: none !important;
    }
    div {
        background-color: #0f1620;
    }
}
