css:
@-moz-document domain("toutiao.com") {
* {
	color: #aaa;
	background-color: #151515;
}
body {
	background-color: #151515 !important;
}
/*
img {
	opacity: 0.7;
}
*/

a {
	color: #C7EDFF !important;
	background: transparent;
}
span {
	color: #C7EDCC !important;
}
input {
	color: #C7EDCC !important;
	background-color: #555555 !important;
}
textarea {
	color: #C7EDCC !important;
	background-color: #555555 !important;
}
p {
	color: #aaa !important;
	background: transparent;
	font-family: DFYuanW5-GB !important;
	font-size: 1.3em !important;
}
a[class*="news_"] {
	background-color: #151515 !important;
}
a.__all__ {
	background-color: #151515 !important;
}
a.item {
	background-color: #151515 !important;
}
div.pin {
	background-color: #151515 !important;
}
div.action-pane {
	background-color: #151515 !important;
}
div.header-bar {
	background: #151515 !important;
}
div.submenu {
	background: #151515 !important;
}
div.container {
	background: #151515 !important;
}
h1 {
	color: #aaa !important;
}
div.toutiao-header div.middlebar,
div.bui-left.index-left,
div.pane-module,
div.y-left.index-channel,
.right-img {
	background-color: #151515 !important;
}
a.tag,
div.answer-user,
div.fix-tools.showTip,
.outer,
.inner {
	background: #151515 !important;
	color: #C7EDFF !important;
}
}
