div.center{width:100%}div.center:after{clear:both}#header{background:#000;height:100px;padding:20px 0}#header .h1{padding-left:30px;width:520px}#header .h1 div{font-weight:normal;font-size:14px;line-height:12px;margin-top:2px;text-align:left}
#wrapper{width:100%;margin:0 auto}.update-hide{padding:0!important}#header .profile{width:600px;float:none;position:absolute;left:620px}#header .profile ul{margin-top:17px}
