.console_box{width:100%;margin:0 auto 40px auto}.console{padding:.5em;font:normal 1em 'PT Mono',monospace;color:#b2b2b2;border:1px solid #555;background:#222}
.fastlinks{background:#333;font:normal 1em Sans-Serif;color:white;padding:.5em}.fastlinks ol{font:normal 1em Sans-Serif;padding:.5em 1.0em;list-style-type:none;counter-reset:item}
.fastlinks ol li:before{content:'';counter-increment:item}.undermenu{font:bold 1em 'Arial',monospace;background:#a62222}
.undermenu>ul{max-width:1280px;width:100%}.undermenu>ul>li:before{background:#4c0808}.undermenu>ul>li>a,.undermenu>ul>li>span{padding:12px 20px 15px 20px;height:40px}
.undermenu>ul>li>a:hover{background:#f59300}.undermenu>ul>li a>span{color:#f59300;font-weight:normal;margin-left:2px}.undermenu>ul>li>a:hover>span{color:#fff}
.sidebar_left{padding:0 20px 0 20px;width:240px}.lenta_menu{margin-bottom:30px}.imp_messages span,.adver a{color:#999;display:inline-block;margin-top:10px}
.advItemTable td{font-size:14px;color:#777;padding-bottom:5px}.advItemTable td a{font-family:PT Sans,Helvetica Neue,Helvetica,Arial,sans-serif;font-size:14px;font-weight:normal;color:#aaa;text-decoration:none}
#content_box{width:660px;position:relative}table.trade td:first-child a{color:#aaa;text-decoration:none}table.trade td:first-child a:hover{color:#fff;text-decoration:none}
.feedtop .inside{line-height:1.1em}.feedtop .inside span{display:none}.feedtop .inside .user{margin-right:5px}#mainthemes{padding:30px 0 20px 0}#mainthemes ul{color:#aaa;list-style:none;margin:0}
#inindex{float:none}#inindex li{display:block;margin:0 20px 0 0;max-width:100%;text-overflow:ellipsis}#inindex li.feedtop_header{display:block;margin:10px 0 5px 0;font-size:12px}
#inindex li.feedtop_header a{text-decoration:none}#inindex li.feedtop_header:first-letter {text-transform:uppercase}#inindex span,#inindex ul li a{color:#f59300;text-decoration:none}
#mainthemes .red{color:inherit}#mainthemes a{color:inherit!important;text-decoration:none;font-size:1.5em}.popstock_header{display:block;text-decoration:none;margin:10px 0 5px 0!important;text-transform:capitalize}
.topic h1.title,.topic h2.title{color:#333}.topic h1 a,.topic h2 a{color:#333}#content .topic h2{margin-bottom:0}.books_vote_stars{background-image:url('http://file.helloidea.ru/uploads/stars.png')}
#content .topic hr{display:none}#content .topic{margin-bottom:30px;overflow:visible}#content .topic::after{display:block;content:'';margin:0 -30px;border-bottom:1px solid #ccc;padding-bottom:30px}
#content .topic .action{font-size:12px}#content .topic li.author a{font-weight:normal}#content .topic a[title='Читать дальше']{background:#fff;padding:16px 0 3px 0;position:absolute;z-index:100;margin-left:-12px;margin-top:-18px}
#content .topic a[title='Читать дальше']:hover{border:0;color:#f63;text-decoration:underline}.topic .tags{width:60%}.topic .voting li.donate a{border:0}
.topic .voting li.plus a{border:0}.topic .voting li a:hover{color:#f63;text-decoration:underline}.topic .voting li.total a:hover{color:#f63;text-decoration:underline}
.topic li.comments-add{float:none;margin-top:30px;margin-bottom:20px}.comments-add a{border:2px solid #ccc;border-radius:3px;position:relative;text-decoration:none;padding:5px}
.topic ul .comments-add a:hover{background-color:#f63;border-color:#f63;color:#fff!important;text-decoration:none}.comments-add a span{color:#999!important}
.comments-add a:hover span{color:#fff!important;text-decoration:none}#mood{border-bottom:0;margin:0;font-size:12px;font-weight:normal;color:#999;padding:0 0 35px 0}
#wrapper div.content_desc{background-color:#222;height:40px;width:100%}#wrapper div.content_desc p{color:#999;padding:10px 15px;font-weight:bold}#content{width:100%;padding:30px}
.topic h1.title{color:#fff;margin-bottom:5px;font-size:18pt;font-weight:bold}.topic h2.title{font-size:15pt;font-weight:bold}.topic h1 span,.topic h2 span{color:#333;display:block}
.topic h1 a.blog,.topic h2 a.blog{background:#f59300;color:#fff;font-size:.5em;font-weight:bold;display:inline-block;padding:0 10px 0 10px;margin-bottom:10px}
.topic .action{color:#333;margin:10px 0 20px 0}.topic .action li{margin-left:0;margin-right:10px}.topic .action li.date{color:#333;font-size:12px;margin-top:0}
.topic .action li.author a{font-weight:bold;color:#333}.topic .action li a{outline:0;text-decoration:none}.trader_other{color:#333!important}.topic .action li.edit a{color:inherit}
.topic .content{color:#222;font-family:'PT Sans',Verdana,sans-serif;font-size:12pt;font-weight:400;line-height:1.2em}.topic .content table td{border-bottom:1px solid #ccc;vertical-align:top;padding-top:.5em}
.topic .content blockquote{margin-bottom:15px}.topic .tags{width:80%}.topic .tags li:first-child{display:none}.topic .tags li{line-height:3em;color:#fff}
.topic .tags li a{border-radius:4px;background:#eee;color:#999;padding:.4em 1em .6em 1em;white-space:nowrap}.topic .tags li a:hover{text-decoration:none;color:#fff;background:#f63}
#form_comment_text_ifr{height:240px}.comments .comment .content{background:0}a[name="comments"]+div{display:none}#adfox_mid1,#adfox_mid2{display:none}
.comment .content .text{border-radius:8px;background:#f7f7f7;padding:12px 25px 17px 15px}.comment .info{margin-top:5px}.tagb{margin-top:30px;width:580px;margin:10px 0 10px 200px;padding:0}
.tagb ul.cloud{text-align:left}.sidebar_right{width:320px;float:left;min-height:200px;margin-left:30px}.sidebar_right .companies_blogs{display:none}div.bbr{margin:20px 0 0 0;padding:20px 0 0 0;border:0;border-top:1px solid #646464}
