css:
@-moz-document domain("archive.org") {
 body.navia.bgEEE {
    background-color: #222 !important;
}
body.navia #nav-tophat {
    background-color: #222;
    color: #ccc;
}
body {
    background: black;
}
h1, h2, h3, h4 {
    color: #999!important;
}
#wrap, #wrapper {
    color: #999;
    background: #111;
    border-color: #333;
    text-shadow: none !important;
}
.col-xs-12, .preamble, .preamble div {
    color: white!important;
    background: #222 !important;
    border-color: #333;
    text-shadow: none !important;
}
.welcome, #wrap .width-max {
    background: #333 !important;
    color: white !important;
    text-shadow: none !important;
    border: none !important;
}
#theatre-controls, .iconochive-fullscreen, .iconochive-flash, .iconochive-unmute {
    background: #333 !important;
}
.boxy, .item-ia {
    background: #111 !important;
    color: white !important;
    text-shadow: none !important;
    border-style: solid !important;
    border-width: 2px !important;
    border-color: #444 !important;
}
body.ia-module.tiles .item-ia .item-ttl .ttl {
    color:white !important;
    ;
}
#favorite-button, #share-button, #flag-button, .sortbar {
    background: #111 !important;
    border: black !important;
}
.boxy-ttl {
    color: white !important;
    text-shadow: none !important;
}
.small-label {
    margin-top: 0!important;
    padding-top: 0!important;
}
p .show-all {
    color: white !important;
    background: #f60 !important;
    border-color: #333 !important;
    text-shadow: none !important;
}
a, a * {
    color: #f60 !important;
    text-decoration: none !important;
}
a:visited, a:visited * {
    color: #f80 !important;
}
a:hover, a:hover * {
    color: #f00 !important;
}
.about-box, tbody, tr, td, .well, .white-box {
    color: #999!important;
    background: #111!important;
    border-color: #333!important;
    text-shadow: none !important;
}
p {
    color: #999!important;
    background: #111!important;
    text-shadow: none !important;
    border: none !important;
}
}
