css:
@-moz-document domain(www.urbandictionary.com)
{

body {
    background: #101010 !important; }

h1, h2, h3, h4, h5, h6 {
    color: #CCC !important; }

ul.pagination li.current a {
    background: #404040 !important; }
ul.pagination li:hover a, ul.pagination li a:focus {
    background: #404040 !important; }


/* ---------- Header ---------- */

#urban-top-bar .top-bar-section .dropdown {
    color: #CCC !important;
    background-color: #202020 !important; }
#urban-top-bar .top-bar-section .dropdown a {
    color: #CCC !important; }
#urban-top-bar .top-bar-section .dropdown a:hover {
    color: white !important; }
#urban-top-bar .top-bar-section .dropdown.alphabet-dropdown li a {
    color: white !important;
    background: #404040 !important; }
#urban-top-bar .top-bar-section .dropdown.alphabet-dropdown li a:hover {
    background: #606060 !important; }

.search-and-actions .main-search input {
    border: 1px solid #050505 !important;
    color: white !important;
    background: #505050 !important; }
.aa-dropdown-menu {
    background-color: #303030 !important; }
.aa-suggestion:hover {
    background: #404040 !important; }
.term {
    color: white !important; }
.preview {
    color: #CCC !important; }

#urban-top-bar a {
    color: #CCC !important; }
#urban-top-bar a:hover {
    color: white !important; }


/* ---------- Definition Panels ---------- */

.slogan-panel .like_buttons a, .button {
    box-shadow: none !important;
    background-color: #303030 !important; }
.slogan-panel .like_buttons a:hover, .button:hover {
    color: white !important;
    background-color: #505050 !important; }

.def-panel .share .svgicon svg {
    fill: #CCC !important; }
.def-panel .share .svgicon svg:hover {
    fill: white !important; }

.def-footer .circle-link, .def-footer.qtip .qtip-content {
    background: #303030 !important; }
.def-footer .circle-link:hover {
    background: #505050 !important; }
.def-footer .circle-link .svgicon svg {
    fill: #CCC !important; }

.def-panel, #urban-top-bar {
    box-shadow: none !important;
    border: 1px solid #050505 !important;
    background: #202020 !important; }
.def-panel .meaning, .def-panel .contributor {
    color: white !important; }
.def-panel .example {
    color: #CCC !important; }

.panel {
    border-color: #050505 !important;
    box-shadow: none !important;
    background: #202020 !important; }
.panel li, .panel h4 .underline {
    color: #CCC !important; }

.panel .button.large {
    color: #CCC !important; }
.panel .button.large:hover {
    color: white !important; }

.def-panel a, .panel a {
    color: #58ACFA !important; }
.def-panel a:hover, .panel a:hover {
    color: #9ccefc !important; }

.qtip.qtip-urban .qtip-content {
    color: #CCC !important;
    background: #303030 !important; }
.def-footer.qtip {
    border-color: #050505 !important; }

a.up, a.down {
    color: white !important;
    background-color: #404040 !important; }
a.up:hover, .def-panel .thumbs.upvoted .up {
    background: #05ff56 !important; }
a.down:hover, .def-panel .thumbs.downvoted .down {
    background: red !important; }
.def-footer .thumbs .svgicon svg {
    fill: white !important; }

.mug-ad-03a .tone, .mug-ad-03b .tone, .mug-ad-03d .tone, .mug-ad-03e .tone {
    border-top: 1px solid #050505 !important;
    background-color: #505050 !important; }
.new-mug-ad .text {
    color: white !important; }


/* ---------- Browse ---------- */

.filter-panel a {
    color: white !important;
    background: #202020 !important; }
.filter-panel a:hover {
    background: #505050 !important; }


/* ---------- Specific Definition ---------- */

.def-panel .play-sound .svgicon-ud_speaker svg {
    fill: #CCC !important; }
.def-panel .play-sound .svgicon-ud_speaker svg:hover {
    fill: white !important; }

.c3 svg {
    fill: white !important; }


/* ---------- Define a Word ---------- */

.blue .blue-inner {
    color: #CCC !important;
    background: #202020 !important; }
.blue .blue-inner a {
    color: #58ACFA !important; }
.blue .blue-inner a:hover {
    color: #9ccefc !important; }

#add-form #definition_word, .blue textarea, .giphy-component .giphy-input-wrapper input, #add-form #definition_tags + .selectize-control .selectize-input {
    color: white !important;
    background: #505050 !important; }

input, input::placeholder, .blue textarea::placeholder {
    color: white !important; }

#definition_language.select.required {
    border-color: #050505 !important;
    color: white !important;
    background: #505050 !important; }
}


@-moz-document domain(help.urbandictionary.com)
{

body {
    background: #101010 !important; }

.search-and-actions .main-search input {
    color: white !important;
    background: #505050 !important; }

.twitter-typeahead .tt-dropdown-menu {
    border-color: #050505 !important;
    box-shadow: none !important;
    color: #606060 !important;
    background: #303030 !important; }
.title {
    color: #808080 !important; }

.panel {
    box-shadow: none !important;
    background: #202020 !important; }

.panel h1, .panel h2, .panel h3, .panel h4, .panel h5, .panel h6, .panel p, .panel li, .panel dl {
    color: white !important; }

.panel a {
    color: #58ACFA !important; }
.panel a:hover {
    text-decoration: underline !important;
color: white !important; }

.button {
    background: #505050 !important; }
.button.radius {
    color: white !important; }
.button:hover {
    background: #707070 !important; }
}
