css:
@-moz-document domain('promodj.com') {

:root {
  --color1: #77babc;
  --color2: #5b8e90;
  --color3: #2c2c36;
  --color4: #23232d;
  --color5: #1d1d24;
  --color6: #848491;
  --color7: #2f2f39;
  --color8: #5a5a63;
  --color9: #c6c6d3;
  --color10: #d3d3e1;
  --color11: #85ced0;
}

::selection {
  background-color: #b1ad91;
  color: #ccc;
}

::-moz-selection {
  background-color: #b1ad91;
  color: #ddd !important;
}

*::-webkit-scrollbar {
  background-color: var(--color4) !important;
  border: 1px solid var(--color3) !important;
}

*::-webkit-scrollbar-thumb {
  background-color: var(--color7) !important;
}

*::-webkit-scrollbar-button {
  background-color: var(--color7) !important;
}

body {
  background-color: var(--color5);
}

.body_container_branded,
.tracks_dump .track,
.header_pre_container_branded,
.header_container_branded,
.popup_container {
  background-color: var(--color4);
}

.popup_container {
  border: none;
}

input,
h5,
.generic_title span.rating a {
  background-color: var(--color3);
}

div.playerr_standalone.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(4) {
  background-color: var(--color6) !important;
}

h1,
h2,
h3,
h4,
h5,
h6,
div.dj_mood,
.motd_now_playing a,
.generic_title span.rating {
  color: var(--color10) !important;
}

a,
a.register,
a.login,
.track .title_opinion a,
a.title_opinion,
span.title_opinion,
.motd_now_playing a, .header_pre a {
  color: var(--color1);
}

a:hover,
span.downloads_count a:hover,
span.play_button a:hover {
  color: var(--color2);
}

.dj_bit,
div.h5 .content_inner p,
.dj_bblock .dj_contacts,
.dj_bblock .dj_promorank,
.dj_bblock .dj_universal,
.dj_bblock .dj_rss, div.comment_body, body {
  color: var(--color6);
}

table.dj_menu a {
  color: var(--color6) !important;
}

td,
th,
strong,
b,
input {
  color: var(--color8);
}

table.dj_menu a:hover,
.dj_bblock h5 a {
  color: var(--color10) !important;
}

div.mainmenu {
  background-color: var(--color2) !important;
}

table.dj_menu td.dj_menu_item_active {
  background-color: var(--color3);
}

#flash_prelisten
  > div.playerr_standard.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(4) {
  background-color: var(--color6) !important ;
}

table.dj_menu,
div.h5, ul.comments li.comments1 {
  border-color: var(--color3);
}

div#dmiac #dmiacr,
div#dmiac #dmiacl,
div.h5c,
#content_right aside > div > div > iframe, #billing_2018_autumn, #overlaybranding {
  display: none !important;
}

.dj_gift_small img {
  border-radius: 50% !important;
  overflow: hidden;
}

#flash_prelisten
  > div.playerr_standard.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(5) {
  color: var(--color1) !important;
}

span.downloads_count a,
span.comments_count a {
  filter: invert(1) brightness(1.5) !important;
}

div.playerr_bigdownloadbutton > a > img,
div.playerr_bigplaybutton > img,
div.playerr_standalone.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(2),
span.play_button a,
.playerr_volume_item.playerr_volume_item_active_fc1,
#header_pre_playerr.header_pre_playerr_play,
#flash_prelisten
  > div.playerr_standard.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(2),
#header_pre_playerr.header_pre_playerr_pause {
  filter: hue-rotate(179deg) saturate(0.5) brightness(1.4);
}

div.playerr_standalone.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(3),
div.playerr_standalone.playerr.__rototype_destructable.playerr_active
  > div:nth-child(3)
  > div
  > div:nth-child(1),
div.playerr_standalone.playerr.__rototype_destructable
  > div:nth-child(3)
  > div
  > div:nth-child(3),
.playerr_volume_item,
#content_left
  > div
  > div
  > div
  > div.dj_bblock
  > div:nth-child(2)
  > table
  > tbody
  > tr
  > td
  > h5
  > a
  > img,
#dj_menu > tbody > tr > td.dj_menu_title > a:nth-child(2) > img {
  filter: hue-rotate(179deg) saturate(0.5) brightness(0.7);
}

.mainmenu_mixednews_link:after,
.mainmenu_mixednews_link:before {
  filter: hue-rotate(180deg);
}
#header_pre_volume .playerr_volume_item {
  width: 30px;
}
div[style*="padding-top: 300px;"] {
    padding-top: 20px!important;
}


}
