css:
@-moz-document domain("php.net") {
body {
    font: /*[[font-size]]*//1.4 'Open Sans', Georgia, Times,
    'Times New Roman', serif;
    background-color: #0f1419;
    color: #c5c5c5;
    font-weight: 400;
}
@media (min-width: 768px) {
    body {
        margin: 2.5rem 0 0;
    }
}
h1,
h2,
h3,
h4,
h5,
h6,
.title a,
.title {
    font-family: 'Fira Sans', 'Helvetica Neue', Helvetica,
    Arial, sans-serif;
    color: #cccccc;
    border-bottom: 1px solid #1f2633;
    font-weight: bold;
}
table,
td,
tr,
th {
    background-color: inherit;
    border-collapse: collapse;
    border-color: #1d242b;
    vertical-align: bottom;
}
td,
th {
    padding: 10px;
}
input,
textarea,
select,
button {
    background-color: #191f26;
    color: #c5c5c5;
    border: 1px solid #1f2633;
}
a {
    color: #14abf5;
}
a:visited {
    color: #ccc;
}
a:hover {
    color: #14abf5;
}
pre {
    background-color: #060c12;
    color: #e6e1cf;
    padding: 15px;
}
code {
    padding: 15px;
    border: 0px solid #1f2633;
    font-family: 'Fira Code', 'Source Code Pro', Menlo, Monaco,
    Consolas, 'DejaVu Sans Mono', Inconsolata, monospace;
    font-size: 1em;
    line-height: 1.5;
}

.methodname b,
.methodname strong,
.methodname a,
.classsynopsis .classname,
.parameter {
    color: #e808ff;
}
#head-nav {
    background-color: #191f26;
    border-bottom: 1px solid #1f2633;
    font-size: 0.8em;
    position: absolute;
}
.navbar .nav .active > a,
.navbar .nav .active > a:hover,
.navbar .nav .active > a:focus {
    color: #fff;
    background-color: #048dd0;
}
.navbar .brand {
    height: 1rem;
}
.headsup {
    margin: 1em auto;
    padding: 0.5rem 0;
    height: 1.5rem;
    background-color: #0f1419;
    border-bottom: 0.125rem solid #3e0320;
}
.headsup a {
    color: #ffdc91;
    border: none;
}
#breadcrumbs {
    padding: 1rem 0;
}
#layout-content {
    color: inherit;
    background-color: #090d13;
}
#layout-content:not(:only-child) {
    border-right: 0;
}

#layout-content a:link,
#usernotes .note .name {
    color: #3db4ff;
    border: none;
    text-decoration: none;
}
.layout-menu ul.child-menu-list li.current a,
#layout-content a:visited {
    color: #4082d6;
}
#layout-content a:hover {
    color: #00daff;
    text-decoration: underline;
}
#usernotes .note .date,
#usernotes .note .votes .tally {
    color: inherit;
}
div.tip,
div.warning,
div.caution,
blockquote.note {
    border: 1px solid;
    border-left: 5px solid;
    border-top: 0;
    border-bottom: 0;
    border-right: 0;
}

blockquote.note strong.note,
div.tip strong.tip,
div.warning strong.warning,
div.caution strong.caution {
    font-weight: bold;
    text-transform: uppercase;
    font-family: sans-serif;
    font-size: small;
}
article strong,
#layout-content ol strong,
#layout-content ul strong,
#layout-content p strong {
    font-weight: bold;
}
div.tip {
    border-color: #538202;
    background-color: #2f4a01;
}
div.warning {
    background-color: #7d0404;
    border-color: #ca0000;
    color: blanchedalmond;
}
div.warning .title {
    border-color: #c4c9df;
}

div.caution {
    border-color: #ad4800;
    background-color: #823701;
}
blockquote.note,
.refsect1 blockquote.note {
    background-color: #0b1620;
    border-color: #082e51;
}

table.standard {
    border-style: solid;
    border: 1px solid #1d242b;
    margin: 1em auto;
}
table.standard td,
table.standard th {
    border: 1px solid #1d242b;
    padding: 5px 10px;
    color: inherit;
    font-size: inherit;
}
table.standard tr:nth-child(even) td {
    background-color: inherit;
}
table.standard tr:nth-child(even) td.subr,
table.standard tr:nth-child(even) th.subr,
table.standard tr td.subr,
table.standard tr th.subr,
table.standard tr:nth-child(even) td.sub,
table.standard tr:nth-child(even) th.sub,
table.standard tr td.sub,
table.standard tr th.sub {
    background: inherit;
}
table.standard td.subr,
table.standard th.subr {
    text-align: right;
}
#usernotes .note .text,
.docs .example-contents > [class$='code'],
.docs .example-contents.screen,
.informalexample .literallayout {
    padding: 0;
    box-shadow: none;
    border-radius: 0;
    border: 1px solid #1d242b;
    background-color: #090d13;
}
.docs .example-contents.screen {
    background-color: #10151c;
}
p code,
li code,
dt code,
dl code,
.para var,
.simpara var {
    background-color: inherit;
    border-radius: 0;
    color: #d2991d;
    padding: 2px 3px;
    white-space: nowrap;
    font-family: 'Fira Code', 'Source Code Pro', Menlo, Monaco,
    Consolas, 'DejaVu Sans Mono', Inconsolata, monospace;
}
.downloads .content-box pre {
    background-color: inherit;
    border: 1px solid #1f2633;
}
header.title,
h1:after,
h2:after,
h3:after {
    border-bottom: 0;
}
.docs .classsynopsis,
.refsect1 .fieldsynopsis,
.refsect1 .dc-description,
.docs .sect1 .dc-description {
    background: #10151c;
    border-color: #10151c;
    border: 1px solid #0f1419;
}
.refsect1 .dc-description code,
.docs .sect1 .dc-description code .docs code.parameter {
    padding: 0;
}

.doctable,
.segmentedlist {
    border-color: #1d242b;
}
.doctable caption,
.segmentedlist caption {
    text-align: left;
    margin: 0.5rem 0;
}
.doctable thead tr,
.segmentedlist thead tr {
    border-color: #1d242b;
    border-bottom-color: #082e51;
    border-bottom-width: 2px;
    color: #333;
}
.doctable th,
.segmentedlist th {
    background-color: #10151c;
    color: #369;
    text-transform: uppercase;
    font-size: small;
    font-family: sans-serif;
}
.doctable tr,
.segmentedlist tr {
    border-color: #1d242b
}
.doctable tbody tr:nth-child(odd),
.segmentedlist tbody tr:nth-child(odd) {
    background-color: #090d13;
}
.doctable tbody tr:nth-child(even),
.segmentedlist tbody tr:nth-child(even) {
    background-color: #0f1419;
}
}

@-moz-document domain("php.net") {
code span[style="color: #DD0000"] {
    color: #0068d0 !important;
}
code span[style="color: #0000BB"] {
    color: #e808ff !important;
}
code span[style="color: #007700"] {
    color: #00de5c !important;
}
code span[style="color: #FF8000"] {
    color: #909090 !important;
}
code span[style="color: #000000"] {
    color: #cccccc !important;
}
}

@-moz-document domain("php.net") {
    /* Get rid of some elements*/
     #usernotes {
         display: /*[[user-notes]]*/;
    }
}
