css:
@-moz-document url-prefix(https://twitter.com) {

body {
	font-family: "Google Sans", Arial, sans-serif;
}
body.no-nav-banners {
	background: #222 !important;
}

.global-nav-inner {
	background: #333;
}
.nav>li>a .text, .nav#global-actions>li .Icon {
	color: #ccc;
    transition: 0.2s;
}
.nav>li>a:hover .text, .nav#global-actions>li:hover .Icon {
	color: #1B95E0;
}

.RichEditor {
	border-radius: 20px;
}
.RichEditor.RichEditor--emojiPicker div[contenteditable], .RichEditor.RichEditor--emojiPicker div[contenteditable]:focus, .RichEditor.RichEditor--emojiPicker div[contenteditable].fake-focus {
	padding-left: 13px;
}

.top-timeline-tweetbox .timeline-tweet-box {
	border: none;
}
.top-timeline-tweetbox .tweet-user {
	border-radius: 10px !important;
}

.stream-item {
	border-radius: 10px;
    margin-top: 8px;
    box-shadow: 0 1px 6px rgba(0,0,0,0.1);
}
.tweet {
	border: none;
    border-radius: 10px;
    transition: 0.2s;
}
.AdaptiveMedia, .AdaptiveMedia-video, .AdaptiveMedia-videoContainer, .PlayableMedia-container {
	border-radius: 8px;
}
.Gallery.with-tweet .Gallery-media {
	border-radius: 10px;
}
.Gallery-content {
	border-radius: 10px !important;
}


.DashboardProfileCard {
	border-radius: 10px;
    border: none;
}
.module {
	border-radius: 10px;
}


.stream-item-header .account-group strong {
	transition: 0.1s;
}
.stream-item-header .account-group:hover strong {
	text-decoration: none !important;
}

.time a:hover span {
	text-decoration: none !important;
}


button {
	transition: 0.1s;
}
button:hover {
	text-decoration: none !important;
}
a {
	transition: 0.1s;
}
a:hover {
	text-decoration: none !important;
}
a:hover s, a:hover b, a:hover span {
	text-decoration: none !important;
    opacity: 0.8;
}
a strong {
	transition: 0.1s;
}
a:hover strong {
	text-decoration: none !important;
}
.u-linkComplex:hover .u-linkComplex-target, .u-linkComplex:focus .u-linkComplex-target, .u-linkComplex:active .u-linkComplex-target {
	text-decoration: none !important;
}


.RichEditor.is-fakeFocus {
	border-radius: 8px;
}


.TwitterCard-container {
	border-radius: 8px;
}

.QuoteTweet {
	border-radius: 8px;
}
.QuoteTweet .QuoteMedia {
	border-radius: 6px;
}


.dropdown-menu {
	border-radius: 10px !important;
}



.ProfileHeading-content {
	border-radius: 10px;
}

.new-tweets-bar {
	border-radius: 10px;
}

.ProfileHeaderCard-bio {
	color: #ccc;
}

.ProfileHeaderCard-nameLink {
	color: #ccc !important
}


.ProfileCard {
	border-radius: 10px;
}
.ProfileCard>a {
	border-radius: 9.5px 9.5px 0 0;
}

.WtfLargeCarouselStreamItem-title {
	color: #ddd;
}


.stream-end-inner {
	margin-top: 6px;
    background: #eee;
    border-radius: 10px;
}

.stream-end-item, .stream-end, .stream-loading, .stream-placeholder {
	border: none;
}


.ProfileListsTimeline .GridTimeline-end:not(.has-items) {
	border-radius: 10px;
}


.ThreadedConversation-tweet:before {
	height: 14px;
}
.ThreadedConversation-tweet:before, .ThreadedConversation-moreReplies:before, .ThreadedConversation-unavailableTweet:before, .ThreadedConversation--permalinkTweetWithAncestors:before {
	top: -10px;
}


.NotificationsHeadingContent {
	border-radius: 10px;
}
.content-header, .ActivityItem {
	border: none;
    border-radius: 10px;
    transition: 0.2s;
}

.js-nav-links li:first-child {
	border-radius: 10px 10px 0 0;
}
.js-nav-links li:last-child {
	border-radius: 0 0 10px 10px;
}

.content-main .content-header .header-inner {
	border-radius: 10px 10px 0 0;
}
.content-inner.no-stream-end {
	border-radius: 0 0 10px 10px;
}


.DMQuickReply-optionText {
	border-radius: 10px;
}


.permalink.self-thread-permalink-with-descendant .inline-reply-tweetbox {
	border-radius: 10px;
    border: none;
}


.permalink .stats .avatar-row a:first-child {
	border: none;
}


.Tombstone {
	border-radius: 8px;
}

}
