css:
@-moz-document domain("vivo.sx") {
#fb-root {
    display: none !important;
}
header {
    display: none !important;
}
footer {
    display: none !important;
}
#light-background {
    display: none !important;
}
.watch-footer {
    display: none !important;
}
.stream-share {
    display: none !important;
}
section.app_overview {
    background: #000;
}
div#alert-stream, div#alert-browser, div#alert-throttle {
    display: none !important;
}
.clearfix {
    display: none !important;
}
.vivo-content-box {
    border-radius: 5px;
    background: #000;
    color: #eee;
    padding: 0;
}
section.vivo-content.stream {
    padding-top: 0px;
    padding-bottom: 0px;
}
.vivo-content-box-wrapper .vivo-content-box h1 {
    color: #f60;
    font-size: 14px;
}
.vivo-content-box-wrapper .vivo-content-box-watch h1 strong {
    font-weight: 100;
    color: #f60;
    font-size: 14px;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    font-family: inherit;
    font-weight: 100;
    line-height: 1;
    color: inherit;
}
#_atssh {
    display: none !important;
}
.fp-filters {
    display: none !important;
}
.addthis-smartlayers, div#at4-follow, div#at4-share, div#at4-thankyou, div#at4-whatsnext {
    display: none !important;
}
.container {
    width: 90%;
}
.vivo-content-box-wrapper {
    padding-left: 0px;
    padding-right: 0px;
}
html {
    font-size: 62.5%;
    -webkit-tap-highlight-color: #555;
    background-color: #000;
}
/* pop up advertisment block - coming soon*/
 #AdWidgetContainer {
    display: none !important;
}
div[onclick*="("] {
    display: none !important;
}

}
