css:
@-moz-document domain("rolechat.org") {
/* 
	Vrsion: 1.1
*/

:root {
  --dark: #0b0b0b;
  --light-dark: #0e0e0e;
  --grey: #d9d9d9;
  --red: #ae2020;
  --blue: #008cba;
}

body {
  background-color: var(--dark);
  color: var(--grey) !important;
}

/* Navbar */
.navbar {
  background-color: var(--light-dark) !important;
  box-shadow: none !important;
}

.bg-light .dropdown-menu {
	background-color: var(--light-dark) !important;
  box-shadow: none !important;
  color: var(--grey) !important;
}

.bg-light .dropdown-menu .dropdown-item, .bg-light .dropdown-menu .dropdown-item:focus {
  color: var(--grey) !important;
}

.bg-light .dropdown-menu .dropdown-item.active, .bg-light .dropdown-menu .dropdown-item:hover, .bg-light .dropdown-menu .dropdown-item:focus {
 	background-color: var(--light-dark) !important;
  box-shadow: none !important;
  color: var(--grey) !important;
}

/* Footer */
.footer {
  background-color: var(--light-dark) !important;
}

/* Change text colour */
.navbar-light .navbar-text, .navbar-light .navbar-nav .nav-link, .option, .notificationchat, .connPartner {
  color: var(--grey) !important; 
}

/* Buttons/Inputs */
button, input, textarea {
  background-color: var(--light-dark) !important;
  border: 1px solid #171717 !important;
  color: var(--grey) !important; 
}

/* Main body */ 
#flowWrap {
 border: none!important;
}

#messagearea {
  background-color: var(--dark) !important;
  box-shadow: none !important;
	color: var(--grey) !important; 
}

.myname {
  color: var(--red) !important;
}

.btn-light:not(:disabled):not(.disabled):active:focus, .btn-light:not(:disabled):not(.disabled).active:focus, .show > .btn-light.dropdown-toggle:focus {
  box-shadow: 0 0 0 0.2rem var(--blue);
}

.partnername {
  color: var(--blue) !important;
}

/* Misc */
.badge .badge-light {
  background-color: var(--light-dark) !important;
	color: var(--grey) !important; 
}

.badge-light {
  background-color: var(--light-dark) !important;
	color: var(--grey) !important; 
}

/* Dropdown box stuff */
.dropdown-menu {
 	background-color: var(--light-dark) !important;
  box-shadow: none !important;
  color: var(--grey) !important;
}

.dropdown-item {
	color: var(--grey) !important;
}

.dropdown-item:hover, .dropdown-item:focus, .btn-light:focus, .btn-light.focus {
 	background-color: var(--light-dark) !important;
  box-shadow: 0 0 0 0.2rem var(--blue);
  color: var(--grey) !important;
}

/* Model box for export */
.modal-content {
  background-color: var(--light-dark) !important;
  box-shadow: none !important;
  color: var(--grey) !important;
}

.modal-body > pre {
  color: var(--grey) !important;
}

.alert-info, .alert-info > th, .alert-info > td, .alert-secondary, .alert-secondary a:not(.btn), .alert-secondary .alert-link, .alert-light, .alert-light a:not(.btn), .alert-light .alert-link, #tagInput {
  background-color: var(--dark) !important;
  border: 1px solid #171717 !important;
  color: var(--grey) !important; 
}
}
