css:
@-moz-document domain("chatango.com")
{
/*this style by kohiruimaki karen*/
/* ---------------------------- Chatango.com --------------------------------------*/
/*please don't re-mark my style */

body {
	background-color: none;
}
.ad_cell {
    display: none;
}
.logo_cell {
    display:none;
}
.group_title {
    color: #ff69b4;
    font-family: 'Roboto', sans-serif;
    font-size: 26px;
    background-color: #565665;
    text-shadow: 0px 0px 9px #ef97ee;
}
.bottombar, .logo_cell, #group_table > tbody > tr:nth-child(3) {
	display: display;
    color: #ff69b4 !important;
    font-family: 'Roboto', sans-serif;
    font-size: 10px;
    background-color: #565665;
}

.msg-date {
	display: inline-block;
    float: right;
    word-spacing: 5px; 
    
    
}

.msg .msg-fg .user-thumb {
    margin-top: -0.3em;
	margin-bottom: -0.3em;
    display: inline-block;
 	width:50px;
    height: 50px;
  border-radius: 50%;

  background-repeat: no-repeat;
  background-position: center center;
  background-size: cover;


}

#CGW, #IW .msg-fg #input-field {
	background-color: #16161D;
	border: 1px solid #565665;
    background-image: url(https://wallpapers.walldevil.com/wallpapers/a59/preview/kotori-itsuka---date-a-live.jpg) !important;
    opacity: 2.0;
  background-size: cover !important;
  background-attachment: fixed !important;
  background-position: center center !important;
}

#IW .msg-fg #input-field {
	border: 1px solid #565665;
	color: #565665;
}

.msg .msg-fg, .msg .msg-fg * {
	color: #ff69b4 !important;
    font-family: 'Roboto', sans-serif;
    font-size: 100% !important;
    font-weight: bold !important;
    font-style: normal !important;
}

.msg .msg-fg span:nth-child(1) {
	text-shadow: 0px 0px 9px #92929C;
	color: #ef97ee !important;
}

.msg {
	border-bottom: 1px solid #565665 !important;
}

.ubdr {
	border: 1px solid #565665 !important;
}

#FTR_LOGO > div {
    color: #565665 !important;
}


.overflow .msg:last-child {
  border-bottom: none !important;
}
.pb_fg {
    background-color: #f07ee5;
    height: 100%;
    border-bottom-left-radius: 0 !important;
    border-bottom-right-radius: 0 !important;
}
}
