css:
#toolbar-menubar {border: none !important;} #toolbar-menubar,
#tools-menu,.menubar-text { color: #fff !important; font-weight: bold !important; text-shadow: none !important;}
#personal-bookmarks .bookmark-item .toolbarbutton-text {color: #fff !important;font-weight: bold !important; text-shadow: none !important;}

#PersonalToolbar, #navigator-toolbox, #nav-bar,toolbox,.tabbrowser-tabs,
#toolbar-menubar,#status-bar {-moz-appearance: none !important; -moz-border-radius: 0px !important; height: 32px !important; 
max-height: 32px !important; -moz-appearance: none !important; border: 0px solid !important; margin: 0px 0px 0px 0px !important;
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAAjCAIAAAAxJEzgAAAACXBIWXMAAAsTAAALEwEAmpwYAAAASklEQVR4nI3NMQ6AMAxD0dfS+5+JI7GVlgGGwBAhJLx82VZiQFkDJ2gblXYEBoU2A3vCSPjR5bCnLi/0WJ8fBzW9vt07nFQWDI8uvGogd5omHS0AAAAASUVORK5CYII=") repeat center center !important;color: #fff !important;font-weight: bold !important; text-shadow: none !important;}

.tabbrowser-tabs { margin-top:-2px !important; margin-bottom:-3px !important; padding:0px !important;}
.tabbrowser-tabs .scrollbox-innerbox {-moz-box-pack: center !important;}

.tabbrowser-tab[selected="true"]{ max-height: 25px !important; padding-top: 0px !important;
 margin: 2px 5px 0px 0px !important; height: 30px !important;-moz-appearance: none !important; 
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAIAAABi9+OQAAAACXBIWXMAAAsTAAALEwEAmpwYAAAASklEQVR4nAXBCQHDMAwEQXcphk/BhZV1jzJznv+LdVmLX8pGxIN1SS89xVucIRGxWYtjc+6wGrZiY1rhFYpQzXroDCdmtyQmNmk/7IxSqqlLIncAAAAASUVORK5CYII=") !important; 
color: #fff !important; border-top: 0px  !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important; }

.tabbrowser-tab[selected="false"]{ text-align: center !important; height: 34px !important; padding: 0px 0px 0px 0px !important; 
margin: 2px 5px 0px 0px !important; height: 34px !important; -moz-appearance: none !important; 
background: #0B4B79 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAaCAIAAAD5ZqGGAAAACXBIWXMAAAsSAAALEgHS3X78AAAAKUlEQVQIW2OYO3cu058/f5h+/frF9Pv3bzAGsUH458+fKPSPHz8YyAQAlpAiKDKcgAsAAAAASUVORK5CYII=") repeat-x center !important; color: #fff !important;
font-weight: bold !important; border-top: 0px !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important;}

.tabbrowser-tab[selected="false"] .tab-text { color: #fff !important; }
.tabbrowser-tab[selected="false"]:hover .tab-text { color: gold !important;} 

.tabbrowser-tab[selected="true"]:hover .tab-text {color:  #7fff00 !important;}
.tabbrowser-tab[selected="true"], .tabbrowser-tab:not([selected="true"] ) { font-weight: bolder !important;}

.searchbar-engine-button,.searchbar-engine-button, #searchbar .textbox-input-box, #urlbar > .autocomplete-textbox-container, 
#urlbar > .autocomplete-history-dropmarker {background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAaCAIAAAD5ZqGGAAAACXBIWXMAAAsSAAALEgHS3X78AAAAKUlEQVQIW2OYO3cu058/f5h+/frF9Pv3bzAGsUH458+fKPSPHz8YyAQAlpAiKDKcgAsAAAAASUVORK5CYII=") !important; color: #fff !important;} 

#identity-box{background-color: transparent !important;border:none!important; margin: 0px -5px 0px -4px!important;}
#identity-box > hbox {background-image: none !important; background-color: transparent !important;
border-left-width: 0px !important; border-right-width: 0px !important;}

#nav-bar { border: none !important; padding: 0px !important; margin: 0px !important; margin-bottom: 0px !important;}
