css:
@-moz-document domain(liveleak.com)
{

/* ---------- Main Page ---------- */


.cc-banner.cc-bottom {
    display: none !important; }

.tab_heading_outer li a, .items_main_outer li a, .item_info_con li a {
    background: #505050 !important; }
.tab_heading_outer li a:hover {
    color: #c20a10 !important; }
a.tab_heading_active, .items_main_outer li a, .item_info_con li a {
    color: white !important; }

.search_bar_outer input[type="text"], .navbar-fixed-top form, .channel_heading_inner input[type="text"], .channel_heading_inner form {
    color: white !important;
    background: #303030 !important; }
.search_bar_outer input[type="text"]::placeholder {
    color: white !important; }

.search_bar_outer input[type="submit"], .channel_heading_inner input[type="button"] {
    background: url(data:image/png;base64,)center top no-repeat !important; }

body, .content_main_outer, .navbar-fixed-top, footer, .facebook_leak_outer, .advertise_main_outer, .msg {
    background: #101010 !important; }

.advertise_main_outer a {
    color: #CCC !important; }

.nav_main_outer {
    background: #202020 !important; }
.nav_main li:last-child a {
    background: #303030 !important; }

.nav_main li a:hover {
    text-decoration: underline !important;
    color: red !important; }

.content_main_inner {
    background: #202020 !important; }

.featured_items_right h3 a {
    color: #c20a10 !important; }
.featured_items_right h3 a:hover, .featured_items_right h3 a:visited:hover  {
    color: #CCC !important; }
.featured_items_right h3 a:visited {
    color: #707070 !important; }

.footer_nav_con li a, .copyright_con, .live_leak_right_con p a, .featured_items_right p, .msg {
    color: #CCC !important; }

.featured_text_con {
    color: #CCC !important; }
.featured_text_con a {
    color: white !important; }
.featured_text_con a:hover {
    text-decoration: underline !important; }

.subscribe_left, .subscribe_right, .liveleakers_community_text p {
    color: #CCC !important; }
.subscribe_right a {
    color: white !important; }

.liveleakers_community_outer {
    border-color: #050505 !important; }

.pagination a, .pagination_con .pagination > li:first-child > a, .pagination_con .pagination > li:last-child > a {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background: #404040 !important; }
.pagination a:hover, .pagination_con .pagination > li:first-child > a:hover,  .pagination_con .pagination > li:last-child > a:hover {
    color: white !important;
    background: #606060 !important; }


/* ---------- On Videos ---------- */


.channel_videos_main_outer {
    background: #303030 !important;
    border: 1px solid #050505 !important;
    box-shadow: none !important; }

.channel_video_heading h3 {
    font-weight: bold !important;
    color: #c20a10 !important; }

.step_outer {
    color: #CCC !important; }
.sorting_out select {
    background-color: #353535 !important;
    color: #CCC !important; }

.step_outer .red_button, .item_info_con li a, #home-tab {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background-color: #505050 !important; }
.step_outer .red_button:hover, .item_info_con li a:hover {
    color: white !important;
    background-color: #707070 !important; }

.item_info_con .tab-content p strong, .item_info_con .tab-content p {
    color: #CCC !important; }
.tab-content > .active a {
    color: white !important; }

.item_info_con .tab-content {
    border: 1px solid #050505 !important;
    background: #303030 !important; }

.content {
    border: 1px solid #050505 !important;
    background: #303030 !important; }
.current_events_right h5 a {
    color: #CCC !important; }
.current_events_right h5 a:hover {
    color: white !important; }

.liveleak_editor_outer textarea {
    color: white !important;
    background: #303030 !important; }
.editor_outer {
    background: #353535 !important; }
.editor_outer li a {
    color: white !important; }

.col-xs-11 {
    border: 1px solid #050505 !important;
    background: #353535 !important; }
.col-xs-11 a {
    text-decoration: underline !important;
    color: white !important; }
.replies_user_outer p {
    color: #CCC !important; }

i.fa.plus_minus.fa-minus-square-o, i.fa.plus_minus.fa-plus-square-o {
    color: white !important; }

.content_inner_page_left_outer strong {
    color: #CCC !important; }


/* ---------- User Page ---------- */

.featured_items_outer, .liveleakers_community_outer, .featured_item_main_outer h2, .featured_con, .new_liveleak_channel_outer, .featured_item_main_outer {
    border-color: #050505 !important;
    background: #202020 !important; }

.liveleakers_community_outer h3 {
    color: #CCC !important; }


/* ---------- Settings Page ---------- */


.table1 tr:nth-child(2n) td, .table2 tr:nth-child(2n) td, .table3 tr:nth-child(2n) td {
    background: #202020 !important; }
.table1 td, .table2 td, .table3 td, .table1 tr:first-child td {
    color: #CCC !important;
    background: #303030 !important; }

.table1 select, .table1 input {
    border: none !important;
    background: #404040 !important; }

.table1 td, .table2 td, .table3 td, .table1 tr:first-child td {
    border: 1px solid #050505 !important; }

.liveleak_guest_dropdown, .dropdown dd li a, .dropdown dd ul {
    background: #505050 !important; }
.liveleak_guest_dropdown a, .dropdown dt span, .dropdown dd li a {
    color: #CCC !important; }

.liveleak_guest_dropdown a:hover {
    color: white !important; }
.dropdown dd li a:hover {
    color: black !important;
    background: #DDD !important; }


/* ---------- Upload Page ---------- */

.create_account_inner {
    box-shadow: none !important; }

.create_form_main input[type="text"], .create_form_main input[type="text"], .create_form_main input[type="password"] {
    border-color: #050505 !important;
    background: #505050  !important; }
.create_form_main input[type="text"]::placeholder, .create_form_main textarea::placeholder {
    color: white !important; }

.create_form_main select {
    background-color: #202020 !important; }

.create_form_main input[type="button"] {
    color: #CCC !important;
    background: #505050 !important; }
.create_form_main input[type="button"]:hover {
    color: white !important;
    background: #707070 !important; }

.input-group .form-control, .create_account_inner a {
    color: #CCC !important; }


/* ---------- FAQ Page ---------- */

ul#faq, ul#faq p {
    color: #CCC !important; }
ul#faq h4 {
    color: white !important; }

ul#j_faq a {
    color: #CCC !important; }
ul#j_faq a:hover{
    color: white !important; }


/* ---------- Contact Us Page, Privacy Policy Page ---------- */

textarea {
    border-color: #050505 !important;
    background: #505050 !important; }

table tbody tr td p a {
    text-decoration: underline !important;
    color: white !important; }

table tbody ol {
    color: #CCC !important; }



table tbody h3, table tbody strong {
    color: white !important; }
table tbody p {
    color: #CCC !important; }
  
}
