css:
@-moz-document regexp("https*://www.google..*") {

    /* COLORS */

    /** DESCRIPTION : set the colors **/
	:root {
	    --body-bg: #2F2E2E !important;
		--card-bg: #333 !important;
		--card-bg-2: #515151 !important;
		--border-color: #0F0C11 !important;
		--primary-color: #FFF !important;
		--secondary-color: #BEBEBE !important;
		--invert: invert(1) !important;
	}

	/* GENERAL */

	/** DESCRIPTION : change the elements of all page **/
	* {
		font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
	}

	*::-moz-focus-inner {
		border: 0;
	}

	select {
		-webkit-appearance: none;
		-moz-appearance: none;
		text-indent: 0px;
		text-overflow: ellipsis;
		white-space: nowrap;
	}

	input::-moz-placeholder {
		color: var(--primary-color);
		font-size: 14pt !important;
	}

	body {
		background-color: var(--body-bg) !important;
		overflow-x: hidden;
	}

	.hdtbItm label:hover,
	.hdtbItm a:hover,
	#hdtb-more-mn a:hover,
	#hdtb .hdtb-mitem a:hover,
	.hdtb-mn-hd:hover,
	#hdtb-more:hover,
	#hdtb-tls:hover {
		color: var(--primary-color);
	}

	.mw #rcnt .col #center_col #res #search *:not(.g) {
		border: none;
	}

	.mnr-c * {
		color: var(--primary-color);
		font-size: 14pt;
	}

	a:link {
		color: #436EBC;
	}

	.mnr-c #ni,
	.fKDtNb {
		color: #436EBC !important;
	}

	.action-menu.ab_ctl,
	.ads-ad,
	.a-no-hover-decoration.irc-nic.isr-rtc /*** IMAGE SEARCH DETAIL ***/,
	.AEprdc.vk_c /*** MAP CARD ***/,
	table a[href*="temp"],
	div > a[href*="support.google.com"] /*** SUPPORT LINK ***/,
	div > a[href*="disclaimer"] /*** DISCLAMER LINK ***/,
	#abar_button_opt /*** SETTINGS LINK ***/,
	#ab_ctls /*** SAVE SEARCH LINK ***/,
	a[class*="duf"],
	div[id*="eobm"] /*** SUGGEST SEARCH ***/,
	div[id*="ed_"],
	.EVSaRd /*** YOUR PHOTOS ***/,
	#fbarcnt /*** FOOTER ***/,
	#foot .nwd,
	.gko-a.ksb.gko-add /*** SHOPPING SHORTLIST BUTTON ***/,
	.gl /*** REPORT IMAGE ***/,
	g-more-link /*** MORE LINK ***/,
	.hdtb-dd-b /*** NAV MORE LINK ***/,
	.irc_ft /*** SEND HELP ***/,
	.irc_but_r /*** IMAGE SEARCH BUTTONS ***/,
	.kno-ftr /*** FEEDBACK ***/,
	kno-share-button /*** SHARE BUTTON ***/,
	.knowledge-panel g-scrolling-carousel /*** SCROLL CARD SIDEBAR ***/,
	.knowledge-panel .thumb span /*** MORE IMAGE BUTTON ***/,
	.knowledge-panel a .rhsg3 /*** VIEW MORE LINK ***/,
	.korbXb /*** MORE IMAGE ***/,
	#media_result_group .fl /*** MORE IMAGE BUTTON ***/,
	.mw #rcnt .col #center_col #res #search #ires #rso .g .card-sction /*** OTHERS LINKS ***/,
	.nrgt .mrf .fl /*** MORE FROM LINK ***/,
	.N6pxec /*** NEWS ALERT FOOTER ***/,
	.pddsle.ellip,
	#rhs .commercial-unit-desktop-rhs /*** COMMERCIAL ADS ***/,
	#rso div .sh-sr__shop-result-group,
	#search #ires #rso .g .ts > .card-section /*** OTHERS NEWS LINKS ***/,
	.sh-dr__restricts /*** SHOPPING SIDEBAR ***/,
	.sh-dtt__top-tools /*** SHOPPING HEADER ***/,
	.shpd /*** SHOPPING FOOTER MESSAGE ***/,
	.sh-sp__line /*** SHOPPING LINE ***/,
	.std.stp.card-section /*** TIPS ***/,
	#topstuff .sh-sp__pswtr,
	#tvcap /*** ADS ***/,
	#tw-gtlink /*** OPEN IN GOOGLE TRADUCTION LINK ***/,
	.vk_ard /*** SIDEBAR MORE ICON ***/,
	.vk_aru/*** SIDEBAR LESS ICON ***/ {
		display: none !important;
	}

	/* HEADER */

	/** DESCRIPTION : change the elements of the header **/
	.sfbgg {

		/*** HEADER BACKGROUND ***/
		background: no-repeat url("https://i.imgur.com/R2jNAKV.jpg") center !important;
		background-size: cover !important;
		height: 84px !important;
	}

	.sfbgx {
		background: transparent !important;
	}

	.sfbgg::before {

		/*** HEADER BORDER TOP ***/
		background-image: linear-gradient(to right, #73D2F6, #3369E8 22%, #EF5350 36%, #D50F25 48%, #FFE082 63%, #EEB211 76%, #009925) !important;
		color: transparent !important;
		content: "dd" !important;
		height: 3px !important;
		width: 100% !important;
		position: absolute;
		top: 0px !important;
	}

	#logocont,
	.logo {

		/*** HEADER LOGO ***/
		background: no-repeat url("https://i.imgur.com/IqDKVDh.png") 55% 51% !important;
		background-color: var(--card-bg-2) !important;
		background-size: 25px auto !important;
		border-top-left-radius: 2px !important;
		border-bottom-left-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 44px 0px 0px 44px !important;
		z-index: 999 !important;
	}

	#logocont {
		margin-top: 3px !important;
		margin-left: 21px !important;
	}

	.logo {
		margin-top: -4px !important;
		margin-left: 21px !important;
	}

	#logocont img,
	.logo img {
		display: none !important;
	}

	#gsr #searchform #tsf .tsf-p {

		/*** HEADER SEARCH BAR ***/
		padding-left: 64px !important;
	}

	#searchform {
		position: absolute !important;
	}

	.sbct {
		background: var(--card-bg) !important;
	}

	#searchform .sbib_a {
		background: var(--card-bg) !important;
	}

	.sbibod {
		background: var(--card-bg) !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
	}

	#lst-ib {
		background: var(--card-bg) !important;
		color: var(--primary-color);
		font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
		font-size: 15pt !important;
		font-weight: normal !important;
	}

	.RNNXgb {
		background: var(--card-bg);
		border: none !important;
		border-top-right-radius: 2px !important;
		border-top-left-radius: 0px !important;
		border-bottom-right-radius: 2px !important;
		border-bottom-left-radius: 0px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		position: relative;
		left: -86px !important;
		z-index: 1;
	}

	.gLFyf.gsfi {
		color: var(--primary-color);
		font-size: 15pt;
		margin-left: -5px;
	}

	.sbdd_b {

		/*** HEADER SEARCH BAR RESULTS BOX ***/
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
	}

	.sbct:first-child .suggestions-inner-container {
		border: none;
	}

	.sbdd_b .sbsb_a,
	.aajZCb {
		background: var(--card-bg);
		border: none !important;
		border-radius: 1px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 20px 0px 25px 0px !important;
		margin-top: 10px !important;
	}

	.aajZCb {
		margin-left: -85px !important;
	}

	.sbdd_b .sbsb_a * {
		color: var(--primary-color);
		font-size: 14pt !important;
		font-weight: normal !important;
		line-height: 20pt !important;
	}

	.sbsb_d,
	.sbsb_c.gsfs.sbsb_d:hover {
		background: transparent !important;
		opacity: .8 !important;
	}

	#sbsb_f,
	#sbfblt {
		display: none !important;
	}

	.aajZCb * {
		color: var(--primary-color);
		font-size: 14pt;
		font-weight: normal !important;
	}

	.sbct:first-child .sbtc {
		border: none;
	}

	.aajZCb .sbtc {
		padding: 5px 15px !important;
	}

	.gscp_a {

		/*** HEADER SEARCH BAR PICTURE ***/
		background: transparent;
		border: none !important;
		margin-top: 2px !important;
		margin-left: 7px !important;
	}

	.gscp_a * {
		color: var(--primary-color);
	}

	.gscp_d {
		top: -1px !important;
	}

	#qbi {

		/*** HEADER SEARCH BAR PICTURE SEARCH BUTTON ***/
		background: no-repeat url("https://i.imgur.com/NgGyw86.png") center !important;
		background-size: 21px auto !important;
		margin-top: -2px !important;
		margin-right: -8px !important;
		opacity: 1 !important;
	}

	#qbp {

		/*** HEADER SEARCH BAR PICTURE SEARCH CONTAINER ***/
		background: var(--card-bg);
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		height: 187px !important;
		position: fixed !important;
		top: 0px !important;
		right: 0px !important;
		bottom: 0px !important;
		left: 0px !important;
		margin: auto !important;
	}

	.qbtt {

		/*** HEADER SEARCH BAR PICTURE SEARCH CONTAINER HEADER ***/
		color: var(--primary-color);
		font-size: 16pt !important;
		font-weight: normal !important;
	}

	#qbdt {

		/*** HEADER SEARCH BAR PICTURE SEARCH CONTAINER TEXT ***/
		color: var(--primary-color);
		font-size: 14pt !important;
	}

	.qbtbha.sl {

		/*** HEADER SEARCH BAR PICTURE SEARCH CONTAINER TABS ***/
		background: transparent !important;
		border: none !important;
		color: var(--primary-color) !important;
	}

	.qbtbha.sl * {
		color: var(--primary-color) !important;
	}

	#qbuti,
	#qbfti {
		display: none !important;
	}

	#qbx {

		/*** HEADER SEARCH BAR PICTURE SEARCH CONTAINER CLOSE BUTTON ***/
		background: no-repeat url('https://imgur.com/4GlrMN7.png') center !important;
		background-size: cover !important;
		height: 25px !important;
		width: 25px !important;
		filter: brightness(0) var(--invert) !important;
	}

	.qbtbp {

		/*** HEADER SEARCH BAR PICTURE SEARCH CONTAINER FOOTER ***/
		background: var(--card-bg-2) !important;
		border-top: none !important;
	}

	.qbwr .qbtbha,
	.qbwr .qbtbha * {
		font-family: "Roboto Condensed" !important;
		font-size: 14pt !important;
		font-weight: normal !important;
	}

	#qbui {
		background: var(--card-bg);
		border: none;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		border-radius: 2px !important;
		color: var(--primary-color);
		font-size: 14pt !important;
		font-family: "Roboto Condensed" !important;
		padding-left: 9px !important;
	}

	#qbbtc .gbqfb {
		font-family: "Roboto Condensed" !important;
		font-size: 14pt !important;
		font-weight: normal !important;
		width: 150px !important;
	}

	#qbfile {
		font-family: "Roboto Condensed" !important;
		font-size: 14pt !important;
	}

	#top_nav {

		/*** HEADER NAVIGATION BAR ***/
		background: transparent !important;
	}

	#hdtb {
		background: transparent !important;
		border-bottom: 1px dotted var(--border-color) !important;
	}

	#hdtb-msb {
		margin-left: -90px !important;
	}

	#hdtbSum {
		background: var(--card-bg) !important;
		border-top: none !important;
		font-size: 14pt !important;
		padding-bottom: 10px !important;
		margin-top: 15px !important;
	}

	.hdtb-mitem.hdtb-msel.hdtb-imb {
		border: none !important;
		font-weight: normal !important;
	}

	#hdtb #hdtbMenus {
		background: var(--card-bg);
		font-size: 14pt !important;
		margin-top: -8px !important;
		padding-bottom: 8px !important;
	}

	#hdtb-tls:active,
	#hdtb-tls:focus,
	#hdtb-tls:hover,
	#hdtb-msb .hdtb-tl-sel {
		background: transparent !important;
		border: none !important;
		box-shadow: none !important;
		color: var(--secondary-color) !important;
	}

	.hdtbU,
	.hdtb-mn-c {
		background: var(--card-bg) !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .4) !important;
	}

	.hdtbU li,
	.hdtbItm a:hover,
	#hdtb-more-mn a:hover,
	.hdtbItm.hdtbSel:hover,
	.hdtbItm a:hover,
	#cdrlnk:hover {
		background: var(--card-bg) !important;
		color: var(--secondary-color) !important;
	}

	.cdr_sep {
		border-top: 1px dotted var(--border-color) !important;
	}

	#hdtb .mn-dwn-arw {
		background: no-repeat  url('https://i.imgur.com/OygFMxF.png') center !important;
		background-size: cover !important;
		border: none !important;
		padding: 13px 0px 0px 13px !important;
		margin-top: -5px !important;
		margin-left: 2px !important;
	}

	#gbw a[title*="apps"] {

		/*** HEADER APP ICON ***/
		background: no-repeat url("https://i.imgur.com/fz6600P.png") center !important;
		background-size: 15px auto !important;
		filter: invert(1) !important;
	}

	.gb_tb,
	.gb_sb {
		border-bottom-color: var(--card-bg);
	}

	.gb_aa,
	.gb_da {
		background: var(--card-bg);
		border: none !important;
	}

	.gb_T *,
	.gb_X {
		background-color: transparent !important;
		color: var(--primary-color);
		font-size: 14pt;
		line-height: 17pt;
	}

	.gb_T:hover a {
		border-color: transparent;
	}

	.gb_ea {
		background: var(--card-bg-2);
		color: var(--primary-color) !important;
		font-size: 14pt;
	}

	.gb_fa {
		color: var(--primary-color) !important;
		font-size: 14pt;
	}

	.gb_fa {
		border-bottom: 1px dotted var(--border-color);
	}

	.gb_Nb,
	.gb_Hb,
	.gb_Ib,
	.gb_Ob,
	.gb_Qb {
		background: var(--card-bg);
		border-top-color: var(--border-color);
	}

	.gb_Pb:hover,
	.gb_Qb:hover {
		background: var(--card-bg-2);
	}

	.gb_zb *,
	.gb_Rb *,
	.gb_Qb *,
	.gb_Ab * {
		color: var(--primary-color);
		font-size: 14pt;
	}

	.gb_zb .gb_Fb {
		border: none;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
		padding-bottom: 4px;
	}

	.gb_wa,
	.gb_xa,
	.gb_za,
	.gb_Aa {
		background: #436EBC !important;
		border: none;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		color: #FFF !important;
		font-size: 14pt;
		padding: 4px 10px 6px 10px !important;
	}

	.gb_lb {
		font-size: 14pt !important;
		padding-bottom: 15px !important;
	}

	.gb_qa {
		background: var(--card-bg-2) !important;
	}

	.gb_qa * {
		color: var(--primary-color);
		font-size: 14pt;
		line-height: 20pt;
	}

	.gb_Bb .gb_mb {
		visibility: hidden;
	}

	#gbw a[title*="notifications"] div {

		/*** HEADER NOTIFICATION ICON ***/
		background: no-repeat url("https://i.imgur.com/aqaUYbG.png") center !important;
		background-size: 22px auto !important;
		margin-left: 0px !important;
	}

	/* SEARCH OTHERS HEADER */

	/** DESCRIPTION : change the lements of the search others header **/
	.kappbar,
	.rl_center,
	.rl_feature,
	.rlc__slider-page {
		background: transparent !important;
		border: none !important;
		box-shadow: none !important;
		margin-top: 0px !important;
	}

	.kappbar g-scrolling-carousel {
		box-shadow: none !important;
	}

	.klbar {
		border: none !important;
		box-shadow: none !important;
		padding-bottom: 15px !important;
	}

	.mlo-c {
		box-shadow: none !important;
	}

	.abup * {
		color: var(--primary-color);
		font-size: 15pt !important;
	}

	.klitem,
	.rl_item.rl_item_base {
		background: var(--card-bg-2) !important;
	}

	.mw #rcnt .col #rhs .knowledge-panel .rl_item {
		background: transparent !important;
	}

	.kltat,
	.appbar .rl_item_base .title {
		color: var(--primary-color);
		font-size: 14pt !important;
	}

	.klmeta,
	.appbar .rl_item_base span {
		color: var(--secondary-color);
		font-size: 12pt !important;
		margin-top: 3px !important;
	}

	.klitem.selected {
		border-bottom: 4px solid #FBBC05;
	}

	/* SEARCH MAIN RESULTS */

	/** DESCRIPTION : change the elements of the search main results page **/
	#main #cnt #appbar #extabar {

		/*** SEARCH MAIN RESULTS TOP BAR RESULTS CONTAINER ***/
		background: var(--card-bg);
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 2px 0px 5px 0px !important;
	}

	#main #cnt #appbar #extabar #topabar #slim_appbar {

		/*** SEARCH MAIN RESULTS TOP BAR RESULTS COUNTER ***/
		font-size: 14pt !important;
		margin-left: 62px !important;
	}

	#main #cnt #appbar #extabar #topabar #slim_appbar * {
		color: var(--primary-color) !important;
		transition: none !important;
	}

	#main #cnt #appbar #extabar #topabar #slim_appbar #resultStats::before {
		background: no-repeat url("https://i.imgur.com/i7wuH7j.png") center !important;
		background-size: cover !important;
		content: "dd" !important;
		color: transparent;
		margin-right: 10px !important;
	}

	.mw #rcnt .col #center_col {

		/*** SEARCH MAIN RESULTS LEFT COLUMN ***/
		background: var(--card-bg) !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
		padding: 15px 15px 10px 10px !important;
		margin-top: 30px;
		margin-left: 80px;
	}

	#fprs {

		/*** SEARCH MAIN RESULTS LEFT COLUMN AUTOCORRECT ***/
		display: block !important;
	}

	#fprs a,
	.ssp a,
	.spell a,
	.spell_orig a {
		color: #436EBC !important;
	}

	.spell,
	.gL9Hy,
	.ssp * {
		color: var(--primary-color) !important;
		font-size: 17pt !important;
	}

	.spell_orig {
		color: var(--primary-color);
		font-size: 16pt !important;
		text-transform: lowercase !important;
	}

	.ts.std {

		/*** SEARCH MAIN RESULTS LEFT COLUMN ARTICLES ***/
		font-size: 14pt !important;
	}

	.mw #rcnt .col #center_col #res #search .g {

		/*** SEARCH MAIN RESULTS LEFT COLUMN CARD RESULT ***/
		border-bottom: 1px dotted var(--border-color);
		padding-bottom: 25px !important;
		margin-bottom: 20px;
	}

	.mw #rcnt .col #center_col #res #search #imagebox_bigimages {

		/*** SEARCH MAIN RESULTS LEFT COLUMN CARD RESULT IMAGE CONTAINER ***/
		padding-bottom: 0px !important;
		margin-top: 0px !important;
	}

	.mw #rcnt .col #center_col #res #search #imagebox_bigimages g-section-with-header {
		display: block !important;
	}

	.mw #rcnt .col #center_col #res #search #imagebox_bigimages h3 a {

		/*** SEARCH MAIN RESULTS LEFT COLUMN CARD RESULT IMAGE CONTAINER TITLE ***/
		color: #436EBC !important;
		font-size: 16pt !important;
	}

	.mw #rcnt .col #center_col #res #search #imagebox_bigimages img {
		border-radius: 2px !important;
	}

	.mw #rcnt .col #center_col .rc h3,
	.mw #rcnt .col #center_col #res #search #ires #rso .g h3,
	.mw #rcnt .col #center_col #res #search #ires #rso .g h3 *,
	.mw #rcnt .col #center_col #res #search #ires #rso .g .xsm {

		/*** SEARCH MAIN RESULTS LEFT COLUMN TITLE ***/
		font-size: 16pt !important;
		font-weight: normal !important;
		margin-bottom: 10px !important;
	}

	.mw #rcnt .col #center_col .rc h3,
	.mw #rcnt .col #center_col #res #search #ires #rso .g h3 a,
	.mw #rcnt .col #center_col #res #search #ires #rso .g h3 .b,
	.mw #rcnt .col #center_col #res #search #ires #rso .g .xsm {
		color: #436EBC !important;
	}

	.mw #rcnt .col #center_col .rc .s .f,
	.mw #rcnt .col #center_col #res #search #ires #rso .g .slp {

		/*** SEARCH MAIN RESULTS LEFT COLUMN LINK CONTAINER ***/
		margin-bottom: 10px !important;
	}

	.mw #rcnt .col #center_col .rc cite,
	.mw #rcnt .col #center_col #res #search #ires #rso .g .slp > span:nth-of-type(1) {

		/*** SEARCH MAIN RESULTS LEFT COLUMN LINK ***/
		color: #1E8945 !important;
		font-size: 14pt !important;
		line-height: 20pt;
	}

	.mw #rcnt .col #center_col .rc .r .fl {
