css:
@-moz-document url-prefix("https://ore.spongepowered.org") {
    body {
        background-color: #454545;
        color: #CCCCCC;
    }
    pre {
        background-color: #454545;
        border: 1px solid #333;
    }
    .panel > .panel-pagination {
        border-top: none;
    }
    .project-header-container {
        margin-top: 60px;
        margin-bottom: 15px;
        border-bottom: 1px solid #404040;
    }
    .page-rendered,
    .panel {
        background-color: #555;
        border: 1px solid #404040;
    }
    .page-rendered table tr > td {
        border-color: #333;
    }
    .panel-footer {
        background-color: #555;
        border-top: 1px solid #454545;
    }
    .panel.panel-default {
        border: 1px solid #454545;
    }
    .list-group-item {
        border: 1px solid #404040;
    }
    .setting,
    .modal-header,
    .user-header,
    .page-rendered h1,
    .page-rendered h2    {
        border-bottom: 1px solid #404040;
    }

    /* Buttons */
    button.open:hover {
        background-color: #4b4b4b;
    }
    .btn-default {
        border-color: #454545 !important;
        color: #f2f2f2;
        background: #555;
    }
    .btn-default:hover {
        background: #4b4b4b;
        color: #f2f2f2;
    }
    .btn-primary {
        color: black!important;
    }

    /* Links */
    a:visited {
        color: #be9800;
    }
    a:hover {
        color: #FFDE58;
        text-decoration: underline;
    }
    a {
        color: #FFDE58;
        text-decoration: none;
        cursor: pointer;
    }
    
    /* Project Creation */
    .project-create-steps {
        background: #555;
        border: 1px solid #454545;
    }
    .project-create-steps .step-active {
        background: #494949;
        border-bottom: 1px solid #F2F2F2!important;
        color: #F2F2F2;
    }
    .project-create-steps .project-create-step:not(:last-child) {
        border-bottom: 1px solid #494949;
    }

    /* Headings will be white */
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    #new-page #new-page-label {
        color: #fff;
    }

    /* Codeblocks should be the same as the body background */
    .page-rendered code {
        background-color: #454545;
        color: #fff;
    }

    /* navbar buttons */
    .navbar-default .navbar-nav>li>a {
        color: #d9d9d9;
    }
    .navbar-default .navbar-nav>li>a:hover {
        color: #fff;
    }
    .project-navbar,
    .project-navbar:hover,
    .project-navbar>.navbar-inner>.navbar-nav>.active>a {
        color: #e6e6e6;
    }
    .project-navbar>.navbar-inner li.active>a {
        border-bottom: 1px solid #777;
    }
    .minor,
    .setting {
        color: #d9d9d9;
    }
    .list-group-item {
        background-color: #555;
    }
    .tags [style*="#FFDAB9"] {
        background: red!important;
        border-color: red!important;
    }
    
    /* Tables */
    table {
        border: 1px solid #555!important;
    }
    .page-rendered table tr > td,
    .page-rendered .page-rendered table tr > th {
        background: #454545;
    }
    .table>thead>tr>th,
    .table>tbody>tr>th,
    .table>tfoot>tr>th,
    .table>thead>tr>td,
    .table>tbody>tr>td,
    .table>tfoot>tr>td {
        border-top: 1px solid #454545
    }
    
    /* Forms */
    select.form-control:focus,
    input.form-control:focus {
        border: 1px solid #454545;
    }
    select.form-control,
    input.form-control {
        background: #555;
        color: #f2f2f2;
        border-color: #454545;
    }
    .form-control[disabled],
    .form-control[readonly],
    fieldset[disabled] .form-control {
        background-color: #3a3a3a;
    }

    /* dropdown */
    #sp-logo-dropdown,
    .dropdown-menu {
        background-color: #555;
        border: 1px solid #454545;
    }
    #sp-logo-dropdown a,
    .dropdown-menu>li>a {
        color: #d9d9d9;
    }
    #sp-logo-dropdown li.active a {
        color: #555;
    }
    .dropdown-menu > li > a:hover,
    .dropdown-menu > li > a:focus {
        background: #454545;
        color: #F2F2F2;
    }
    .dropdown-menu .divider {
        background: #454545;
    }

    /* Main page - plugin list */
    .project-list .title {
        color: #FFF;
    }
    .tags .tag {
        background: #454545;
        border: 1px solid #555;
    }

    /* Recommended project */
    .project-list .info span.recommended-version a {
        color: #FFF;
    }

    /* Version List */
    .version-panel .version-table tr > td.base-info .name a {
        color: #dedede;
    }
    .version-panel tr:hover,
    .category-table tr:hover {
        background-color: #4b4b4b;
    }

    /* SYMBOLS */
    .fa-check-circle {
        color: #76ff03;
    }

    /* Modal */
    .modal-content {
        background-color: #4b4b4b;
    }
    .modal-footer {
        border-top: 1px solid #404040;
    }
}
