css:
@-moz-document domain("www.blabbermouth.net") {
body, .home.blog, .header, #content, #container, .pagination, .footer, .banner {
    background-color: black !important;
}

.banner {
    border-bottom: 5px solid #000;
}

#container, .widgettitle {
    background: black !important;
    color: lightgray !important;
}

.widgettitle {
    background-color: #0A0A0A !important;
    border-color: #0b0b0b;
    box-shadow: inset 0px 0px 3px #000;
}

a, a:visited {
    color: lightgray !important;
}

a:hover {
    color: red !important;
}

.trc_rbox {
    border-color: #0A0A0A !important;
}

.current {
    background-color: #0A0A0A !important;
    text-shadow: none;
}

#logo {
    filter: invert(100%);
}
}
