css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.lioden.com") {
body {
    background: url('https://static.lioden.com/images/nightBG.jpg');
    background-attachment: fixed;
    background-size: 100%;
    background-position: center;
    color: #a8b3b7!important;
}
div#lion_image > div {
    background: url('http://static.lioden.com/images/dynamic/backgrounds/RootsOfTheWarriorsBG.png');
}
div.box {
    background: #28434e!important;
    width: 700px;
    border-radius: 15px;
}
h1 {
    color: #dde1dd!important;
    text-align: center;
}
h2 {
    color: #dde1dd!important;
}
h3 {
    color: #dce3e5!important;
}
table.table {
    border: 1px solid black!important;
}
.table > td,
.pagination > span {
    background: #28434e!important;
    color: #100800!important;
    border: 1px solid #100800;
}
td.right,
.sub_menu,
.right_odd.xsmall,
.right_locked,
td.right_odd,
span.left,
.customiser-sortable li:nth-child(2n + 1) {
    background: #3e4648!important;
    color: #e9f0f2!important;
    border: 1px solid black;
}
div.randommember {
    background-color: #344446!important;
}
ul.nav.nav-pills.nav-stacked > li.active > a {
    background-color: #344446!important;
}
html body div.container.main div.row div.col-md-9.col-xs-12 div.col-md-9.col-md-push-3 table.table tbody tr td.right span {
    color: #a8b3b7!important;
}
html body div.container.main div.row div.col-md-9.col-xs-12 div.col-md-9.col-md-push-3 {
    background: #151717!important;
}
tr.right:nth-child(3) > td:nth-child(1) {
    background: #1e2324!important;
}
td.left {
    background: #1e2324!important;
    color: #a8b3b7!important;
    border: 1px solid black;
}
td.left.b {
    background: #1e2324!important;
    color: #a8b3b7!important;
    border: 1px solid black;
}
td.right_cub,
.sub_menu_item_selected {
    background: #4c5253!important;
    color: #100800!important;
}
.item {
    border: 1px solid #100800;
}
td.top,
.item-header,
.item-footer {
    background: #1e2324!important;
    color: #88999b!important;
    text-align: center;
}
th {
    background: #1e2324!important;
    color: #88999b!important;
    text-align: center;
    border: 1px solid black
}
td.bottom {
    background: #1e2324!important;
    color: #88999b!important;
    text-align: center;
}
a:link {
    color: #9caca7!important;
}
a:visited {
    color: #9caca7!important;
}
a:hover {
    color: #76817e!important;
}
a:active {
    color: #76817e!important;
}
.sidebar {
    background: #344446!important;
}
.panel {
    background: #21170c!important;
    color: #dde1dd!important;
    box-shadow: none!important;
    border: none!important;
}
.xsmall {
    background: none!important;
}
/* For your inbox. Highlights unread messages. */
td.unread-msg.small,
td.unread-msg.center {
    background: #54625e!important;
}
.s-chat-message {
    color: #a8b3b7!important;
    background: #21170c!important;
}
#txtChatContainer #lblCharacterCounter {
	color: #c1b375
}
.s-chat-sidebar #label_currentChannel {
    background: #21170c;
}
.s-chat-popout {
	background: #1e2324;
}
#fra_chatContainer .channelMenu .channelSwitcher {
	background: #799091;
	color: #FFF;
}
#chatMessagePinned.s-chat-message__pinned {
	background: #799091 url(http://static.lioden.com/images/layout/sticky.png) no-repeat 10px 6px;
}
#fra_chatContainer #channelMessage {
	color: #c1b375;
	padding: 10px 0 18px 0;
}
.topbar {
    background: #1e2324!important;
}
.navbar,
.sub_sub_menu {
    background: #1e2324!important;
}
input[type="submit"] {
    background: #21170c!important;
    color: #dde1dd!important;
    text-shadow: none!important;
    box-shadow: none!important;
    border: none!important;
    border-radius: 10px!important;
}
input[type="submit"]:hover {
    background: #aeb5ae!important;
    color: #21170c!important;
    text-shadow: none!important;
    box-shadow: none!important;
    border: none!important;
    border-radius: 10px!important;
}
input[type="button"] {
    background: #21170c!important;
    color: #dde1dd!important;
    text-shadow: none!important;
    box-shadow: none!important;
    border: none!important;
    border-radius: 10px!important;
}
input[type="button"]:hover {
    background: #dde1dd!important;
    color: #21170c!important;
    text-shadow: none!important;
    box-shadow: none!important;
    border: none!important;
    border-radius: 10px!important;
}
.navbar-default .navbar-nav > li > a {
    color: #9caca7!important;
}
.navbar-default .navbar-nav > li > a:hover {
    background: #47636e!important;
}
.breadcrumb {
    background: #444d4e!important;
}
.breadcrumb > .active {
    color: #dde1dd!important;
}
.progress-bar-warning {
    background: #dde1dd!important;
}
.progress-bar-danger {
    background: #dde1dd!important;
}
.progress-bar-success {
    background: #dde1dd!important;
}
.progress-bar {
    background: #a4aca4!important;
}
.progress {
    background-color: #47636e!important;
    border: 1px solid #546d71;
}
.progress div {
    color: #151717!important;
}
input[type="text"] {
    color: #8aadb2!important;
    background: #21170c!important;
    border: 2px solid black;
    border-radius: 10px;
}
input[type="password"] {
    color: #8aadb2!important;
    background: #21170c!important;
    border: 2px solid black;
    border-radius: 10px;
}
textarea {
    color: #8aadb2!important;
    background: #21170c!important;
    border: 2px solid black;
}
select {
    color: #8aadb2!important;
    background: #21170c!important;
    border: 2px solid black;
}
.alert,
.pagination span {
    /* These are your "panels"(in sidebar), alerts(like new messages, pride member leaving warnings, etc.), and other parts of the layout across the site.
         Again, I recomend leaving these together.  */
    box-shadow: none!important;
    border: none!important;
    background: #21170c!important;
    border-radius: 15px;
    /* Rounded corners. Remove if you don't want them. */
    color: #c1b375!important;
}
div.feature {
    /* Small piece of coding, used in some events, Play All, Feed All tool, and maybe some other uses on the site. */
    box-shadow: none!important;
    border: none!important;
    background: #546d71!important;
    border-radius: 15px;
    /* Rounded corners. Remove if you don't want them. */
    color: #dde1dd!important;
}
div.container.main {
    background: #151717!important;
    box-shadow: none!important;
    border: 2px solid black!important;
    border-top-right-radius: 15px!important;
    border-bottom-right-radius: 15px!important;
    border-bottom-left-radius: 15px!important;
}
div.col-md-9.col-xs-12 {
    background: none!important;
    border: none!important;
    border-top: 2px solid black!important;
}
.item-surround {
    background: none!important;
}
/* This is for your inventory, shops, and such. */
div.item-surround {
    text-align: center!important;
}
div.item-surround a {
    color: #88999b!important;
}
.item {
    border: none!important;
    background: #88999b!important;
}
.item-footer {
    background: #1e2324!important;
    color: black;
}
.item-header {
    background: #1e2324!important;
    color: black;
}
div.col-md-9.col-xs-12 {
    background: none;
    border: none;
}
.cave-grid,
.mound-grid {
    background: #1e2324!important;
    color: #a8b3b7!important;
    border: 1px solid #100800;
}
td.attack-lost {
    background: #3b4344!important;
}
table.table,
tbody,
tr.right_odd td {
    background: #3b4344!important;
}
table.table,
tbody,
tr.right td {
    background: #1e2324!important;
}
div.achievement,
div.achievement-header {
    background: #1e2324!important;
    border: 2px solid;
}
div.achievement,
div.achievement-body {
    background: #444d4e!important;
    border: none
}
input:checked + .slider {
    background-color: #21170c!important;
}
div.abandoned-count {
    color: #4e5a5b
}
div.left {
    background-color: #344446!important;
    color: #88999b!important;
}
div.cbo-list {
    background: #100800!important;
}
input {
    background: #21170c!important;
}
input[type="button"].bad, input[type="submit"].bad, input[type="reset"].bad:hover {
	background: #770303!important;
}
}
