css:
@-moz-document url-prefix("https://ipon.hu/") {
body {
    background-color: rgba(0, 0, 0, .2);
}

.article-list {
    background-color: rgba(0, 0, 0, .1)
}

.main-menu {
    background-color: rgba(0, 0, 0, .5);
    width: 80%
}

.shop-menu {
    background-color: rgba(0, 0, 0, .5)
}

.shop-menu__level1-block {
    background-color: rgba(0, 0, 0, .5)
}

.article__body>* {
	text-align: justify;
    width: 65%;
    margin-left: 5%;
}

/* - cikk szövege - */
.article__body p {
    line-height: 1.5rem;
    margin-top: 1.5em;
    font-size: 1.15rem;
}
/* -- */

.site-nav {
    background-color:#07307b
}

.site-header {
    	background-color: #002260
}

.site-nav__shop-menu {
    background-color: #002260
}

.ipon-logo {
    background-color: transparent
}

.article__body__block--center {
    margin-top: 1.6rem;
    margin-bottom: 3.75rem;
    width: 62%;
    margin-left: 6.3%;
}

.category-label {
    color: #002260
}

.button--lg {
    background-color: #07307b
}

.site-footer__language-social-block {
    background-color: #07307b
}

.site-footer__block-title {
    color: #0341b1
}

.site-footer__language-dropdown .select-input__text {
    background-color: #07307b
}

.site-footer__main-block {
    background-color: #062662
}

.main-menu__link:hover {
    background-color: rgb(7, 48, 123)
}

/* - fórum - */

.forum-list--post__author_image {
    list-style-image: url(https://i.imgur.com/DDx0euL.png)
}

.forum-section__header {
    background-color: #002260
}

.forum-section--simple__header {
      color: #fff
}

.forum-list--post__item {
    text-align: justify;
    background-color: #1c1c1c40;
    margin-bottom: 0.25rem;
    padding-left:20px;
}

.forum-list--post__item__comments, .forum-list--post__item__new-comment {
    border-top: 1px;
    
}

.forum-list--post__item__comments {
    padding-left: 3rem;
    margin-left: 11.5rem;
    background-color: #1c1c1c10;
    padding-right: 2.5rem;
}

.forum-list--post__item__new-comment {
    background-color: #a0a0a0;
    padding: 0.2rem 5rem 1rem 16rem;
}

.forum-list--post__item__author {
    flex-shrink: 0.5;
    width: 100%;
    padding-top: 0rem;    
}

/* - 'új hozzászólás' és 'válasz' feliratok színe - */
.forum-list--post__item-actions-list-item a {
    color: #32314a;
}

/* - 'LIKE' gomb és számláló színe - */
.forum-list--post__item-like-link {
    color: #146314;
}

.likes-dislikes-count {
    color: #146314;
}

/* - 'DISLIKE' gomb és számláló színe - */
.material-icon--thumb_down:after {
    color: #c5183b;
}

.forum-list--post__item-like--dislike .likes-dislikes-count {
    color: #c5183b;
}

/* - hozzászólás - */
.forum-list--post__item__text {
    margin-top: 1rem;
    font-size: 1.2rem;
    line-height: 1.4rem;
}

/* - hozzászólásra válaszok - */
.forum-list--post__item__comment-text {
    font-size: 1.2rem;
    line-height: 1.4rem;
}

.forum-list__item__date {
    color: #423e4b;
    font-size: 1rem
}

@media {
.forum-list--post__item__post-wrapper {
    padding: 0.5rem 2.5rem 2rem 0.5rem;
 }
}

.forum-list--post__item__number
{
  width: 0;
  padding-top: 4rem;
  padding-left: 0;
  position: absolute;
  font-size: 1.5rem;
}

/* - cikk alatti kommentek - */ 

.article-comments {
	text-align: justify;
    margin: 4.5rem 200px 3.75rem;
}

.article-comments .forum-list--post__item__text {
     font-size: 1.15rem;
    flex-basis: 70%;
}

.article-comments .forum-list--post__item__comment-text-wrapper {
     font-size: 1.15rem;
}

@media {.article comments .forum-list--post__item__comments {
    padding-left: 3rem;
    margin-left: 11.5rem;
    padding-right: 2.5rem;
 }
}

.article-comments .forum-list--post__item__post-wrapper {
    justify-content: flex-start;
    padding: 1.5rem 2.5rem 2rem 0.5rem;
    
}

.article-comments .forum-list--post__item__comments .forum-list--post__item__comment {
    max-width: 100%;
    margin-left: 1rem;
}

.article-comments .forum-list--post__item__comments {
	margin-left: 10rem;
    padding-left: 1.75rem;
    
}

.article-comments .forum-list--post__item__new-comment {
    padding: 0.2rem 1rem 1rem 1rem;
}

.article-comments .forum-list--post__item__author {
    width: 95%;
  
}

.article__body__inline-block--rating {
    left: auto;
    right: 4rem;
    
}

.article__body .tldr {
    font-size: 2rem;    
}

.article__text {
    font-size: 1.3rem;
    font-style: italic;
}


/* - cikkek alatti első komment behúzása balra */
.article-comments .forum-list--post__item__post-wrapper .forum-list--post__item__author, .article-comments .forum-list--post__item__post-wrapper .forum-list--post__item__number {
    padding-left: 0px;
}

.flag--placeholder {
    background-color: #07307b; 
}

.nav-columns--sticky .nav-columns__layouts .nav-columns__item.is-active .nav-columns__icon {
    color: #07307b;
}
}
