@-moz-document domain("noodletools.com") {
::selection {
    background: #8e631ca1;
    color: #ffeede;
    text-shadow: 0 0 4px #ff9f00;
}
p {
    color: #a2a2a2;
}
body {
    background-color: #333;
}
.site-header {
    border-bottom: 1px solid #e1e0e5;
    background: #464646;
}
.product-benefits {
    background: #464646;
}
.target-audiences {
    background: #383838;
}
.callout-box .text-content,
.target-audiences .audience .text-content,
.plans-callout .text-content,
.signup-form-wrapper .text-content {
    background: #585858;
}
.site-footer {
    background: #464646;
    border-bottom: 4px solid #292929;
}
.callout-box,
.target-audiences .audience,
.plans-callout,
.signup-form-wrapper {
    background-color: #6f6f6f;
    border-bottom: 7px solid #fdbd57;
    margin-bottom: 2rem;
}::selection {
    background: #8e631ca1;
    color: #ffeede;
    text-shadow: 0 0 4px #ff9f00;
}
body {
    color: #dadada !important;
    background: rgb(49, 49, 49)!important;
}
div.layeredDiv {
    background: #000000;
    height: 37px;
    position: absolute;
    width: 100%;
    z-index: 999;
    opacity: 0.5;
}
.navbar-default .navbar-nav>.active>a,
.navbar-default .navbar-nav>.active>a:hover,
.navbar-default .navbar-nav>.active>a:focus {
    background: linear-gradient(to bottom, #ffb94f 0, #e27700 100%) !important;
    color: #fff !important;
}
.navbar-default .navbar-nav>.active>a {
    background: #313131 !important;
    box-shadow: inset 0px 0px 10px 0px #000000 !important;
    color: #ffffff !important;
}
.navbar-default .navbar-nav>li>a {
    background: linear-gradient(to bottom, #777777 0, #848484 100%) !important;
    box-shadow: inset 0px 0px 40px -8px #000000;
    color: #c5c5c5 !important;
}
.navbar-default .navbar-nav>li>a:hover {
    background: linear-gradient(to bottom, #ffb94f 0, #e27700 100%) !important;
    box-shadow: inset 0px 0px 30px -4px #000000 !important;
    color: #000000 !important;
}
.displayTabs {
    background: linear-gradient(to bottom, #313131 0, #353535 100%);
}
.projectInfoPanel {
    width: 100%;
    min-height: 165px;
    background-color: #5d5d5d;
    color: #adadad;
}
.panel-default {
    width: 100%;
    min-height: 165px;
    background: #5d5d5d;
    color: #adadad;
}
#researchQuestions,
#thesisStatements {
    border: none !important;
}
.panel-default>.panel-heading {
    background: none repeat scroll 0 0 #6d6d6d !important;
    border-top: 5px solid #ffffff70!important;
    border-bottom-style: hidden !important;
    color: #d4d4d4!important;
}
a {
    color: #ffac26;
    text-shadow: 0 0 1px #ff9f00;
    text-decoration: none;
}
a:hover,
a:focus {
    color: #ffd368;
    text-shadow: 0 0 2px #ffa715;
    text-decoration: solid;
}
.table-hover>tbody>tr:hover>td,
.table-hover>tbody>tr:hover>th {
    transition: background 0.2s ease;
    background: #656565;
}


.btn-default {
    transition: all 0.1s ease !important;
    background: linear-gradient(to bottom, #777777 0, #848484 100%) !important;
    box-shadow: inset 0px 0px 40px -8px #000000;
    color: #c5c5c5 !important;
    
    border-color: #ccc;
}
.container-fluid .panel-heading .btn.btn-default:hover {
    background: #ffab20 !important;
    color: #ffffff!important;
}
button,
select {
    text-transform: none;
    padding: 0px 10px !important;
    border-radius: 8px !important;
}
.notecard {
    background: url(https://i.imgur.com/tzpBD5k.png);
    background-position: 11px 0;
    background-repeat: no-repeat;
    border: 2px solid transparent;
    cursor: pointer;
    display: block;
    height: 70px;
    position: absolute;
    visibility: visible;
    width: 85px;
}
a.infoSign {
    margin: 2px 0px 1px 10px;
    font-size: 14px;
    color: #ff9d00 !important;
    text-decoration: none !important;
}
.projectCommentDetail .detailCommentView,
.NoteSourceCommentDetail .detailCommentView {
    background: #565656!important;
}
.form-control {
    color: #cecece !important;
    vertical-align: middle;
    background: #6d6d6d;
    border: 1px solid #848484;
    border-radius: 6px !important;
    box-shadow: inset 0px 0px 20px 0px #3f3f3f !important;
}
.container-fluid .btn.btn-default {
    background: none repeat scroll 0 0 #e0861b !important;
    border-color: #ffffff !important;
    box-shadow: 0px 0px 5px 0px #ff9f00 !important;
    color: #ffffff !important;
    border-radius: 5px;
    padding: 1px 9px !important;
    text-shadow: none;
}
.showMeHow {
    border: 1px solid #CCCCCC;
    border-radius: 3px;
    color: #ffb500;
    cursor: pointer;
    font-size: 12%;
    line-height: 1.5em;
    padding: 0px 2px;
    margin: 10px 5px 0;
}
select#printExportSelect,
select#sortByCitation,
select#applyDescription {
    padding: 0px 0px 3px 8px !important;
    border-radius: 5px;
    color: #333;
    border: 1px solid #333;
    background: #a5a5a5 url(https://mystatic.noodletools.com/web/_images/dropdown_arrow_bib.png) no-repeat right 8px top 8px;
    height: 24px;
    font-size: 12px;
}
.commentDivParent {
    background: #3c3c3cc4!important;
    padding: 8px 10px !important;
    border-color: #3e3e3e !important;
    border-radius: 7px;
}
.linkInfoColor,
.linkInfoColor:hover {
    color: #afafaf;
}
.detailCommentView {
    padding: 10px;
    background: #f9f9f900 !important;
}
.projectCommentDetail .noteThreadTitle,
.NoteSourceCommentDetail .noteThreadTitle {
    background: rgb(68, 68, 68)!important;
    border: 1px solid #383838!important;
    color: #d6d6d6!important;
    margin-top: -2px;
    padding: 10px!important;
}
.commentChats {
    margin-top: 0px;
    background: #6d6d6d;
}
.optionsBtn {
    background: none repeat scroll 0 0 #e0861b !important;
    border-color: #ffffff !important;
    box-shadow: 0px 0px 5px 0px #ff9f00 !important;
    color: #ffffff !important;
    border-radius: 5px;
    padding: 1px 9px !important;
    text-shadow: none;
}
.optionsBtn:hover {
    color: #ffffff!important;
    background: #ffab20 !important;
}
.container-fluid .btn.btn-default:hover {
    color: #ffffff!important;
    background: #ffab20 !important;
}
font.openProjectText {
    color: rgb(255, 233, 114);
    text-shadow: 0px 0px 10px #ffd70a;
}
.modal-header {
    background: #5f5f5f !important;
}
.popupHeader {
    color: #e0e0e0!important;
}
.row.padding2.topSection.body {
    background: #e0e0e0 !important;
}
