css:
button + span[data-test="hint-sentence"] {
    border-radius: 10px;
    cursor: pointer;
    display: block;
    margin-left: 40px;
    position: relative;
}
button + span[data-test="hint-sentence"]::after {
    background-color: #dbdbdb;
    border-radius: inherit;
    color: #7d7d7d;
    content: "Hover to reveal";
    display: block;
    font-weight: 500;
    height: 100%;
    left: 0;
    opacity: 1;
    padding-left: 10px;
    pointer-events: none;
    position: absolute;
    text-align: center;
    top: 0;
    transition: opacity 200ms linear !important;
    width: 100%;
}
button + span[data-test="hint-sentence"]:hover::after {
    opacity: 0;
}
button + span[data-test="hint-sentence"] > span[data-test="hint-token"] {
    background: none;
    color: transparent;
    transition: color 200ms linear !important;
}
button + span[data-test="hint-sentence"]:hover > span[data-test="hint-token"] {
    background: revert;
    color: inherit;
}
