css:
@-moz-document domain('moodle.telt.unsw.edu.au'){
.header-top {
    background: -webkit-linear-gradient(45deg, rgba(57, 59, 98, .5) 0%, rgba(81, 60, 99, .5) 19%, rgba(140, 65, 104, .5) 43%, rgba(203, 78, 108, .5) 69%, rgba(237, 106, 111, .5) 99%);
}

.block .header .title h2,
.block h3.main {
    color: #5e0231;
    background: #fafafa;
    font-family: Abel, sans-serif;
}

.block .content {
    padding: 20px 20px;
    word-wrap: break-word;
    background: #fafafa;
}

.block {
    min-height: 10px;
    padding: 19px;
    padding: 5px 5px;
    margin-bottom: 20px;
    background: #fafafa;
    border: 2px #5e0231;
    border-radius: 7px;
}

.well {
    margin-bottom: 15px;
    border: 1px solid #5e0231;
    border-radius: 4px;
    padding: 5px;
    background-color: #5e0231;
}

.nav-tabs {
    border: none;
    background-color: #fafafa;
    margin-left: auto;
    margin-right: auto;
    width: 50%;
    padding-left: 38%;
    display: block;
    margin-bottom: 10px;
    margin-top: -10px;
    font-family: Abel, sans-serif;
}

#page-footer {
    margin-top: -3em;
    padding: 1em 0;
    /* border-top: 2px solid #ddd; */
}
#page-footer,
#page-footer .validators,
#page-footer .purgecaches,
#page-footer .performanceinfo {
    color: #5e0231;
    background: #c7a693;
    border-top: 2px solid;
    border-bottom: 2px solid;
}
body {
    margin: 0;
    background: #fafafa;
    font-family: Abel, sans-serif;
    color: #856046;
}
#page-header {
    margin-top: -20px;
}

.nav-tabs li a {
	background: #dbc3d0;
    color:#856046;
    font-family: Abel,'san-serif';
    font-size: 1em;
    line-height: 1em;
    margin-bottom: 0.2em;
    margin-right: 0.2em;
    padding-left: 1em;
    padding-right: 1em;
}
.nav-tabs li.active a {
    color: #FFFFFF;
    background: -webkit-linear-gradient(45deg, rgba(57, 59, 98, .5) 0%, rgba(81, 60, 99, .5) 19%, rgba(140, 65, 104, .5) 43%, rgba(203, 78, 108, .5) 69%, rgba(237, 106, 111, .5) 99%);
}

.header {
    background-color: #fafafa;
    color: #fafafa;
}

table {
    max-width: 100%;
    background-color: transparent;
    border-collapse: collapse;
    border-spacing: 0;
    background: #fafafa;
}

.path-mod-assign div.submissionstatustable,
.path-mod-assign div.submissionfull,
.path-mod-assign div.submissionlinks,
.path-mod-assign div.usersummary,
.path-mod-assign div.feedback,
.path-mod-assign div.gradingsummary {
    margin-top: 50px;
    padding: 5px;
    background: linear-gradient(45deg, rgba(254, 222, 150, 1) 0%, rgba(255, 221, 123, 1) 10%, rgba(254, 203, 111, 1) 20%, rgba(252, 155, 112, 1) 32%, rgba(237, 106, 111, 1) 45%, rgba(203, 78, 108, 1) 59%, rgba(140, 65, 104, 1) 72%, rgba(81, 60, 99, 1) 87%, rgba(57, 59, 98, 1) 100%);
    border-radius: 10px;
}
.dir-ltr,
.mdl-left,
.dir-rtl .mdl-right {
    text-align: left;
    background-color: #fafafa;
}

a.btn{
    background: #dbc3d0;
    color:#856046;
    border: 3px;
}

a.btn.active {
    background: -webkit-linear-gradient(45deg, rgba(57, 59, 98, .5) 0%, rgba(81, 60, 99, .5) 19%, rgba(140, 65, 104, .5) 43%, rgba(203, 78, 108, .5) 69%, rgba(237, 106, 111, .5) 99%);
    color: white
}

.button,
input.form-submit,
input[type="button"],
input[type="submit"],
input[type="reset"] {
    background: -webkit-linear-gradient(45deg, rgba(57, 59, 98, .5) 0%, rgba(81, 60, 99, .5) 19%, rgba(140, 65, 104, .5) 43%, rgba(203, 78, 108, .5) 69%, rgba(237, 106, 111, .5) 99%);
    color: white;
}
.feedback h3 {
    color: #5e0231;
}
.submissionstatustable h3 {
    color: #5e0231;
}

.message .messagearea .messagehistory .left {
    margin-bottom: 10px;
    width: 50%;
    float: left;
    position: relative;
    clear: both;
    background-color: white;
}

#message_user_pictures {
    text-align: center;
    background-color: white;
}
.dir-ltr .message .messagearea .messagehistory .right .message {
    margin-left: 20px;
    background-color: transparent;
}
#mdl-left messagehistory dir-ltr,
.mdl-left,
.dir-rtl .mdl-right {
    text-align: left;
    background-color: #ffffff;
}
#useractionlinks {
    width: 50px;
}
.message .messagearea .messagehistorytype {
    clear: both;
    padding-bottom: 20px;
    padding-top: 20px;
    margin-right: -10px;
    margin-left: -10px;
    background: linear-gradient(45deg, rgba(254, 222, 150, 1) 0%, rgba(255, 221, 123, 1) 10%, rgba(254, 203, 111, 1) 20%, rgba(252, 155, 112, 1) 32%, rgba(237, 106, 111, 1) 45%, rgba(203, 78, 108, 1) 59%, rgba(140, 65, 104, 1) 72%, rgba(81, 60, 99, 1) 87%, rgba(57, 59, 98, 1) 100%);
    font-family: Century Gothic, sans-serif;
}
#mdl-left messagehistory .mdl-left a {
    background-color: transparent;
}
.message .contactselector {
    float: left;
    margin-top: 30px;
    padding: 0px 8px 0px 0px;
}
#message_user_pictures {
    text-align: center;
    background-color: white;
    margin-top: 0px;
    PADDING-TOP: 20px;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
}
.message .messagearea .messagehistory .user>div {
    text-align: center;
    WIDTH: 252PX;
}
.message .messagearea .messagehistory .heading {
    width: 100%;
    clear: both;
    margin-bottom: 25px;
}
#mdl-align heading .mdl-left a {
    background-color: #ffffff;
    border: 0px solid white;
    border-radius: 1px;
}
.dir-ltr .message .messagearea .messagehistory .message {
    margin-right: 20px;
    background-color: #262626;
    color: white;
}
#mdl-left messagehistory dir-ltr,
.mdl-left,
.dir-rtl .mdl-right {
    text-align: left;
    background-color: #262626;
}
.message .messagearea .messagehistory .left {
    margin-bottom: 10px;
    width: 50%;
    float: left;
    position: relative;
    clear: both;
    background-color: transparent;
}
.message .messagearea .messagehistory .notification {
    min-height: 20px;
    padding: 19px;
    background-color: #262626;
    border: none;
}
.dir-ltr .message .messagearea .messagehistory .message {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #262626;
    border: 1px solid #e3e3e3;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
    border-color: #e3e3e3;
}
.message .messagearea {
    border-left: 0px solid #ddd;
    padding: 0px 0px;
}
table#form td.submit,
.form-buttons,
.path-admin .buttons,
#fitem_id_submitbutton,
.fp-content-center form+div,
div.backup-section+form,
#fgroup_id_buttonar {
    padding: 19px 20px 20px;
    margin-top: -25px;
    margin-left: 420px;
    padding-left: 0;
    background-color: transparent;
}
.message .messagearea .messagesend fieldset {
    padding: 0;
    margin-left: -150px;
}
input.form-submit,
input#id_submitbutton,
input#id_submitbutton2,
.path-admin .buttons input[type="submit"],
td.submit input {
    color: #262626;
    border: none;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
    background: linear-gradient(45deg, rgba(254, 222, 150, 1) 0%, rgba(255, 221, 123, 1) 10%, rgba(254, 203, 111, 1) 20%, rgba(252, 155, 112, 1) 32%, rgba(237, 106, 111, 1) 45%, rgba(203, 78, 108, 1) 59%, rgba(140, 65, 104, 1) 72%, rgba(81, 60, 99, 1) 87%, rgba(57, 59, 98, 1) 100%);
}
.row {
    margin-left: auto;
    margin-right: auto;
}
.progress-chart-container .no-progress {
    height: 70px;
    width: 70px;
    background-color: #c7a693;
    border-radius: 50%;
    position: relative;
    display: flow;
}

.block_myoverview h4 {
    color: #856046;
    font-weight: 400;
    margin-top: 24px;
    margin-bottom: auto;
}
.block_myoverview h4 a{
    color: #856046;
}
.block_myoverview .well {
    background: -webkit-linear-gradient(45deg, rgba(57, 59, 98, .5) 0%, rgba(81, 60, 99, .5) 19%, rgba(140, 65, 104, .5) 43%, rgba(203, 78, 108, .5) 69%, rgba(237, 106, 111, .5) 99%);
    box-shadow: inset -1px 1px 5px #999;
    border-left: 15px solid #856046;
}

.content ul #block-myoverview-view-choices {
    margin-left: 500px;
}
}
