}
.house-list-wrap li {
    position: relative;
    /*border-bottom: 1px solid #f3f3f3;*/    
    padding: 8px 8px 8px;
    margin-top: 0px;
    margin-bottom: 15px;
    border-radius: 5px;
    background-color: #fff;
    box-sizing: border-box;
    box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
}
