css:
@-moz-document domain("www.juejin.im"){
body{
    background: url("data:image/png;base64,");
}
.banner-block {
    display: none;
}
nav[class^=user],.timeline-entry-list,.sidebar-block{
    box-shadow: 0 2px 6px rgba(93, 93, 93, 0.1) !important;
    border-radius: 6px !important;
    transition-duration: 0.4s !important;
}
nav[class^=user]:hover,.timeline-entry-list:hover,.sidebar-block:hover{
    box-shadow: 2px 5px 15px 0px #e6e6e6 !important;
}
.content-box{
	transition-duration:0.4s;
}
.content-box:hover{
	box-shadow: 0px 1px 18px 1px #e2e2e2;
    border-radius: 5px;
    background-color: #f9f9f9;
}
.sticky-banner {
    display: none;
}
.index-book-collect{
    display: none;
}
}
