css:
@-moz-document url-prefix("https://repl.it/repls/"), url-prefix("https://repl.it/") {
	::-moz-selection {  background: #ffb7b7; /* Gecko Browsers */}
	body { background-color: #232323 !important; }
   .dynamic-header { background-color: #232323 !important;  border-bottom: 1px solid #232323 !important; }
   .dynamic-header-nav-left, .dynamic-header-nav-right { background: #232323 !important;}
   .dynamic-header-nav-item { background-color: #222 !important; color: #3fa !important; transition: all .15s ease-in-out; }
   .dynamic-header-nav-item:hover { color: #3fa !important; text-shadow: 0 0 3px #3fa !important; }
   .workspace-header-title { color: #3fa !important; }
   .file-tree { border-left :1px solid #232323 !important; background: #232323 !important; }
   .file-tree-header { background: #232323  !important; border-bottom:1px solid #333 !important; }
   .header-fork-repl-icon { color:#fff  !important; }
   .node-row:hover { background: #232323 !important;}
   .node-row-active.jsx-2129870860 { background: #333 !important;}
   .node-row-active .node-row-label { background: #333 !important; color:#3fa !important; }
   .workspace-button { background: transparent !important; border:1px solid #3fa !important; color: #3fa !important;}
   .active-file-tree-toggle { background: #333 !important; }
   .active-file-name, .active-file-seperator { background: #333 !important; color: #3fa !important; }
   .active-file-seperator { border-left: none !important; }
   #console { background: #282b32 !important; }
  .jqconsole {color: #3fa !important; }
  .workspaceWrapper { border-top: 1px solid #333 !important; }
  .ace_keyword { color: #3fa !important; }
  .ace_constant .ace_other { color:#ff4343 !important;}
  .ace_constant.ace_other { color: #f43 !important; text-shadow: 0 0 1px #f43 !important; }
  .ace_identifier { color: #eee !important; }
  .ace_identifier { color : #43aaff !important;}
  .ace_paren.ace_rparen , .ace_paren.ace_lparen { color: #78f3af !important; }
  .ace_content { background: #282b33 !important ; }
  .ace_storage.ace_type { color: #f45 !important; }
  .ace_punctuation.ace_operator {  color: white;}
  .ace_constant.ace_numeric { color: #ff1 !important;}
  .ace_string { color: #66f !important;}
  .ace_gutter-layer, .ace_folding-enabled { color: #3a6c9e !important; background: #333 important;}
  .ace_storage { color: #ddd !important;}
  .side-nav-options { background: #2b2b2b !important;}
  .toolbar { background: #2b2b2b !important;}
  .windowManagerWrapper div div div:nth-child(2) { background: transparent !important; }
  .view-lines {background: #282b32;}
  .margin-view-overlays { background: #282b32;}
  .current-line.current-line-margin {  background: #3fa !important;  box-shadow: 0 0 4px #3fa;}
  .monaco-editor .line-numbers { color: #337475 !important; }
  .mtk6 {  color: #e3e3e3 !important;}
  .mtk20 { color: #3fa !important; }
  .mtk1 {  color: #11a16f !important }
  .mtk7 { color: #3fa;text-shadow: 0 0 1px #3fa;}

}
