css:
@-moz-document domain("f1.dziel-pasje.pl") {
body,
.kode_wrapper,
.container,
.kode_header_2,
.kf_top_bar,
.row,
.row.mb20.mt20{
    background: #212121 !important;
    color: #BDBDBD;
}

.input_textarea textarea, .select_dec select, .input_dec input[type="text"], .input_dec input[type="password"]{
    background:#616161;
}

input::placeholder,
a.pass-forget,
.radio_dec,
.btn{
    color: #9E9E9E;
}

.kf_hd1,
.thumbnail,
.modal-content,
.kf_table li:hover{
    background: #424242;
}

.thumbnail{
    border-color: #616161;
}

.text_wrper .text p,
.text_wrper .text h2 a,
a.btn_2,
.kode_header_2 .nav > li > a,
.kf_date,
body h1, body h2, body h3, body h4, body h5, body h6, p,
.thumb_footer a,
.kf_recentnews,
h6 a,
.kf_table span,
.kf_table li,
.team_logo,
.team_logo a,
.team_point,
.team_point i,
.row .recentpost_dec a,
.input_textarea textarea::placeholder,
.comment_hd a,
.tranding_tab ul li a{
    color: #BDBDBD !important;
}

table,
.btn:hover{
    color: #fff;
}
.table-striped>tbody>tr:nth-of-type(odd){
    background: #424242;
}

.table-striped>tbody>tr:nth-of-type(odd):hover{
    background: #212121;
}

.table-hover>tbody>tr:hover,
.input_textarea textarea{
    background: #616161;
}


.widget_nextmatch .kf_border .kf_countdown,
.kf_countdown li span{
    background: #424242;
    color: #fff;
}

.nav>li>a:focus,
.nav>li>a:hover,
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover{
    background: #616161;
    color: #fff !important;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover{
    border: 1px solid #757575;
}

}
