css:
@-moz-document domain("komorkomania.pl") {
body,
.insert--top-offer,
.content-wrapper,
.main-header-small__tools mobile-tools-active,
.mobile-tools-active a,
.main-header__dropdown,
.header-tools,
.post-article__drawer--fixed,
.author,
.grid-item--bottom-desc .grid-item__content,
.modal__body,
.main-footer,
.card{
     background: #263238 !important;
    background-color: #263238 !important;
    color: #fff !important;
}

.color-widget--red .grid-wrapper{
    background: #d12727;
}

a,
.author--insert .author__desc a{
    color: #7E57C2;
}

.search-form input[type="search"]{
	background: #7E57C2; 
    color: #fff;
}

.search-form input[type="search"]::placeholder,
.grid-item--desc-layer h2, .grid-item--desc-layer.grid-item--dark h2,
.grid-item--desc-layer .grid-item__meta a,
.grid-item__author,
.table th, .table td{
    color: #eee !important;
}

.komorkomania .article>p>a, .komorkomania .article>ul li>a, .komorkomania .article>ol li>a, .komorkomania .article>p strong>a, .komorkomania .article>ul li strong>a, .komorkomania .article>ol li strong>a{
    color: #EEEEEE;
    box-shadow: 0 -7px 0 #455A64 inset;
}

.komorkomania .article>p>a:hover, .komorkomania .article>p>a:active, .komorkomania .article>ul li>a:hover, .komorkomania .article>ul li>a:active, .komorkomania .article>ol li>a:hover, .komorkomania .article>ol li>a:active, .komorkomania .article>p strong>a:hover, .komorkomania .article>p strong>a:active, .komorkomania .article>ul li strong>a:hover, .komorkomania .article>ul li strong>a:active, .komorkomania .article>ol li strong>a:hover, .komorkomania .article>ol li strong>a:active,
.table th, .table td,
.EmbeddedTweet-tweet,
.table--alt-subheaders tr:nth-child(odd) th, .table--alt-subheaders tr:nth-child(odd) td{
    background-color: #37474F;
}

.btn-drawer-collapse,
.quote,
.table th.table__sub-header,
.pagination-select-2 .pagination__page-select,
.pagination-select-2 .pagination__page-select p,
.pagination__page-select ul,
.pagination__page-select ul li a,
.pagination-select-2:hover .pagination__page-select, .pagination-select-2:focus .pagination__page-select,
.table--alt-subheaders tr:nth-child(even) th, .table--alt-subheaders tr:nth-child(even) td,
.next-posts-alt .post-article--read-more a,
.insert__default{
    background-color: #455A64;
    color: #fff;
}

.next-posts-alt .post-article--read-more a{
    border-color: #fff;
}

.next-posts-alt .post-article--read-more{
    background: linear-gradient(rgba(38, 50, 56 ,0) 5%, #263238 55%, #263238);
}
}

@-moz-document regexp("https?://disqus\\.com/embed/comments/.*komorkomania\\..*") {
/*Komentarze Disqus*/
.post-message p{
    color: #fff !important;
}

.tab-conversation.active > a {
         color: #fff !important;
    }
     .nav-primary {
         border-bottom: 2px solid #fff;
    }
}
