css:
@-moz-document domain("telewizjarepublika.pl") {
body,
.body,
#header-top,
#header,
.rel.wrapper,
.wrapper,
#footer-bottom,
#column-article{
    background: #263238;
    color: #fff;
}

a{
    color: #E0E0E0 !important;
}

#search:hover .search-inner{
    background: #455A64;
}

.bar,
#article h1,
h2,
#article p,
.lead,
.comment-text,
.articleBody{
    color: #fff;
}

.articleLead,
.data,
.embed-work-detail{
    color: #BDBDBD;
}

.data,
.EmbeddedTweet{
    background: #455A64;
}

#video,
.timeline-Widget,
.MediaCard-mediaContainer,
.bar,
#column-right,
#column-leftmiddle{
    background: #37474F !important;
}

.najnowsze-box .bg{
    background: linear-gradient(#0000, #263238) !important;
}
}
