css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("lioden.com") {
body {
    background: url('https://orig00.deviantart.net/07f1/f/2018/121/3/e/lioden_dark_bg_by_roselynn1214-dcadk2h.png')!important;
    background-attachment: fixed!important;
    background-size: 100%!important;
    color: gray!important;
}
h1,
h2,
h3 {
    color: gray!important;
}
table.table td {
    background: #202133!important;
    color: gray!important;
}
td.right,
.sub_menu,
.right_odd.xsmall,
.right_locked,
td.right_odd,
span.left,
.customiser-sortable li:nth-child(2n+1) {
    background: #202133!important;
    color: gray!important;
}
th,
td.top,
td.bottom,
div.left {
    background: #484b54!important;
    color: black!important;
}
td.left.b.center,
td.left.b,
td.left,
td.right_cub,
.sub_menu_item_selected {
    background: #1c1b1e!important;
    color: gray!important;
}
a:link,
a:visited {
    color: gray!important;
}
a:hover,
a:active {
    color: white!important;
    transition: all ease 2s;
}
.sidebar,
.navbar {
    background: #202133!important;
    color: gray!important;
}
.navbar {
    border-top-right-radius: 15px;
}
.sidebar {
    border-bottom-right-radius: 15px;
    border-left: 1px solid black;
}
.navbar-default .navbar-nav > li > a {
    color: gray!important;
}
.navbar-default .navbar-nav > li > a:hover {
    background: #484b54!important;
    transition: all ease 3s;
}
.xsmall {
    background: none!important;
}
.panel,
.sub_menu,
.sub_sub_menu,
.pagination span {
    box-shadow: none!important;
    border: 1px solid black!important;
    background: #1c1b1e!important;
    border-radius: 15px;
    color: white!important;
}
div.feature {
    box-shadow: none!important;
    border: 1px black solid!important;
    background: #202133!important;
    border-radius: 15px;
    color: gray!important;
}
.chat_light {
    background: #3f3547!important;
    color: gray!important;
}
.chat_dark {
    background: #1c1b1e!important;
    color: gray!important;
}
.topbar,
.breadcrumb {
    background: #1c1b1e!important;
    color: black!important;
}
.breadcrumb > .active {
    color: white!important;
}
input[type="submit"],
input[type="button"] {
    color: gray!important;
    background: black!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    transition: all ease 1s;
    border-radius: 15px!important;
}
input[type="submit"]:hover,
input[type="button"]:hover {
    color: gray!important;
    background: #3f3547!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    border-radius: 15px!important;
}
.progress-bar-warning,
.progress-bar-danger,
.progress-bar-success,
.progress-bar {
    background: #3f3547!important;
}
.progress {
    background: #484b54!important;
    border: 1px solid black;
    border-radius: 15px;
}
.progress div {
    color: gray!important;
}
table.table,
div.table {
    border: 1px solid black!important;
}
input[type="text"] {
    color: black;
}
input[type="password"] {
    color: black;
}
textarea {
    color: black;
}
select {
    color: black;
}
div.container.main {
    background: #2d333e!important;
    box-shadow: none!important;
    border: 2px solid black!important;
    border-top-right-radius: 15px!important;
    border-bottom-right-radius: 15px!important;
    border-bottom-left-radius: 15px!important;
}
div.col-md-9.col-xs-12 {
    background: none!important;
    border: none!important;
    border-top: 2px solid black!important;
}
.item {
    border: none!important;
    background: #2d333e!important;
}
.item-footer {
    background: #1c1b1e!important;
    color: gray;
}
.item-header {
    background: #1c1b1e!important;
    color: gray;
}
.badge {
    background: #1c1b1e!important;
    color: gray!important;
}
.alert-success {
    background: #1c1b1e!important;
    color: gray!important;
    border: 1px solid black;
}
.alert-danger {
    background: #4e4663!important;
    color: black!important;
    border: 1px solid black;
}
table.table.forum > tbody > tr > td.left.center {
    background: #1c1b1e!important;
    color: black!important;
    border-right: 1px solid black;
}
table.table.forum > tbody > tr > td.bottom {
    background: #1c1b1e!important;
    color: black!important;
    border-bottom: 1px solid black;
}
table.table.auto > tbody > tr > td.left.center.hidden-xs {
    background: #1c1b1e!important;
    color: black!important;
    border-right: 1px solid black;
}
td.unread-msg.small,
td.unread-msg.center {
    background: #4e4663!important;
}
input[type="text"] {
    background: #1c1b1e;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
input[type="text"]:focus {
    background: #4e4663;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
input[type="password"] {
    background: #1c1b1e;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
input[type="password"]:focus {
    background: #4e4663;
    color: black;
    border: 1px solid black;
    transition: all ease 1s;
    border-radius: 15px;
}
textarea {
    background: #1c1b1e;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
}
textarea:focus {
    background: #4e4663;
    color: white;
    border: 1px solid black;
    transition: all ease 1s;
}
select {
    background: #1c1b1e;
    color: gray;
    transition: all ease 1s;
}
table.table> tbody> tr> td.right > div {
    color: gray;
}
table.table> tbody> tr> td.bottom {
    background: #484b54!important;
    color: black!important;
}
.item-surround {
    background: #484b54!important;
    box-shadow: none!important;
    border: 2px solid black!important;
    border-top-right-radius:5px!important;
    border-bottom-right-radius:5px!important;
    border-bottom-left-radius:5px!important;
}
}
