css:
@-moz-document url-prefix("https://mangadex.org/chapter/") {
/* NAV BAR */
 .navbar {
    min-height: 0;
    margin-bottom: 0;
}
.navbar, .navbar-nav, .navbar-right, #home_button {
    margin: 0;
    font-size: 12px;
    height: 30px;
    padding: 2px 15px;
}

.navbar-toggle, button.navbar-button {
  padding: 0 5px;
  margin-top: 2px;
}

.nav > li > a {
    padding: 2px 10px;
}
        
img[title="MangaDex"] {
    display: none!important;
}

.d-lg-none {
    display: block;
    margin-top: 10px;
}

.d-xl-inline {
    display: none;
}

.navbar-brand {
    margin: 0;
    margin-left: 0;
    font-size: 12px;
    height: 30px;
    padding: 2px;
}
#quick_search_button, #quick_search_type, .input-group-btn {
    display: none;
}
.quick_search_inpupt, .form-control {
    height: 20px;
    padding: 0;
    font-size: 12px;
}
.panel-default, .panel-body, .panel-heading {
    padding: 0;
    margin: 0;
}

.collapse.in {
  background: #000c;
}



/* Manga link text */
 .hidden-sm {
    display: none;
}
/* IMAGE */
 .container, .reader {
    width: 100%;
    padding-left: 0;
    padding-right: 0;
}
/* Selector */

.card {
    width: auto;
    background-color: transparent;
    background-image: none;    
    border: none;
}
.fa-book {
    display: none;
}

.card-header {
    margin-top: 35px;
    padding: 0px;
    background-color: transparent;
    background-image: none;    
    border-bottom: none;
}

.manga_title {
    font-size: 10px;   
    display: block;
    text-align: center; 
    background-color: transparent; 
}

img[title="Japanese"], img[title="Chinese"], img[title="Korean"] {
    display: none!important;
}

.card-body {
    position: static;
    height: 0;
    padding: 0;
}

.mb-3 {
    margin-bottom: 0px !important;
}
/* Old selector */
 .row {
    position: fixed;
    font-size: 10px;
    bottom: 0;
    left: 300px;
    margin-left: auto;
    margin-right: auto;
    opacity: .1;
    z-index: 1000;
    background-color: transparent;
}
.row:hover {
    opacity: 1;
}
.btn, .btn-group {
    padding: 0 10px;
    font-size: 10px;
    width: auto;
    max-width: 200px;
    background-color: transparent;
}
.col-md-2 {    
    width: auto;
    float: left;
    flex: none;
    max-width: 50%;
}
.col-md-3 {    
    width: 35%;
    float: left;
    flex: none;
    max-width: 50%;
}
.col-md-4 {
    display:none;
}
.btn-warning, .minimise, .comment_button, #settings_button {
    display: none;
}
/* Extra */
 .footer {
    display: none;
}
body {
    margin-bottom: 0;
    padding: 0 !important;
}
}
