css:
/* i really want this to be global */
/** site **/

body.font--open_sans.app__body {
  font-family: NYTfranklin;
}
svg:not(:root) {
  fill: currentColor;
}
#app-content {
  background: #eee;
}
.app__body .status .away--icon {
  fill: grey;
}
.sidebar--left .status .offline--icon,
.app__body .sidebar--menu svg,
.app__body .sidebar-item .icon {
  fill: grey;
}
.app__body .status .online--icon {
  fill: grey;
}
.app__body .status .dnd--icon {
  fill: grey;
}
blockquote p {
  font-size: 20px !important;
  color: rgba(0, 0, 0, 0.6);
  font-style: italic;
  font-weight: 300;
}
/** message area **/

.new-separator {
      margin-top: 16px;
    box-shadow: rgba(0,0,0,.1) 0px -3px 4px;
}

.post-list__table {
  background: #eee;
}
.app__body .new-separator .separator__text {
  color: grey;
  text-decoration: underline;
  background: white;
  border-radius: 0;
  cursor: default;
}
.separator__text {
  background: transparent !important;
}
.separator__hr {
  border-color: transparent !important;
  color: white;
}

#post-create {
  background: white;
  padding-top: 10px;
  box-shadow: #ccc 0 0 4px 0;
  border-radius: 4px;
  transform: translate(0, 7px);
  transition: all 0.2s linear;
}
#post-create:focus-within {
  transform: translate(0, 0);
  box-shadow: #aaa 0 0 4px 0;
}

#post_textbox {
  border-radius: 4px;
}

.container-fluid,
.inner-wrap > .row.main {
  display: block;
}
/** messages **/
/** message separator **/

.date-separator.hovered--after::before,
.date-separator.hovered--before::after {
  height: 0;
}
.new-separator.hovered--after::before,
.new-separator.hovered--before::after {
  height: 0;
}
/** single message **/

.post {
  padding: 10px 20px;
  margin-top: 20px;
  background: white;
  margin: 10px 10px 0;
  border-radius: 3px;
  box-shadow: #ddd 1px 1px 2px 0;
}
.post.same--user {
  border-top: 0;
  margin-top: -10px;
  padding: 0 20px 10px  !important;
}
.post.same--user:before {
  content: '';
  width: 20px;
  height: 1px;
}
.post__content {
  display: flex !important;
  align-items: center;
}
.post__link {
  max-width: 700px;
  text-overflow: ellipsis;
}
.post__content .post__header {
  font-family: NYTCheltenham;
  font-size: 16px;
}
.post .post__header .col__name .user-popover:hover {
  text-decoration: underline;
}
.post__content .post__header--info {
  font-family: NYTfranklin;
}
.status-wrapper .status {
  right: -4px;
  bottom: -17px;
}
.post .post__img {
  margin-right: 20px;
}
.post .post__img img {
  width: 50px;
  height: 50px;
}
.post .comment-icon__container .comment-count {
  font-family: NYTFranklin;
}
.post p {
  font-family: NYTFranklin;
}
.post .post__time,
.post .post__visibility {
  font-size: 0.7em;
}
.app__body .webrtc__header:not(.offline) button:hover,
.app__body .post-add-reaction:hover .post-reaction,
.app__body .channel-header .channel-header__favorites.inactive:hover,
.app__body .channel-header__links > a.active,
.app__body a,
.app__body a:focus,
.app__body a:hover,
.app__body .channel-header__links > .color--link.active,
.app__body .color--link,
.app__body a:focus,
.app__body .color--link:hover,
.app__body .btn,
.app__body .btn:focus,
.app__body .btn:hover {
  color: grey;
}
.app__body .post:hover,
.app__body .more-modal__list .more-modal__row:hover,
.app__body .modal .settings-modal .settings-table .settings-content .section-min:hover {
  background: white;
}
.app__body .post.current--user .post__body {
  background: white;
}
/** search highlight **/

.app__body .mention--highlight .mention-link,
.app__body .mention--highlight,
.app__body .search-highlight {
  border-top-left-radius: 2px;
  border-bottom-right-radius: 2px;
  padding: 3px;
}
/** header **/

#channelHeaderDropdownButton strong {
  font-family: 'English Towne';
}
#channel-header {
  border-bottom: 1px solid #ccc;
  box-shadow: 0 4px 4px rgba(0, 0, 0, 0.06);
  display: flex;
  align-items: center;
  background: white;
}
#post-list .post-list-holder-by-time,
.app__body .post .dropdown-menu a {
  background: rgb(253, 253, 253);
}
.channel-header__info {
  margin-top: 0;
  display: flex;
  align-items: center;
}
.channel-header__info .dropdown-toggle {
  margin-right: 10px;
  display: flex;
  align-items: center;
}
.channel-header .flex-parent {
  width: 100%;
}
.channel-header .channel-header__icon {
  margin: 0 5px;
}
.search-bar__container .search__form {
  margin: 0;
}
strong#channelHeaderTitle {
  font-size: 36px;
  border-bottom: 2px solid;
  margin-bottom: 7px;
}
.channel-header__info .dropdown-toggle strong {
  max-width: inherit;
}
.flex-parent {
  align-items: center;
}
.channel-header__info .channel-header__description {
  font-family: NYTCheltenham;
  text-transform: capitalize;
}
/** sidebar **/

#unreadIndicatorBottom,
#unreadIndicatorTop {
  box-shadow: rgba(0, 0, 0, 0.15) 0 0 7px 0;
  opacity: 0.8;
  color: black;
}
#unreadIndicatorBottom svg,
#unreadIndicatorTop svg {
  fill: black;
}
.app__body .sidebar--left .badge {
  color: black;
  background: rgba(0, 0, 0, 0.07);
  border-radius: 25%;
}
.sidebar--left {
  background: white !important;
}
.app__body .sidebar--left .nav-pills__container li > h4,
.app__body .sidebar--left .nav-pills__container li .sidebar-item,
.app__body .sidebar--left .nav-pills__container li > .nav-more,
.app__body .sidebar--right,
.app__body .modal .settings-modal .nav-pills > li button {
  color: black;
}
.app__body #status-dropdown .status-wrapper .status,
.app__body #status-dropdown .status-wrapper .status-edit,
.sidebar--left .team__header,
.app__body .sidebar--menu .team__header,
.app__body .post-list__timestamp > div {
  background: white;
}
.app__body #status-dropdown .status-wrapper .status,
.app__body #status-dropdown .status-wrapper .status-edit,
.multi-teams .team-sidebar .team-wrapper .team-container .team-btn,
.sidebar--left .team__header .header__info,
.app__body .sidebar--menu .team__header .header__info,
.app__body .post-list__timestamp > div {
  color: black;
}
.admin-sidebar .team__header .admin-navbar-dropdown .admin-navbar-dropdown__icon,
.admin-sidebar .team__header .admin-navbar-dropdown .sidebar-header-dropdown__icon,
.admin-sidebar .team__header .sidebar-header-dropdown .admin-navbar-dropdown__icon,
.admin-sidebar .team__header .sidebar-header-dropdown .sidebar-header-dropdown__icon,
.sidebar--left .team__header .admin-navbar-dropdown .admin-navbar-dropdown__icon,
.sidebar--left .team__header .admin-navbar-dropdown .sidebar-header-dropdown__icon,
.sidebar--left .team__header .sidebar-header-dropdown .admin-navbar-dropdown__icon,
.sidebar--left .team__header .sidebar-header-dropdown .sidebar-header-dropdown__icon,
.sidebar--menu .team__header .admin-navbar-dropdown .admin-navbar-dropdown__icon,
.sidebar--menu .team__header .admin-navbar-dropdown .sidebar-header-dropdown__icon,
.sidebar--menu .team__header .sidebar-header-dropdown .admin-navbar-dropdown__icon,
.sidebar--menu .team__header .sidebar-header-dropdown .sidebar-header-dropdown__icon {
  box-shadow: 0 3px 2px 0 rgba(0, 0, 0, 0.06), inset -1px -1px 1px 0 rgba(0, 0, 0, 0.02);
  border-bottom-color: #cecece;
  border-radius: 3px;
  transition: background-color 0.3s;
  display: inline-block;
  margin: 0;
  border: 1px solid #e2e2e2;
  background-color: #fff;
  color: #333;
  cursor: pointer;
  text-transform: uppercase;
}
.app__body .icon--sidebarHeaderTextColor svg,
.app__body .sidebar-header-dropdown__icon svg {
  fill: currentColor;
}
.sidebar--left .team__header .user__name,
.app__body .sidebar--menu .team__header .user__name {
  color: black;
}
#teamHeader {
  height: 72px;
  box-shadow: 0 4px 4px rgba(0, 0, 0, 0.06);
  border-bottom: 1px solid #ccc;
}
.app__body .sidebar--left .sidebar__switcher,
.sidebar--left,
.sidebar--left .sidebar__divider .sidebar__divider__text,
.app__body .modal .settings-modal .settings-table .settings-links,
.app__body .sidebar--menu {
  background: white;
}
.app__body .sidebar--left .sidebar__switcher span {
  color: black;
}
.sidebar--left .nav-pills__container li .sidebar-item:hover,
.sidebar--left .nav-pills__container li > .nav-more:hover,
.app__body .modal .settings-modal .nav-pills > li:hover button {
  background: rgb(240, 240, 240);
  color: black;
}
.sidebar--left .nav-pills__container li.active .sidebar-item,
.sidebar--left .nav-pills__container li.active .sidebar-item:hover,
.sidebar--left .nav-pills__container li.active .sidebar-item:focus,
.app__body .modal .settings-modal .nav-pills > li.active button,
.app__body .modal .settings-modal .nav-pills > li.active button:hover,
.app__body .modal .settings-modal .nav-pills > li.active button:active {
  color: black;
}
.sidebar--left .nav-pills__container li .sidebar-item.unread-title .sidebar-item__name {
  border-bottom: 1px solid rgba(0, 0, 0, 0.3);
}
.app__body .sidebar--left .add-channel-btn {
  color: black;
}
.admin-sidebar .team__header .admin-navbar-dropdown .dropdown-menu a,
.admin-sidebar .team__header .admin-navbar-dropdown .dropdown-menu button,
.admin-sidebar .team__header .sidebar-header-dropdown .dropdown-menu a,
.admin-sidebar .team__header .sidebar-header-dropdown .dropdown-menu button,
.sidebar--left .team__header .admin-navbar-dropdown .dropdown-menu a,
.sidebar--left .team__header .admin-navbar-dropdown .dropdown-menu button,
.sidebar--left .team__header .sidebar-header-dropdown .dropdown-menu a,
.sidebar--left .team__header .sidebar-header-dropdown .dropdown-menu button,
.sidebar--menu .team__header .admin-navbar-dropdown .dropdown-menu a,
.sidebar--menu .team__header .admin-navbar-dropdown .dropdown-menu button,
.sidebar--menu .team__header .sidebar-header-dropdown .dropdown-menu a,
.sidebar--menu .team__header .sidebar-header-dropdown .dropdown-menu button,
.app__body .sidebar--left .nav-pills__container li > h4,
.app__body .sidebar--left .nav-pills__container li .sidebar-item,
.app__body .sidebar--left .nav-pills__container li > .nav-more,
.app__body .sidebar--right,
.app__body .modal .settings-modal .nav-pills > li button {
  transition: all linear 0.2s;
}
div#teamHeader:hover {
  background: #eff8fb;
  color: black;
}
div#teamHeader:hover .user__name {
  color: black;
}
div#teamHeader:hover:before {
  background: none;
}
/** right bar **/

.search-bar__container.channel-header {
  display: flex;
  align-items: center;
}

/** popup **/
.app__body .modal .modal-content {
border-radius: 4px;
    border: transparent;
    overflow: hidden;
}
.app__body .modal .modal-header {
	    background: black;
}
.app__body .modal .modal-header .modal-title {
	font-family: 'English Towne';
  font-size: 24px;
}
.modal-image__content {
	border-radius: 4px;
}

/** dropdown **/
.app__body .dropdown-menu {
	background: white;
}

/** misc **/
.status-wrapper .status {
	bottom: -6px;
}
