css:
@-moz-document domain("campus-booster.net") {
.sidebar, .sidebar.minified, .navbar, .navbar .navbar-brand, .panel-body.curriculum, .panel-noborder-md, .panel-noborder-md h2, .panel-body, .iconpanel, #ctl00_cphMain_ctrlActorContactInfo_btnAddInfo, #ctl00_cphMain_divStudentMain *, #ctl00_ctrlTopBar_ulOtherPortals *, .smallstat, #ctl00_cphMain_ctrlDocumentCertificate1_divMain *, #ctl00_cphMain_ctl00_grvPackage_wrapper *, #ctl00_cphMain_ctl00_btnReviewOrder {
    background: #404040 ;
    color: white !important;
}

.post-container .post .post-inner {
    background: #404040 !important;
    color: white !important;
}

.post-container .post .arrow::after {
	display: none;
}

#ctl00_ctrlTopBar_liOtherPortals li:hover span {
	background-color: grey !important;
}

#ctl00_ctrlTopBar_liOtherPortals li.dropdown-menu-header, #ctl00_ctrlTopBar_liOtherPortals li.dropdown-menu-header span {
	background-color: grey !important;
}

.sidebar a:hover, .sidebar a.active, .sidebar a:focus {
	background-color: grey !important;
}

.panel-heading {
    background: grey !important;
}

.panel-heading h2, .panel-heading li a, #ctl00_cphMain_ctrlActorProfile_divActor *, #ctl00_cphMain_ctrlActorContactInfo_udpActorInfo *, #ctl00_cphMain_divBirthdayMain *, h1, #ctl00_cphMain_divAnnounces blockquote, .post-container .post .post-inner a {
    color: white !important;
}

.panel-heading li.active a, #ctl00_cphMain_ddlClasses, #ctl00_cphMain_grvStudent_length select, #ctl00_cphMain_ctrlDocumentCertificate1_divMain select {
	color: grey !important;
}

.sidebar * {
	color: white ;
}

.nav-sidebar.title span {
	color: white ;
}

.main {
    background: #333333 !important;
}

#ctl00_ctrlTopBar_lnkHome img.day_only {
    display: none;
	/*content: url(https://image.noelshack.com/fichiers/2018/18/3/1525266842-supinfo-logo-color.png);*/
}
#ctl00_ctrlTopBar_lnkHome img.night_only {
    display: inline-block;
}

ol.breadcrumb {
	background: #9e9e9e !important;
}

#main-menu-min i {
	color: white;
}
#main-menu-min:hover, .navbar-right li a:hover {
	background-color: grey !important;
}

.nav-sidebar.title span, .nav-sidebar.title span::after {
	background: #404040 !important;
}

#calendarPublicEvents, #calendarMyNextEvents {
	background: #333333;
}

#calendarPublicEvents .fc-view-container, #calendarMyNextEvents .fc-view-container {
	background: #404040 ;
}
#calendarPublicEvents .fc-view-container tr.fc-list-item:hover td, #calendarMyNextEvents .fc-view-container tr.fc-list-item:hover td {
	background-color: grey !important;
}

#ctl00_cphMain_ctl00_grvAccount *, #ctl00_cphMain_ctrlSuccessPoints1_divAccount *, #DataTables_Table_0_wrapper *, #ctl00_cphMain_ddlStaff, #ctl00_cphMain_ddlStaff *, .fc-widget-header {
    background: #404040 !important;
}

#ctl00_cphMain_ctl00_divPanelUserAmount a, #ctl00_cphMain_aPasswordChange, dd a, #ctl00_cphMain_divAnnounces a {
	color: white;
    text-decoration: underline;
}

#ctl00_cphMain_ctl00_divAccount * {
    background: #404040 ;
}
#ctl00_cphMain_ctrlDocumentCertificate1_divMain select option, #ctl00_cphMain_divStaffMain *, #DataTables_Table_0_wrapper *, #ctl00_cphMain_ddlStaff, #ctl00_cphMain_ddlStaff * {
	color: white;
}

#ctl00_cphMain_olBreadCrumb {
	display: none;
}

#main-menu-min:focus {
	background: #404040 ;
}

text {
	fill: #FFF;
}

*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}
*::-webkit-scrollbar-button {
    display: none ! important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #585858 ! important;
}
*::-webkit-scrollbar-thumb:hover {
    background: #737373!important;
}
*::-webkit-scrollbar-thumb:active {
    background: #494949!important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #1e1e1e ! important;
}

div.progress {
    border: none;
    border-radius: 4px;
}
path[fill="none"] {
   display: none;
}
path[fill="#ffcd5b"] {
    fill: rgb(230, 185, 82);
    stroke: none;
}
.progress-bar, .rounded-icon-success, .label-success {
    background-color: rgb(230, 185, 82) !important;
}
path[fill="#62d653"] {
    fill: rgb(80, 181, 67);
    stroke: none;
}
.progress-bar-success, .rounded-icon-success, .label-success {
    background-color: rgb(80, 181, 67) !important;
}

.progress-bar.progress-bar-info {
    background-color: #3aa1af !important;
}
}
